def save_graph_to_html(graph: nx.Graph, output_dir: str) -> None:
    graph_data = nx.node_link_data(graph)

    if not os.path.exists(output_dir):
        try:
            os.makedirs(output_dir)
        except OSError as e:
            print_status(f"Error creating directory: {e}")
            sys.exit(1)

    # Saving as D3.js HTML
    graph_json_path = os.path.join(output_dir, 'graph.json')
    with open(graph_json_path, 'w') as json_file:
        json.dump(graph_data, json_file)

    html_content = f"""
    <!DOCTYPE html>
    <html>
    <head>
        <title>Graph</title>
        <script src="https://d3js.org/d3.v7.min.js"></script>
        <style>
            .node {{
                stroke: #fff;
                stroke-width: 1.5px;
            }}
            .link {{
                stroke: black;
                stroke-opacity: 0.6;
                stroke-width: 3px;
            }}
            .label {{
                font: 12px sans-serif;
                pointer-events: none;
                text-shadow: 0 1px 0 #fff, 1px 0 0 #fff, 0 -1px 0 #fff, -1px 0 0 #fff;
            }}
            marker {{
                fill: #999;
            }}
            .tooltip {{
                position: absolute;
                text-align: center;
                width: auto;
                height: auto;
                padding: 5px;
                font: 12px sans-serif;
                background: lightgrey;
                border: 1px solid grey;
                border-radius: 5px;
                pointer-events: none;
                visibility: hidden;
            }}
            .center-button {{
                position: absolute;
                top: 10px;
                left: 10px;
                padding: 10px;
                background: white;
                border: 1px solid grey;
                border-radius: 5px;
                cursor: pointer;
                font: 14px sans-serif;
            }}
        </style>
    </head>
    <body>
        <div id="tooltip" class="tooltip"></div>
        <div class="center-button" id="centerButton">Center</div>
        <svg width="1200" height="800">
            <defs>
                <marker id="arrow" viewBox="0 -5 10 10" refX="10" refY="0" markerWidth="6" markerHeight="6" orient="auto">
                    <path d="M0,-5L10,0L0,5" fill="#999"></path>
                </marker>
            </defs>
        </svg>
        <script>
            fetch('/static/generated/{os.path.basename(output_dir)}/graph.json')
                .then(response => response.json())
                .then(data => {{
                    var graph = data;
                    var svg = d3.select("svg"),
                        width = +svg.attr("width"),
                        height = +svg.attr("height");

                    var color = d3.scaleOrdinal(d3.schemeCategory10);

                    var zoom = d3.zoom()
                        .scaleExtent([0.1, 4])
                        .on("zoom", function(event) {{
                            container.attr("transform", event.transform);
                            updateTooltipPosition();
                        }});

                    svg.call(zoom);

                    var container = svg.append("g");

                    var simulation = d3.forceSimulation()
                        .force("link", d3.forceLink().id(function(d) {{ return d.id; }}).distance(200))
                        .force("charge", d3.forceManyBody().strength(-500))
                        .force("center", d3.forceCenter(width / 2, height / 2))
                        .force("x", d3.forceX())
                        .force("y", d3.forceY());

                    var link = container.append("g")
                        .attr("class", "links")
                        .selectAll("line")
                        .data(graph.links)
                        .enter().append("line")
                        .attr("stroke", "black")
                        .attr("stroke-width", function(d) {{ return (d.weight || 3); }})
                        .attr("marker-end", "url(#arrow)")
                        .attr("id", function(d, i) {{ return "link" + i; }});

                    var linkLabel = container.append("g")
                        .attr("class", "link-labels")
                        .selectAll("text")
                        .data(graph.links)
                        .enter().append("text")
                        .attr("font-size", 10)
                        .attr("fill", "black")
                        .attr("dy", ".35em");

                    linkLabel.append("textPath")
                        .attr("xlink:href", function(d, i) {{ return "#link" + i; }})
                        .attr("startOffset", "50%")
                        .text(function(d) {{ return d.title; }});

                    var node = container.append("g")
                        .attr("class", "nodes")
                        .selectAll("circle")
                        .data(graph.nodes)
                        .enter().append("circle")
                        .attr("r", 10)
                        .attr("fill", function(d) {{ return color(d.group); }})
                        .call(d3.drag()
                            .on("start", dragstarted)
                            .on("drag", dragged)
                            .on("end", dragended))
                        .on("click", click);

                    node.append("title")
                        .text(function(d) {{ return d.id; }});

                    var labels = container.append("g")
                        .attr("class", "labels")
                        .selectAll("text")
                        .data(graph.nodes)
                        .enter().append("text")
                        .attr("class", "label")
                        .text(function(d) {{ return d.id; }});

                    simulation
                        .nodes(graph.nodes)
                        .on("tick", ticked);

                    simulation.force("link")
                        .links(graph.links);

                    graph.nodes.forEach(function(d) {{
                        d.x = Math.random() * width;
                        d.y = Math.random() * height;
                    }});

                    var tooltip = d3.select("#tooltip");

                    function ticked() {{
                        link
                            .attr("x1", function(d) {{ return d.source.x; }})
                            .attr("y1", function(d) {{ return d.source.y; }})
                            .attr("x2", function(d) {{ return d.target.x; }})
                            .attr("y2", function(d) {{ return d.target.y; }});

                        node
                            .attr("cx", function(d) {{ return d.x; }})
                            .attr("cy", function(d) {{ return d.y; }});

                        labels
                            .attr("x", function(d) {{ return d.x + 10; }})
                            .attr("y", function(d) {{ return d.y + 4; }});

                        linkLabel
                            .attr("transform", function(d) {{
                                var angle = Math.atan2(d.target.y - d.source.y, d.target.x - d.source.x) * 180 / Math.PI;
                                return 'translate(' + [(d.source.x + d.target.x) / 2, (d.source.y + d.target.y) / 2] + ')rotate(' + angle + ')';
                            }});
                    }}

                    function click(event, d) {{
                        tooltip.style("visibility", "hidden");
                        d3.selectAll(".nodes circle").classed("selected", false);
                        d3.select(this).classed("selected", true);

                        var links = graph.links.filter(function(l) {{ return l.source.id === d.id || l.target.id === d.id; }});

                        var tooltipHtml = links.map(function(l) {{
                            return '<p>' + l.source.id + ' → ' + l.target.id + ': ' + l.title + '</p>';
                        }}).join("");

                        tooltip.html(tooltipHtml)
                            .style("visibility", "visible")
                            .style("left", (event.pageX + 10) + "px")
                            .style("top", (event.pageY + 10) + "px");

                        event.stopPropagation(); // Prevents the click event from bubbling up to the SVG element
                    }}

                    svg.on("click", function() {{
                        tooltip.style("visibility", "hidden");
                        d3.selectAll(".nodes circle").classed("selected", false);
                    }});

                    function dragstarted(event, d) {{
                        if (!event.active) simulation.alphaTarget(0.3).restart();
                        d.fx = d.x;
                        d.fy = d.y;
                    }}

                    function dragged(event, d) {{
                        d.fx = event.x;
                        d.fy = event.y;
                    }}

                    function dragended(event, d) {{
                        if (!event.active) simulation.alphaTarget(0);
                        d.fx = null;
                        d.fy = null;
                    }}

                    function updateTooltipPosition() {{
                        var selected = d3.select(".nodes circle.selected").data()[0];
                        if (selected) {{
                            var transform = d3.zoomTransform(container.node());
                            tooltip.style("left", (selected.x * transform.k + transform.x + 10) + "px")
                                   .style("top", (selected.y * transform.k + transform.y + 10) + "px");
                        }}
                    }}

                    document.getElementById("centerButton").addEventListener("click", function() {{
                        if (graph.nodes.length > 0) {{
                            var node = graph.nodes[0]; // Centering the first node
                            var transform = d3.zoomTransform(svg.node());
                            var translateX = width / 2 - (node.x * transform.k);
                            var translateY = height / 2 - (node.y * transform.k);
                            svg.transition().duration(750).call(
                                zoom.transform,
                                d3.zoomIdentity.translate(translateX, translateY).scale(transform.k)
                            );
                        }}
                    }});
                }});
        </script>
    </body>
    </html>
    """

    graph_html_path = os.path.join(output_dir, 'index.html')
    with open(graph_html_path, 'w') as file:
        file.write(html_content)

    # Saving as PyVis HTML
    net = Network(
        notebook=False,
        cdn_resources="remote",
        height="900px",
        width="100%",
        select_menu=True,
        filter_menu=False,
    )

    net.from_nx(graph)
    net.force_atlas_2based(central_gravity=0.015, gravity=-31)
    net.show_buttons(filter_=["physics"])
    pyvis_output_file = os.path.join(output_dir, 'pyvis.html')
    net.save_graph(pyvis_output_file)
```

