                        .attr("class", "link-labels")
                        .selectAll("text")
                        .data(graph.links)
                        .enter().append("text")
                        .attr("class", "link-label")
                        .attr("text-anchor", "middle")
                        .attr("dy", "-0.6em");

                    linkLabel.append("textPath")
                        .attr("xlink:href", function(d, i) {{ return "#link" + i; }})
                        .attr("startOffset", "50%")
                        .text(function(d) {{ return d.title; }});

