let imageLoaded = false;
let img = new Image();
let canvas, ctx, resizing = false;
let mediaRecorder;
let audioChunks = [];
let startOffset = 0;
let drawing = false;
let points = [];
let isPenMode = false;

function initialize() {
    console.log("Initializing...");
    const urlParams = new URLSearchParams(window.location.search);
    window.username = urlParams.get('User_RecordId');

    canvas = document.getElementById('board');
    if (!canvas) {
        console.error("Canvas element not found.");
        return;
    }
    ctx = canvas.getContext('2d');

    initializeCanvas();
    initializeMediaRecorder();
    window.addEventListener('resize', resizeCanvas);

    const sessionDir = urlParams.get('sessionDir');
    const timestamp = urlParams.get('timestamp');

    const metadataUrl = `${sessionDir}/metadata_${timestamp}.json`;
    fetch(metadataUrl)
        .then(response => response.json())
        .then(metadata => {
            const photoURL = metadata.photoURL;
            if (photoURL) {
                document.getElementById('photoURL').value = photoURL;
                loadImage(photoURL);
            }
        })
        .catch(error => {
            console.error("Failed to load metadata:", error);
        });

    const loadImageBtn = document.getElementById('loadImageBtn');
    if (loadImageBtn) {
        loadImageBtn.addEventListener('click', () => {
            const inputPhotoURL = document.getElementById('photoURL').value;
            loadImage(inputPhotoURL);
        });
    }

    console.log("Initialization complete.");
}

function loadImage(url) {
    console.log("Loading image:", url);
    img = new Image();
    img.onload = function() {
        console.log("Image loaded successfully");
        imageLoaded = true;
        resizeCanvas();
        drawImageWithPadding();
    };
    img.onerror = function() {
        console.error("Failed to load image");
        imageLoaded = false;
    };
    img.src = url;
}

function resizeCanvas() {
    const container = canvas.parentElement;
    canvas.width = container.clientWidth;
    canvas.height = container.clientHeight;

    if (imageLoaded) {
        drawImageWithPadding();
    }
}

function drawImageWithPadding() {
    const padding = 20;
    const scale = Math.min(
        (canvas.width - padding * 2) / img.width,
        (canvas.height - padding * 2) / img.height
    );

    const scaledWidth = img.width * scale;
    const scaledHeight = img.height * scale;

    const x = (canvas.width - scaledWidth) / 2;
    const y = (canvas.height - scaledHeight) / 2;

    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.drawImage(img, x, y, scaledWidth, scaledHeight);
}

function initializeCanvas() {
    canvas.addEventListener('pointerdown', startDrawing);
    canvas.addEventListener('pointermove', draw);
    canvas.addEventListener('pointerup', stopDrawing);
    canvas.addEventListener('pointerout', stopDrawing);

    // Disable touch scrolling on the canvas
    canvas.addEventListener('touchmove', (event) => {
        event.preventDefault();
    }, { passive: false });

    console.log("Canvas initialized with event listeners");
}

function initializeMediaRecorder() {
    navigator.mediaDevices.getUserMedia({ audio: true }).then(stream => {
        mediaRecorder = new MediaRecorder(stream);
        mediaRecorder.ondataavailable = event => {
            audioChunks.push(event.data);
            console.log("Data available from MediaRecorder:", event.data);
        };
        mediaRecorder.onstop = () => console.log("MediaRecorder stopped");
        console.log("MediaRecorder initialized successfully");
    }).catch(err => {
        console.error("Error initializing MediaRecorder:", err);
        alert("Please allow access to the microphone for audio recording.");
    });
}

function startDrawing(event) {
    if (event.pointerType === 'pen') {
        isPenMode = true;
    }

    if (isPenMode && event.pointerType !== 'pen') {
        return;
    }

    drawing = true;
    if (mediaRecorder.state === 'inactive') {
        audioChunks = [];
        mediaRecorder.start();
        startOffset = event.timeStamp;
        console.log("MediaRecorder started");
    }
    points.push(createPoint(event, true));
    console.log("Start drawing:", points);
}

function draw(event) {
    if (!drawing || (isPenMode && event.pointerType !== 'pen')) {
        return;
    }

    const point = createPoint(event, true);
    points.push(point);
    drawLine(points[points.length - 2], point);
    console.log("Drawing:", point);
}

function stopDrawing(event) {
    if (event.pointerType === 'pen') {
        isPenMode = false;
    }

    drawing = false;
    points.push(createPoint(event, false));
    console.log("Stop drawing:", points);
}

function createPoint(event, penDown) {
    const rect = canvas.getBoundingClientRect();
    const pressure = event.pressure !== undefined ? event.pressure : 1;
    const tiltX = event.tiltX !== undefined ? event.tiltX : 0;
    const tiltY = event.tiltY !== undefined ? event.tiltY : 0;

    let x, y;
    if (event.type.startsWith('touch')) {
        x = event.touches[0].clientX;
        y = event.touches[0].clientY;
    } else {
        x = event.clientX;
        y = event.clientY;
    }

    return {
        x: x - rect.left,
        y: y - rect.top,
        pressure: pressure,
        tiltX: tiltX,
        tiltY: tiltY,
        time: event.timeStamp - startOffset,
        penDown: penDown
    };
}

function drawLine(point1, point2) {
    if (point1.penDown && point2.penDown) {
        ctx.beginPath();
        ctx.moveTo(point1.x, point1.y);
        ctx.lineTo(point2.x, point2.y);
        ctx.lineWidth = point2.pressure * 5;
        ctx.stroke();
    }
}

