                    <td>${formatNumber(org.reach)}</td>
                    <td>${org.employees}</td>
                    <td>${org.trainings}</td>
                    <td>${org.traineesPerEmployee}</td>
                    <td>${org.topics}</td>
                `;
                tbody.appendChild(row);
            });
        }

        let currentSort = { column: 'avgScore', direction: 'desc' };

        function sortData(column) {
            const direction = column === currentSort.column && currentSort.direction === 'asc' ? 'desc' : 'asc';
            currentSort = { column, direction };

            const sortedData = [...organizations].sort((a, b) => {
                const aVal = a[column];
                const bVal = b[column];
                return direction === 'asc' ? aVal - bVal : bVal - aVal;
            });

            renderTable(sortedData);
            updateSortIcons();
        }

        function updateSortIcons() {
            document.querySelectorAll('th .sort-icon').forEach(icon => {
                icon.className = 'sort-icon';
            });
            const currentIcon = document.querySelector(`th[data-sort="${currentSort.column}"] .sort-icon`);
            currentIcon.classList.add(currentSort.direction);
        }

        document.querySelectorAll('th').forEach(th => {
            th.addEventListener('click', () => {
                const column = th.dataset.sort;
                sortData(column);
            });
        });

        document.getElementById('searchInput').addEventListener('input', (e) => {
            const searchTerm = e.target.value.toLowerCase();
            const filteredData = organizations.filter(org => 
                org.name.toLowerCase().includes(searchTerm)
            );
            renderTable(filteredData);
        });

        // Initial render
        sortData('avgScore');
    </script>
</body>
