Improve this code: import React, { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Heart } from 'lucide-react';

const AnimalHeartbeats = () => {
  const animals = [
    {
      name: 'Blue Whale',
      heartRate: 10,
      lifespan: 90,
      weight: 140000,
      lifetimeBeats: ((10 * 60 * 24 * 365 * 90) / 1000000000).toFixed(2)
    },
    {
      name: 'African Elephant',
      heartRate: 30,
      lifespan: 60,
      weight: 5400,
      lifetimeBeats: ((30 * 60 * 24 * 365 * 60) / 1000000000).toFixed(2)
    },
    {
      name: 'Giraffe',
      heartRate: 65,
      lifespan: 25,
      weight: 1200,
      lifetimeBeats: ((65 * 60 * 24 * 365 * 25) / 1000000000).toFixed(2)
    },
    {
      name: 'Human',
      heartRate: 70,
      lifespan: 75,
      weight: 70,
      lifetimeBeats: ((70 * 60 * 24 * 365 * 75) / 1000000000).toFixed(2)
    },
    {
      name: 'Lion',
      heartRate: 75,
      lifespan: 15,
      weight: 190,
      lifetimeBeats: ((75 * 60 * 24 * 365 * 15) / 1000000000).toFixed(2)
    },
    {
      name: 'Horse',
      heartRate: 44,
      lifespan: 30,
      weight: 450,
      lifetimeBeats: ((44 * 60 * 24 * 365 * 30) / 1000000000).toFixed(2)
    },
    {
      name: 'Dog',
      heartRate: 120,
      lifespan: 12,
      weight: 30,
      lifetimeBeats: ((120 * 60 * 24 * 365 * 12) / 1000000000).toFixed(2)
    },
    {
      name: 'Cat',
      heartRate: 150,
      lifespan: 15,
      weight: 4,
      lifetimeBeats: ((150 * 60 * 24 * 365 * 15) / 1000000000).toFixed(2)
    },
    {
      name: 'Rabbit',
      heartRate: 205,
      lifespan: 9,
      weight: 2,
      lifetimeBeats: ((205 * 60 * 24 * 365 * 9) / 1000000000).toFixed(2)
    },
    {
      name: 'Guinea Pig',
      heartRate: 240,
      lifespan: 4,
      weight: 1,
      lifetimeBeats: ((240 * 60 * 24 * 365 * 4) / 1000000000).toFixed(2)
    },
    {
      name: 'Hamster',
      heartRate: 450,
      lifespan: 2,
      weight: 0.15,
      lifetimeBeats: ((450 * 60 * 24 * 365 * 2) / 1000000000).toFixed(2)
    },
    {
      name: 'Mouse',
      heartRate: 500,
      lifespan: 2,
      weight: 0.02,
      lifetimeBeats: ((500 * 60 * 24 * 365 * 2) / 1000000000).toFixed(2)
    },
    {
      name: 'Hummingbird',
      heartRate: 1260,
      lifespan: 4,
      weight: 0.004,
      lifetimeBeats: ((1260 * 60 * 24 * 365 * 4) / 1000000000).toFixed(2)
    },
    {
      name: 'Shrew',
      heartRate: 835,
      lifespan: 2,
      weight: 0.003,
      lifetimeBeats: ((835 * 60 * 24 * 365 * 2) / 1000000000).toFixed(2)
    },
    {
      name: 'Etruscan Shrew',
      heartCount: 835,
      heartRate: 835,
      lifespan: 2,
      weight: 0.001,
      lifetimeBeats: ((835 * 60 * 24 * 365 * 2) / 1000000000).toFixed(2)
    }
  ];

  const [selectedAnimal, setSelectedAnimal] = useState(null);

  const maxBeats = Math.max(...animals.map(a => parseFloat(a.lifetimeBeats)));

  return (
    <Card className="w-full max-w-4xl">
      <CardHeader>
        <CardTitle className="text-2xl font-bold text-center">The Heartbeat Hypothesis</CardTitle>
        <p className="text-center text-gray-600">
          Many mammals have roughly the same number of heartbeats in their lifetime (~1-3 billion),
          just used at different rates
        </p>
      </CardHeader>
      <CardContent>
        <div className="space-y-4">
          {animals.map((animal, index) => (
            <div
              key={animal.name}
              className="relative p-4 border rounded-lg hover:bg-gray-50 cursor-pointer"
              onClick={() => setSelectedAnimal(selectedAnimal === animal ? null : animal)}
            >
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-4">
                  <Heart 
                    className="text-red-500"
                    size={24}
                    style={{
                      animation: `heartbeat ${60/animal.heartRate}s ease-in-out infinite`
                    }}
                  />
                  <span className="font-medium">{animal.name}</span>
                </div>
                <div className="text-sm text-gray-500">
                  {animal.lifetimeBeats} billion beats
                </div>
              </div>

              <div className="mt-2 bg-gray-200 rounded-full h-2">
                <div
                  className="bg-red-500 rounded-full h-2 transition-all duration-500"
                  style={{
                    width: `${(parseFloat(animal.lifetimeBeats) / maxBeats) * 100}%`
                  }}
                />
              </div>

              {selectedAnimal === animal && (
                <div className="mt-4 text-sm text-gray-600 grid grid-cols-3 gap-4">
                  <div>
                    <span className="font-medium">Weight:</span> {animal.weight} kg
                  </div>
                  <div>
                    <span className="font-medium">Heart Rate:</span> {animal.heartRate} BPM
                  </div>
                  <div>
                    <span className="font-medium">Lifespan:</span> {animal.lifespan} years
                  </div>
                </div>
              )}
            </div>
          ))}
        </div>
      </CardContent>
      <style>{`
        @keyframes heartbeat {
          0% { transform: scale(1); }
          50% { transform: scale(1.2); }
          100% { transform: scale(1); }
        }
      `}</style>
    </Card>
  );
};

export default AnimalHeartbeats;  it doesn’t makes it seem like they’re similar - how would tufte do this.
