import React, { useState } from 'react';
import { Card, CardHeader, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Sparkles, Send, Download } from 'lucide-react';

const ReflectionTool = () => {
  const [step, setStep] = useState(0);
  const [responses, setResponses] = useState({});
  const [isTyping, setIsTyping] = useState(false);

  const questions = [
    "What moments made you feel most alive this year?",
    "What challenges helped you grow?",
    "What patterns do you notice in your successes?",
    "What would make next year beautiful?"
  ];

  const simulateAIResponse = (response) => {
    setIsTyping(true);
    // In real app, this would call the AI API
    setTimeout(() => {
      setResponses(prev => ({
        ...prev,
        [step]: {
          answer: response,
          insight: "I notice themes of connection and creativity in your response. These moments seem to light you up especially when sharing with others."
        }
      }));
      setIsTyping(false);
    }, 1500);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const input = e.target.elements.reflection.value;
    simulateAIResponse(input);
    e.target.reset();
  };

  return (
    <div className="max-w-2xl mx-auto p-4">
      <Card className="mb-4">
        <CardHeader className="flex flex-row items-center justify-between">
          <h2 className="text-2xl font-bold">Memory Crystal</h2>
          <Sparkles className="text-indigo-500" />
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {step < questions.length ? (
              <>
                <p className="text-lg font-medium">{questions[step]}</p>
                <form onSubmit={handleSubmit} className="space-y-4">
                  <textarea
                    name="reflection"
                    className="w-full h-32 p-2 border rounded-md"
                    placeholder="Share your thoughts..."
                  />
                  <div className="flex justify-between">
                    <Button 
                      type="submit"
                      className="bg-indigo-600 hover:bg-indigo-700 text-white"
                    >
                      <Send className="w-4 h-4 mr-2" />
                      Share
                    </Button>
                  </div>
                </form>
              </>
            ) : (
              <div className="text-center">
                <h3 className="text-xl font-bold mb-4">Your Reflection Crystal</h3>
                <Button className="bg-indigo-600 hover:bg-indigo-700 text-white">
                  <Download className="w-4 h-4 mr-2" />
                  Download Insights
                </Button>
              </div>
            )}

            {responses[step]?.insight && (
              <div className="mt-4 p-4 bg-indigo-50 rounded-md">
                <p className="text-sm italic">{responses[step].insight}</p>
              </div>
            )}

            {isTyping && (
              <div className="text-sm text-gray-500">
                Reflecting on your response...
              </div>
            )}
          </div>
        </CardContent>
      </Card>

      {Object.keys(responses).length > 0 && (
        <Button 
          onClick={() => setStep(prev => prev + 1)}
          className="w-full bg-indigo-600 hover:bg-indigo-700 text-white"
          disabled={isTyping}
        >
          Continue
        </Button>
      )}
    </div>
  );
};

export default ReflectionTool;

import React, { useState } from 'react';
import { Card } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Pencil, Send } from 'lucide-react';

const ReflectiveWriting = () => {
  const [messages, setMessages] = useState([
    {
      type: 'ai',
      content: "Welcome! Let's reflect on your year together. Rather than quick answers, try writing freely for a few minutes. What surprised you most this year?"
    }
  ]);
  const [isWriting, setIsWriting] = useState(false);

  const generateNextQuestion = (lastResponse) => {
    // In real app, this would use AI to generate contextual questions
    const questions = [
      "Looking at what you wrote, what patterns do you notice?",
      "How did these experiences change how you see yourself?",
      "What feels most important to carry forward?",
      "What would make next year feel meaningful?"
    ];
    return questions[messages.filter(m => m.type === 'ai').length - 1] || 
           "Thank you for reflecting. Would you like to explore any of these themes deeper?";
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const response = e.target.elements.response.value.trim();
    if (!response) return;

    setMessages(prev => [...prev, { type: 'user', content: response }]);
    setIsWriting(true);

    // Simulate AI thinking time
    setTimeout(() => {
      const nextQuestion = generateNextQuestion(response);
      setMessages(prev => [...prev, { type: 'ai', content: nextQuestion }]);
      setIsWriting(false);
    }, 1000);

    e.target.reset();
  };

  return (
    <div className="max-w-2xl mx-auto p-4">
      <Card className="mb-4 p-4">
        <div className="space-y-4">
          <div className="flex items-center gap-2 mb-6">
            <Pencil className="w-5 h-5 text-indigo-600" />
            <h2 className="text-xl font-semibold">End of Year Reflection</h2>
          </div>

          <div className="space-y-4 mb-4">
            {messages.map((message, index) => (
              <div
                key={index}
                className={`p-3 rounded-lg ${
                  message.type === 'ai' 
                    ? 'bg-gray-100' 
                    : 'bg-indigo-50 ml-4'
                }`}
              >
                {message.content}
              </div>
            ))}

            {isWriting && (
              <div className="text-sm text-gray-500 ml-2">
                Reflecting on your response...
              </div>
            )}
          </div>

          <form onSubmit={handleSubmit} className="space-y-2">
            <textarea
              name="response"
              className="w-full h-32 p-3 border rounded-lg"
              placeholder="Take your time to write..."
              disabled={isWriting}
            />
            <Button 
              type="submit"
              className="w-full bg-indigo-600 hover:bg-indigo-700 text-white"
              disabled={isWriting}
            >
              <Send className="w-4 h-4 mr-2" />
              Share Reflection
            </Button>
          </form>
        </div>
      </Card>
    </div>
  );
};

export default ReflectiveWriting;

import React, { useState } from 'react';
import { Card, CardHeader, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Heart, Briefcase, Brain, Scroll } from 'lucide-react';

const ReflectionGuide = () => {
  const [selectedPath, setSelectedPath] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [responses, setResponses] = useState({});

  const paths = {
    spiritual: {
      icon: Heart,
      title: "Spiritual & Inner Growth",
      questions: [
        "What moments this year made you pause in wonder?",
        "When did you feel most connected to something larger than yourself?",
        "What challenges refined your understanding?",
        "Where did you notice divine guidance?",
        "How might you deepen your journey in the coming year?"
      ]
    },
    work: {
      icon: Briefcase,
      title: "Work & Creation",
      questions: [
        "What did you create this year that felt true to your essence?",
        "When did work feel like a natural expression of your gifts?",
        "What projects lit up both you and others?",
        "Where did you hold back, and why?",
        "How might your work better reflect your inner light?"
      ]
    },
    patterns: {
      icon: Brain,
      title: "Pattern Recognition",
      questions: [
        "What environments help you thrive?",
        "What activities consistently bring you joy?",
        "What old stories might be shaping your choices?",
        "What gifts do you habitually diminish?",
        "How might you honor these patterns while staying grounded?"
      ]
    },
    integration: {
      icon: Scroll,
      title: "Integration & Action",
      questions: [
        "What insights feel most important to carry forward?",
        "What small steps could honor these insights?",
        "What support might you need?",
        "What becomes possible when you align with these truths?",
        "How will you remind yourself of what matters most?"
      ]
    }
  };

  const handlePathSelect = (path) => {
    setSelectedPath(path);
    setCurrentQuestionIndex(0);
    setResponses({});
  };

  const handleResponseChange = (e) => {
    setResponses({
      ...responses,
      [currentQuestionIndex]: e.target.value
    });
  };

  const handleNext = () => {
    if (currentQuestionIndex < paths[selectedPath].questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    }
  };

  const handlePrevious = () => {
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex(currentQuestionIndex - 1);
    }
  };

  const handleReset = () => {
    setSelectedPath(null);
    setCurrentQuestionIndex(0);
    setResponses({});
  };

  if (!selectedPath) {
    return (
      <div className="max-w-2xl mx-auto p-4">
        <Card>
          <CardHeader>
            <h2 className="text-2xl font-bold text-center">Choose Your Reflection Path</h2>
            <p className="text-gray-600 text-center mt-2">Select an area to explore</p>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {Object.entries(paths).map(([key, path]) => {
                const IconComponent = path.icon;
                return (
                  <Button
                    key={key}
                    onClick={() => handlePathSelect(key)}
                    className="p-6 text-left flex items-center space-x-3 bg-white hover:bg-gray-50 border rounded-lg"
                  >
                    <IconComponent className="w-6 h-6 text-indigo-600" />
                    <span className="font-medium">{path.title}</span>
                  </Button>
                );
              })}
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  const currentPath = paths[selectedPath];
  const currentQuestion = currentPath.questions[currentQuestionIndex];
  const isLastQuestion = currentQuestionIndex === currentPath.questions.length - 1;

  return (
    <div className="max-w-2xl mx-auto p-4">
      <Card>
        <CardHeader>
          <div className="flex items-center space-x-2">
            <currentPath.icon className="w-6 h-6 text-indigo-600" />
            <h2 className="text-xl font-bold">{currentPath.title}</h2>
          </div>
          <p className="text-gray-600 mt-2">Question {currentQuestionIndex + 1} of {currentPath.questions.length}</p>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            <p className="text-lg">{currentQuestion}</p>
            <textarea
              value={responses[currentQuestionIndex] || ''}
              onChange={handleResponseChange}
              className="w-full h-32 p-3 border rounded-lg"
              placeholder="Take your time to reflect..."
            />
            <div className="flex justify-between">
              <Button 
                onClick={handlePrevious} 
                disabled={currentQuestionIndex === 0}
                className="bg-gray-100 text-gray-700 hover:bg-gray-200"
              >
                Previous
              </Button>
              <Button onClick={handleReset}>
                Start Over
              </Button>
              {!isLastQuestion ? (
                <Button 
                  onClick={handleNext}
                  className="bg-indigo-600 hover:bg-indigo-700 text-white"
                >
                  Next
                </Button>
              ) : (
                <Button 
                  onClick={handleReset}
                  className="bg-green-600 hover:bg-green-700 text-white"
                >
                  Complete
                </Button>
              )}
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
};

export default ReflectionGuide;

import React, { useState } from 'react';
import { Card, CardHeader, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { CheckSquare, Square, ArrowRight, Brain } from 'lucide-react';

const ValueReflection = () => {
  const [isSelecting, setIsSelecting] = useState(true);
  const [selectedTopics, setSelectedTopics] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [responses, setResponses] = useState({});
  const [valueInsights, setValueInsights] = useState([]);

  const topics = {
    achievements: {
      title: "Key Achievements",
      description: "Moments of accomplishment and pride",
      questions: [
        {
          text: "What achievements this year felt most meaningful?",
          followUps: [
            "What were you paying attention to when making choices that led to this?",
            "Can you share a specific story about this achievement?",
            "Who do you admire that embodies this kind of success?"
          ]
        }
      ]
    },
    challenges: {
      title: "Growth Through Challenges",
      description: "Difficulties that led to insights",
      questions: [
        {
          text: "What challenges helped you grow this year?",
          followUps: [
            "What did you pay attention to while navigating this challenge?",
            "How did this experience change what matters to you?",
            "Who has inspired you in handling similar challenges?"
          ]
        }
      ]
    },
    relationships: {
      title: "Meaningful Connections",
      description: "Important relationships and interactions",
      questions: [
        {
          text: "Which relationships brought out your best this year?",
          followUps: [
            "What do you pay attention to in these connections?",
            "Can you share a specific moment that exemplifies this?",
            "Who models the kind of relationships you value?"
          ]
        }
      ]
    },
    creativity: {
      title: "Creative Expression",
      description: "Moments of creation and innovation",
      questions: [
        {
          text: "When did you feel most creatively alive?",
          followUps: [
            "What draws your attention during these creative moments?",
            "Tell me about a specific time you felt this way",
            "Who inspires your creative expression?"
          ]
        }
      ]
    }
  };

  const handleTopicToggle = (topic) => {
    setSelectedTopics(prev => 
      prev.includes(topic) 
        ? prev.filter(t => t !== topic)
        : [...prev, topic]
    );
  };

  const startReflection = () => {
    if (selectedTopics.length > 0) {
      setIsSelecting(false);
    }
  };

  const getAllQuestions = () => {
    return selectedTopics.flatMap(topic => 
      topics[topic].questions.map(q => ({
        ...q,
        topic
      }))
    );
  };

  const currentQuestion = getAllQuestions()[currentQuestionIndex];

  const analyzeResponse = (response) => {
    // This would connect to backend AI analysis
    // For now, we'll simulate value pattern recognition
    if (response && response.length > 0) {
      setValueInsights(prev => [...prev, {
        topic: currentQuestion.topic,
        attention: "Sample attention pattern",
        value: "Sample underlying value"
      }]);
    }
  };

  const handleNext = () => {
    analyzeResponse(responses[currentQuestionIndex]);
    if (currentQuestionIndex < getAllQuestions().length - 1) {
      setCurrentQuestionIndex(prev => prev + 1);
    }
  };

  if (isSelecting) {
    return (
      <div className="max-w-2xl mx-auto p-4">
        <Card>
          <CardHeader>
            <h2 className="text-2xl font-bold">Choose Your Reflection Areas</h2>
            <p className="text-gray-600">Select all that you'd like to explore</p>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {Object.entries(topics).map(([key, topic]) => (
                <button
                  key={key}
                  onClick={() => handleTopicToggle(key)}
                  className="w-full flex items-center p-4 border rounded-lg hover:bg-gray-50"
                >
                  {selectedTopics.includes(key) ? 
                    <CheckSquare className="w-6 h-6 text-indigo-600 mr-3" /> :
                    <Square className="w-6 h-6 text-gray-400 mr-3" />
                  }
                  <div className="text-left">
                    <h3 className="font-medium">{topic.title}</h3>
                    <p className="text-sm text-gray-600">{topic.description}</p>
                  </div>
                </button>
              ))}

              <Button
                onClick={startReflection}
                disabled={selectedTopics.length === 0}
                className="w-full mt-6 bg-indigo-600 hover:bg-indigo-700 text-white"
              >
                Begin Reflection
                <ArrowRight className="w-4 h-4 ml-2" />
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="max-w-2xl mx-auto p-4">
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <h2 className="text-xl font-bold">
              {topics[currentQuestion.topic].title}
            </h2>
            <Brain className="w-6 h-6 text-indigo-600" />
          </div>
          <p className="text-gray-600">
            Question {currentQuestionIndex + 1} of {getAllQuestions().length}
          </p>
        </CardHeader>
        <CardContent>
          <div className="space-y-6">
            <div>
              <p className="text-lg mb-4">{currentQuestion.text}</p>
              <textarea
                value={responses[currentQuestionIndex] || ''}
                onChange={(e) => setResponses({
                  ...responses,
                  [currentQuestionIndex]: e.target.value
                })}
                className="w-full h-32 p-3 border rounded-lg"
                placeholder="Take your time to reflect..."
              />
            </div>

            {responses[currentQuestionIndex] && (
              <div className="space-y-4">
                {currentQuestion.followUps.map((followUp, idx) => (
                  <div key={idx} className="border-l-4 border-indigo-200 pl-4">
                    <p className="text-gray-600 mb-2">{followUp}</p>
                    <textarea
                      className="w-full h-20 p-2 border rounded-lg"
                      placeholder="Your thoughts..."
                    />
                  </div>
                ))}
              </div>
            )}

            <div className="flex justify-between">
              <Button
                onClick={() => setCurrentQuestionIndex(prev => prev - 1)}
                disabled={currentQuestionIndex === 0}
                className="bg-gray-100 text-gray-700 hover:bg-gray-200"
              >
                Previous
              </Button>
              <Button
                onClick={handleNext}
                className="bg-indigo-600 hover:bg-indigo-700 text-white"
              >
                {currentQuestionIndex === getAllQuestions().length - 1 ? 
                  'Complete' : 'Next'}
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
};

export default ValueReflection;

import React, { useState, useEffect } from 'react';
import { Card, CardHeader, CardContent } from '@/components/ui/card';
