const ValueAnalysis = ({ responses }) => {
  const [valuePatterns, setValuePatterns] = useState([]);
  const [valueRelationships, setValueRelationships] = useState([]);

  // Analyze a response for value patterns
  const analyzeForValues = (response) => {
    // This would connect to AI backend for actual analysis
