          </div>

          <ValueInsights />

          <div className="bg-indigo-50 p-4 rounded-lg">
            <h3 className="font-medium mb-2">Growth Opportunities</h3>
            <p className="text-sm">
              Consider exploring how your value of authenticity might enhance rather than conflict with professional growth.
            </p>
          </div>
        </div>
      </CardContent>
    </Card>
  );
};

export default ValueAnalysis;

import React, { useState } from 'react';
import { Card, CardHeader, CardContent } from '@/components/ui/card';

