export default ValuePatterns;

import React, { useState } from 'react';
import { Card, CardHeader, CardContent } from '@/components/ui/card';
import { ArrowRight, Plus } from 'lucide-react';

const MoralGraph = () => {
  const [selectedNode, setSelectedNode] = useState(null);

  // Example of values and their relationships
  const valueNodes = [
    {
      id: 1,
      context: "When making career decisions",
      attention: [
        "Looking for secure, stable options",
        "Focusing on salary and benefits",
        "Considering conventional career paths"
      ],
      story: "I used to primarily seek stability in my work choices",
    },
    {
      id: 2,
      context: "When making career decisions",
      attention: [
        "Noticing what activities energize me",
        "Looking for learning opportunities",
        "Watching for chances to grow"
      ],
      story: "I learned to pay attention to what truly engages me",
      wiserThan: 1,
      wisdomGain: "Realized that true security comes from developing capabilities I'm passionate about"
    },
    {
      id: 3,
      context: "When making career decisions",
      attention: [
        "Seeking ways to contribute meaningfully",
        "Looking for alignment with values",
        "Noticing opportunities to serve others"
      ],
      story: "Now I look for ways my work can benefit others",
      wiserThan: 2,
      wisdomGain: "Discovered that personal growth is most fulfilling when it serves a larger purpose"
    }
  ];

  const ValueNode = ({ node, isSelected }) => (
    <div 
      className={`p-4 rounded-lg border ${
        isSelected ? 'border-indigo-500 bg-indigo-50' : 'border-gray-200'
      } cursor-pointer`}
      onClick={() => setSelectedNode(node.id)}
    >
      <h3 className="font-medium mb-2">In {node.context}</h3>
      <div className="space-y-2">
        <p className="text-sm text-gray-600">I pay attention to:</p>
        <ul className="list-disc pl-5 text-sm space-y-1">
          {node.attention.map((item, idx) => (
            <li key={idx}>{item}</li>
          ))}
        </ul>
      </div>
    </div>
  );

  const WisdomTransition = ({ from, to }) => (
    <div className="flex items-center py-4">
      <ArrowRight className="w-6 h-6 text-indigo-500 mx-4" />
      <div className="text-sm text-gray-600 flex-1">
        {valueNodes.find(n => n.id === to).wisdomGain}
      </div>
    </div>
  );

  return (
    <div className="max-w-3xl mx-auto p-4">
      <Card>
        <CardHeader>
          <h2 className="text-2xl font-bold">Your Value Journey</h2>
          <p className="text-gray-600">
            See how your values connect and evolve through reflection
          </p>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {valueNodes.map((node, index) => (
              <div key={node.id}>
                <ValueNode 
                  node={node} 
                  isSelected={selectedNode === node.id}
                />
                {node.wiserThan && (
                  <WisdomTransition 
                    from={node.wiserThan} 
                    to={node.id}
                  />
                )}
              </div>
            ))}
          </div>
        </CardContent>
      </Card>
    </div>
  );
};

export default MoralGraph;
