// Reflection Flow Structure
const reflectionFlow = {
  // Core Flow Elements
  opening: {
    welcome: {
      voice: "encourager",
      text: "Welcome to this moment of reflection. In the next 8 minutes, you'll discover something meaningful about your year.",
      duration: 30
    },
    grounding: {
      voice: "sufi",
      text: "Let's begin with one conscious breath. Just inhale slowly... and exhale...",
      duration: 30
    }
  },

  // Life Wheel Integration (Optional Branch)
  lifeWheel: {
    areas: [
      "Health & Wellbeing",
      "Relationships & Community",
      "Work & Impact",
      "Learning & Growth",
      "Joy & Play",
      "Purpose & Meaning"
    ],
    quickAssessment: {
      prompt: "Which area feels most alive for you right now?",
      followUp: "What's one moment from this area that stands out?"
    }
  },

  // Social Sharing Elements
  sharingOptions: {
    anonymous: {
      template: "Someone reflected on {{area}} and discovered they value {{value}}",
      privacy: "high"
    },
    withName: {
      template: "{{name}} had an insight about {{value}} while reflecting on {{area}}",
      privacy: "medium"
    },
    fullShare: {
      template: "{{name}}'s reflection on {{area}}: {{fullInsight}}",
      privacy: "low"
    }
  },

  // Community Insights
  communityFeatures: {
    insightFeed: {
      displayFormat: "carousel",
      updateFrequency: "realtime",
      privacyFilter: (insight) => insight.privacyLevel >= userPreferences.privacyThreshold
    },
    valuePatterns: {
      aggregation: {
        byArea: true,
        byTimeframe: "weekly",
        anonymized: true
      }
    }
  },

  // Enhanced Values Discovery
  valuesDiscovery: {
    patterns: [
      {
        prompt: "In moments that light you up, what do you tend to notice?",
        focusArea: "attention",
        type: "open"
      },
      {
        prompt: "When making important choices, what matters most?",
        focusArea: "decision_making",
        type: "open"
      }
    ],
    synthesis: (responses) => {
      // Analyze patterns in responses to suggest value themes
      return analyzeValuePatterns(responses);
    }
  },

  // Integration Points
  integrationPoints: {
    lifeWheel: {
      timing: "pre-reflection",
      optional: true,
      duration: 60
    },
    communityInsights: {
      timing: "post-reflection",
      optional: true,
      duration: 30
    },
    sharing: {
      timing: "post-card-creation",
      optional: true,
      duration: 30
    }
  },

  // Privacy Controls
  privacySettings: {
    defaultSharing: "anonymous",
    optOut: true,
    dataRetention: {
      personal: "user_controlled",
      anonymous: "90_days"
    }
  }
};

// Helper Functions
function analyzeValuePatterns(responses) {
  // Implement pattern recognition logic
  // Returns suggested values based on response analysis
}

function generateSharingPreview(template, data) {
  // Generate preview of how shared insight will appear
  return template.replace(/\{\{(\w+)\}\}/g, (match, key) => data[key] || '');
}

// Example Usage:
/*
1. Start with quick Life Wheel check if user opts in
2. Move through core reflection flow
3. Create Values Card
4. Offer sharing options with privacy controls
5. Show relevant community insights
*/

export default reflectionFlow;

// Core Reflection System - 2 Day Implementation
const reflectionSystem = {
  // Personal Reflection (8 minutes)
  selfReflection: {
    opening: {
      prompt: "Take a gentle breath. Think of one moment from this year that still carries energy for you.",
      duration: 30
    },

    discovery: {
      valuePrompt: "In this moment, what were you paying attention to? What mattered most?",
      bodyPrompt: "Where do you feel this in your body?",
      duration: 120
    },

    synthesis: {
      template: {
        values: "I feel most alive when...",
        attention: "What I tend to notice is...",
        action: "Tomorrow, I could honor this by..."
      },
      duration: 90
    }
  },

  // Friend Reflection (5 minutes)
  friendReflection: {
    opening: {
      prompt: "Think of a moment when {{name}} brought light to your life this year.",
      duration: 30
    },

    discovery: {
      valuePrompt: "What quality or gift did they bring to this moment?",
      impactPrompt: "How did their presence make a difference?",
      duration: 90
    },

    synthesis: {
      template: {
        gift: "I see {{name}}'s light shine when...",
        impact: "Their unique gift is...",
        appreciation: "I appreciate how they..."
      },
      duration: 60
    }
  },

  // Monetization (Simple Version)
  revenueOptions: {
    handwrittenPoem: {
      price: 35,
      includes: [
        "Custom poem based on reflection",
        "Handwritten on quality paper",
        "Mailed within 3 days"
      ]
    },
    poemAndArt: {
      price: 55,
      includes: [
        "Custom poem based on reflection",
        "Original artistic drawing",
        "Personal note with insights",
        "Mailed within 3 days"
      ]
    }
  },

  // Link Generation
  shareSystem: {
    generateLink: (userId) => {
      return `reflect.com/friend/${userId}`;
    },

    privacySettings: {
      defaultPrivacy: "nameless",
      shareOptions: ["anonymous", "first name", "full name"]
    }
  },

  // Value Card Generation
  valueCard: {
    template: (data) => {
      return {
        moment: data.moment,
        value: data.value,
        intention: data.action,
        dateCreated: new Date()
      };
    },

    friendTemplate: (data) => {
      return {
        moment: data.moment,
        gift: data.gift,
        appreciation: data.appreciation,
        dateCreated: new Date()
      };
    }
  }
};

// Implementation Order (2 Days)
const implementationPlan = {
  dayOne: {
    morning: [
      "Set up basic web interface",
      "Implement self-reflection flow",
      "Create value card generation"
    ],
    afternoon: [
      "Add friend reflection flow",
      "Implement link generation",
      "Basic styling and user experience"
    ]
  },

  dayTwo: {
    morning: [
      "Add monetization options",
      "Create email collection for poems",
      "Polish user interface"
    ],
    afternoon: [
      "Testing and refinement",
      "Deploy basic version",
      "Set up tracking for orders"
    ]
  }
};

export default reflectionSystem;

import React, { useState } from 'react';
import { Card, CardHeader, CardContent } from '@/components/ui/card';
import { Heart, Star, MessageCircle } from 'lucide-react';

const FriendReflectionViewer = () => {
  const [showReflectionPrompt, setShowReflectionPrompt] = useState(false);

  // Example friend reflection data
  const friendReflection = {
    moment: "When you helped me move houses and made the whole day feel light",
    gift: "Your ability to bring humor and warmth to challenging situations",
    impact: "You helped me see that difficult transitions can also be joyful",
    appreciation: "I appreciate how you show up fully for your friends"
  };

  return (
    <div className="max-w-2xl mx-auto p-4">
      {/* Initial Card Reveal */}
      <Card className="mb-6">
        <CardHeader className="text-center">
          <h2 className="text-xl font-semibold">Someone shared a reflection about you</h2>
          <p className="text-gray-600">Take a moment to receive this gift of perspective</p>
        </CardHeader>

        <CardContent className="space-y-6">
          {/* Memory Section */}
          <div className="p-4 bg-blue-50 rounded-lg">
            <div className="flex items-center mb-2">
              <Star className="w-5 h-5 text-blue-500 mr-2" />
              <h3 className="font-medium">A Moment They Remember</h3>
            </div>
            <p className="text-gray-700">{friendReflection.moment}</p>
          </div>

          {/* Gift Section */}
          <div className="p-4 bg-purple-50 rounded-lg">
            <div className="flex items-center mb-2">
              <Heart className="w-5 h-5 text-purple-500 mr-2" />
              <h3 className="font-medium">The Gift They See in You</h3>
            </div>
            <p className="text-gray-700">{friendReflection.gift}</p>
          </div>

          {/* Impact Section */}
          <div className="p-4 bg-green-50 rounded-lg">
            <div className="flex items-center mb-2">
              <MessageCircle className="w-5 h-5 text-green-500 mr-2" />
              <h3 className="font-medium">Their Words of Appreciation</h3>
            </div>
            <p className="text-gray-700">{friendReflection.appreciation}</p>
          </div>
        </CardContent>
      </Card>

      {/* Reflection Prompt */}
      {!showReflectionPrompt ? (
        <button 
          onClick={() => setShowReflectionPrompt(true)}
          className="w-full p-4 text-center bg-gray-100 rounded-lg hover:bg-gray-200 transition-colors"
        >
          Take a moment to reflect on this feedback
        </button>
      ) : (
        <Card className="p-4">
          <CardContent className="space-y-4">
            <h3 className="font-medium">Mini-Reflection Questions</h3>
            <div className="space-y-2">
              <p className="text-gray-700">• How does it feel to be seen in this way?</p>
              <p className="text-gray-700">• Does this reflection remind you of other moments where you've shared this gift?</p>
              <p className="text-gray-700">• How might you bring more of this quality into your coming year?</p>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
};

export default FriendReflectionViewer;
