    "middleware.ts": "// middleware.ts
import { createMiddlewareClient } from '@supabase/auth-helpers-nextjs'
import { NextResponse } from 'next/server'
import type { NextRequest } from 'next/server'

export async function middleware(req: NextRequest) {
  const res = NextResponse.next()
  const supabase = createMiddlewareClient({ req, res })

  const {
    data: { session },
  } = await supabase.auth.getSession()

  // Only protect routes that need authentication
  // Like saving cards or viewing saved reflections
  if (!session && (
    req.nextUrl.pathname.startsWith('/reflection/save') ||
    req.nextUrl.pathname.startsWith('/gallery')
  )) {
    return NextResponse.redirect(new URL('/', req.url))
  }

  return res
}

export const config = {
  // Only run middleware on routes that need protection
  matcher: ['/reflection/save/:path*', '/gallery/:path*']
}
