'use client';

import { useState } from 'react';
import { useReflection } from '@/contexts/ReflectionContext';
import { Card, CardContent } from '@/components/ui/card';
import { Textarea } from '@/components/ui/textarea';
import { Button } from '@/components/ui/button';

export function StoryStep() {
  const { state, updateResponse, getAIPrompts } = useReflection();
  const [story, setStory] = useState(state.responses.story || '');
  const [aiResponse, setAiResponse] = useState('');
  const [isThinking, setIsThinking] = useState(false);

  const handleSubmit = async () => {
    setIsThinking(true);
    try {
      // Get initial AI response
      const response = await fetch('/api/reflection/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          messages: [{
            role: 'user',
            content: story
          }],
          context: 'Initial story sharing'
        })
      });

      const data = await response.json();
      setAiResponse(data.response);

      // Update reflection state
      updateResponse({ story });

      // Get follow-up prompts
      await getAIPrompts();
    } finally {
      setIsThinking(false);
    }
  };

  return (
    <Card className="w-full max-w-2xl mx-auto">
      <CardContent className="p-6 space-y-4">
        <div className="space-y-2">
          <h2 className="text-2xl font-bold">Share a Meaningful Moment</h2>
          <p className="text-gray-600">
            Tell me about a moment that stands out as meaningful to you—what happened, 
            who was involved, and how did it feel? Take your time.
          </p>
        </div>

        <Textarea
          value={story}
          onChange={(e) => setStory(e.target.value)}
          placeholder="Start typing your story..."
          className="min-h-[200px]"
        />

        {aiResponse && (
          <div className="bg-gray-50 p-4 rounded-lg space-y-2">
            <p className="text-gray-700">{aiResponse}</p>
            {state.aiSuggestions?.map((prompt, i) => (
              <p key={i} className="text-gray-600 italic">
                {prompt}
              </p>
            ))}
          </div>
        )}

        <div className="flex justify-end space-x-2">
          <Button 
            onClick={handleSubmit}
            disabled={!story.trim() || isThinking}
          >
            {isThinking ? 'Thinking...' : 'Share'}
          </Button>
        </div>
      </CardContent>
    </Card>
  );
