'use client';

import { createContext, useContext, useState } from 'react';

interface ReflectionState {
  responses: Record<string, any>;
}

const defaultState: ReflectionState = {
  responses: {}, // Initialize with an empty object to avoid undefined errors
};

const ReflectionContext = createContext<any>(null);

export function ReflectionProvider({ children }: { children: React.ReactNode }) {
  const [state, setState] = useState(defaultState);

  const goToStep = (step: string) => {
    console.log(`Navigating to step: ${step}`);
  };

  const updateResponse = (response: Record<string, any>) => {
    setState((prevState) => ({
      ...prevState,
      responses: { ...prevState.responses, ...response },
    }));
  };

  return (
    <ReflectionContext.Provider value={{ state, goToStep, updateResponse }}>
      {children}
    </ReflectionContext.Provider>
  );
}

export const useReflection = () => {
  const context = useContext(ReflectionContext);
  if (!context) {
    throw new Error('useReflection must be used within a ReflectionProvider');
  }
  return context;
};
