'use client';

import { useState } from 'react';
import { useReflection } from '@/contexts/ReflectionContext';
import { supabase } from '@/lib/supabase';
import { Card, CardHeader, CardContent } from '@/components/ui/card';
import { ChatUI } from '@/components/reflection/shared/ChatUI';
import ValuesCard from '@/components/reflection/ValuesCard';
import { ValuesCard as ValuesCardType } from '@/lib/types';

export default function ReflectionPage() {
  const { state, updateState } = useReflection();
  const [story, setStory] = useState('');
  const [photoFile, setPhotoFile] = useState<File | null>(null);
  const [showChat, setShowChat] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleStorySubmit = async () => {
    if (!story.trim() || isSubmitting) return;

    setIsSubmitting(true);
    try {
      let photo_url = '';
      if (photoFile) {
        const { data, error } = await supabase.storage
          .from('values-photos')
          .upload(`public/${Date.now()}-${photoFile.name}`, photoFile);

        if (!error && data) {
          const { data: urlData } = supabase.storage
            .from('values-photos')
            .getPublicUrl(data.path);
          photo_url = urlData.publicUrl;
        }
      }

      updateState({ story, photo_url });
      setShowChat(true);
    } catch (error) {
      console.error('Error uploading photo:', error);
      alert('Failed to upload photo. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleChatComplete = async (valuesCard: any) => {
    try {
      const { title, description, attention_patterns, practices } = valuesCard;

      // Check if we already have a card ID in state
      if (state.values_card?.id) {
        // Update existing card
        const { data, error } = await supabase
          .from('values_cards')
          .update({
            title,
            description,
            attention_patterns,
            practices,
          })
          .eq('id', state.values_card.id)
          .select()
          .single();

        if (error) throw error;

        // Update state with the updated card data
        updateState({
          values_card: data as ValuesCardType,
        });
      } else {
        // Create new card
        const { data, error } = await supabase
          .from('values_cards')
          .insert([{
            title,
            description,
            attention_patterns,
            practices,
            story: state.story,
            photo_url: state.photo_url,
            is_public: false,
          }])
          .select()
          .single();

        if (error) throw error;

        // Update state with the new card data
        updateState({
          values_card: data as ValuesCardType,
        });
      }
    } catch (err) {
      console.error('Error saving card:', err);
      alert('An error occurred while saving your values card. Please try again.');
    }
  };

  const handleShare = (shareKey: string) => {
    updateState({ share_key: shareKey });
  };

  return (
    <div className="min-h-screen bg-background py-12">
      <div className="max-w-4xl mx-auto px-4">
        {showChat || state.values_card ? (
          <div className="rounded-lg bg-card shadow-lg">
            <header className="p-6 border-b border-gray-200">
              <h1 className="text-2xl font-bold text-text-dark text-center">
                Explore Your Values
              </h1>
            </header>
            <main className="p-cardPadding">
              {showChat && !state.values_card && (
                <Card>
                  <CardHeader>
                    <h1 className="text-2xl font-bold text-gray-900">Continue Your Story</h1>
                  </CardHeader>
                  <CardContent>
                    <ChatUI
                      onComplete={handleChatComplete}
                      className="min-h-[400px]"
                      initialMessage={`Thank you for sharing this story: "${story}". Let's explore what makes it meaningful and the values it highlights. What stands out most to you about this experience?`}
                    />
                  </CardContent>
                </Card>
              )}
              {state.values_card && (
                <ValuesCard
                  {...state.values_card}
                  photo_url={state.photo_url}
                  onShare={handleShare}
                />
              )}
            </main>
          </div>
        ) : (
          <div className="rounded-lg bg-card shadow-lg">
            <header className="p-6 border-b border-gray-200">
              <h1 className="text-2xl font-bold text-text-dark text-center">
                Share a Meaningful Story
              </h1>
            </header>
            <main className="p-cardPadding space-y-6">
              <textarea
                value={story}
                onChange={(e) => setStory(e.target.value)}
                placeholder="Tell me about a meaningful experience..."
                className="w-full p-4 rounded-lg border border-gray-300 bg-white text-black placeholder-gray-600 focus:ring-2 focus:ring-blue-500 focus:outline-none min-h-[200px]"
              />
              <div>
                <label className="block text-sm text-gray-700 mb-2">
                  Add a photo (optional)
                </label>
                <input
                  type="file"
                  accept="image/*"
                  onChange={(e) => setPhotoFile(e.target.files?.[0] || null)}
                  className="text-sm text-gray-700 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
                />
              </div>
              <button
                onClick={handleStorySubmit}
                disabled={!story.trim() || isSubmitting}
                className={`w-full py-2 rounded-lg font-medium transition-colors ${
                  story.trim() && !isSubmitting
                    ? 'bg-blue-500 text-white hover:bg-blue-600'
                    : 'bg-gray-300 text-gray-500 cursor-not-allowed'
                }`}
              >
                {isSubmitting ? 'Submitting...' : 'Continue'}
              </button>
            </main>
          </div>
        )}
      </div>
    </div>
  );
}",
    "app/api/reflection/chat/route.ts": "// app/api/reflection/chat/route.ts
// app/api/reflection/chat/route.ts
import OpenAI from 'openai';
import { NextResponse } from 'next/server';

const openai = new OpenAI({
  apiKey: process.env.OPENAI_API_KEY!,
});

const systemPrompt = `You are a compassionate guide helping people discover their core values through meaningful moments. Your approach:

1. Listen deeply to their story and reflect back what seems meaningful
2. Ask gentle, probing questions to explore deeper themes
3. Notice patterns and metaphors they use
4. Help identify what they pay attention to in these meaningful moments

Approach:
- Use warmth and emotional mirroring
- Offer multiple interpretations to help them explore
- Build on metaphors they introduce
- Move gradually from story to structured insights

Format responses as a natural conversation, but work towards identifying:
- What they notice in meaningful moments
- What patterns of attention bring them alive
- What ways of being feel most authentic

End goal is to help them articulate a clear values card with:
- A title capturing the essence
- 3-4 specific things they pay attention to
- Always grounded in their actual experience

When they express clarity about a value, respond with:
<values-card>
{
  "title": "Core value as a simple phrase",
  "description": "2-3 sentences connecting their story to this value",
  "attention_patterns": ["3-4 specific things they naturally notice or care about"],
  "practices": ["2-3 ways to live this value more fully"]
}
</values-card>

Otherwise, continue the natural conversation helping them explore what matters.`;

export async function POST(req: Request) {
  try {
    const { messages } = await req.json();

    const completion = await openai.chat.completions.create({
      model: 'gpt-4-turbo',
      stream: true,
      messages: [
        { role: 'system', content: systemPrompt },
        ...messages,
      ],
    });

    // Create a readable stream
    const stream = new ReadableStream({
      async start(controller) {
        try {
          for await (const chunk of completion) {
            const content = chunk.choices[0]?.delta?.content || '';
            if (content) {
              controller.enqueue(new TextEncoder().encode(content));
            }
          }
        } catch (err) {
          console.error('Error streaming response:', err);
          controller.error(err);
        } finally {
          controller.close();
        }
      },
    });

    return new Response(stream, {
      headers: {
        'Content-Type': 'text/plain; charset=utf-8',
        'Cache-Control': 'no-cache',
      },
    });
  } catch (error) {
    console.error('Error in API route:', error);
    return NextResponse.json({ error: 'Failed to process the request' }, { status: 500 });
  }
}",
    "components/reflection/ValuesCard.tsx": "//components/reflection/ValuesCard.tsx
'use client';

import { useState } from 'react';
import { Card, CardHeader, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Textarea } from '@/components/ui/textarea';
import { supabase } from '@/lib/supabase';
import type { ValuesCard as ValuesCardType } from '@/lib/types';

interface ValuesCardProps extends Partial<ValuesCardType> {
  onShare?: (shareKey: string) => void;
}

export default function ValuesCard({
  id,
  title,
  description,
  attention_patterns = [],
  practices = [],
  photo_url,
  share_key: initialShareKey,
  value_promise: initialValuePromise,
  onShare,
}: ValuesCardProps) {
  const [isSharing, setIsSharing] = useState(false);
  const [shareKey, setShareKey] = useState<string | null>(initialShareKey || null);
  const [valuePromise, setValuePromise] = useState(initialValuePromise || '');
  const [isEditingPromise, setIsEditingPromise] = useState(!initialValuePromise);
  const [isImageModalOpen, setIsImageModalOpen] = useState(false);
  const [isSavingPromise, setIsSavingPromise] = useState(false);

  const handleShare = async () => {
    if (!id) {
      console.error('No card ID available');
      return;
    }

    setIsSharing(true);
    try {
      const newShareKey = crypto.randomUUID();

      const { data, error } = await supabase
        .from('values_cards')
        .update({ 
          share_key: newShareKey,
          is_public: true 
        })
        .eq('id', id)
        .select('share_key')
        .single();

      if (error) throw error;

      if (data?.share_key) {
        setShareKey(data.share_key);
        if (onShare) onShare(data.share_key);
      }
    } catch (err) {
      console.error('Database error:', err);
      alert('Sorry, there was an error sharing your card. Please try again.');
    } finally {
      setIsSharing(false);
    }
  };

  const saveValuePromise = async () => {
    if (!id) {
      console.error('No card ID available');
      return;
    }

    setIsSavingPromise(true);
    try {
      const { error } = await supabase
        .from('values_cards')
        .update({ value_promise: valuePromise })
        .eq('id', id);

      if (error) throw error;
      setIsEditingPromise(false);
    } catch (err) {
      console.error('Error saving promise:', err);
      alert('Sorry, there was an error saving your promise. Please try again.');
    } finally {
      setIsSavingPromise(false);
    }
  };

  return (
    <Card className="w-full bg-white shadow-lg">
      <CardHeader className="border-b border-gray-100 bg-gray-50">
        <h2 className="text-3xl font-bold text-gray-900 text-center">{title}</h2>
      </CardHeader>
      <CardContent className="space-y-6 p-8">
        {/* Image Section */}
        {photo_url && (
          <div 
            onClick={() => setIsImageModalOpen(true)}
            className="w-full aspect-video relative overflow-hidden rounded-lg shadow-inner cursor-pointer"
          >
            <img
              src={photo_url}
              alt="Meaningful moment"
              className="object-cover w-full h-full hover:opacity-90 transition-opacity"
            />
          </div>
        )}

        <p className="text-gray-700 text-lg leading-relaxed">{description}</p>

        {/* Attention Patterns Section */}
        <div className="space-y-3">
          <h3 className="font-semibold text-gray-900 text-xl">What you pay attention to:</h3>
          <ul className="space-y-2">
            {attention_patterns.map((pattern, i) => (
              <li key={i} className="text-gray-700 flex items-start">
                <span className="text-blue-500 mr-2">â€¢</span>
                {pattern}
              </li>
            ))}
          </ul>
        </div>

        {/* Practices Section */}
        <div className="space-y-3">
          <h3 className="font-semibold text-gray-900 text-xl">Ways to live this value:</h3>
          <ul className="space-y-2">
            {practices.map((practice, i) => (
              <li key={i} className="text-gray-700 flex items-start">
                <span className="text-blue-500 mr-2">â€¢</span>
                {practice}
              </li>
            ))}
          </ul>
        </div>

        {/* Value Promise Section */}
        <div className="space-y-3 border-t pt-6">
          <div className="flex justify-between items-center">
            <h3 className="font-semibold text-gray-900 text-xl">Your Value Promise</h3>
            {!isEditingPromise && (
              <Button
                onClick={() => setIsEditingPromise(true)}
                variant="outline"
                size="sm"
              >
                Edit Promise
              </Button>
            )}
          </div>

          {isEditingPromise ? (
            <div className="space-y-3">
              <Textarea
                value={valuePromise}
                onChange={(e) => setValuePromise(e.target.value)}
                placeholder="How will you bring this value to life in the coming year?"
                className="min-h-[100px]"
              />
              <div className="flex gap-2">
                <Button
                  onClick={saveValuePromise}
                  disabled={isSavingPromise}
                  className="flex-1 bg-blue-600 hover:bg-blue-700 text-white"
                >
                  {isSavingPromise ? 'Saving...' : 'Save Promise'}
                </Button>
                <Button
                  onClick={() => {
                    setIsEditingPromise(false);
                    setValuePromise(initialValuePromise || '');
                  }}
                  variant="outline"
                  className="flex-1"
                  disabled={isSavingPromise}
                >
                  Cancel
                </Button>
              </div>
            </div>
          ) : (
            <div className="bg-gray-50 p-4 rounded">
              <p className="text-gray-700">
                {valuePromise || "Click 'Edit Promise' to add your commitment to living this value."}
              </p>
            </div>
          )}
        </div>

        {/* Share Section */}
        <div className="space-y-4 border-t pt-6">
          <div className="flex items-center justify-between">
            <h3 className="font-semibold text-gray-900">Share Your Values Card</h3>
            {!shareKey && (
              <Button
                onClick={handleShare}
                disabled={isSharing}
                className="bg-blue-600 hover:bg-blue-700 text-white"
              >
                {isSharing ? 'Creating Link...' : 'Create Share Link'}
              </Button>
            )}
          </div>

          {shareKey && (
            <div className="bg-gray-50 p-4 rounded-lg space-y-4">
              <div>
                <p className="text-sm text-gray-600 mb-2">
                  Share this link to view your values card:
                </p>
                <code className="block w-full bg-white p-3 rounded border text-sm break-all">
                  {`${window.location.origin}/card/${shareKey}`}
                </code>
              </div>
              <div>
                <p className="text-sm text-gray-600 mb-2">
                  Share this link to get friends' perspectives:
                </p>
                <code className="block w-full bg-white p-3 rounded border text-sm break-all">
                  {`${window.location.origin}/friend/${shareKey}`}
                </code>
              </div>
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}",
    "components/reflection/shared/ChatUI.tsx": "// components/reflection/shared/ChatUI.tsx
'use client';

import { useRef, useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Textarea } from '@/components/ui/textarea';
import { Send } from 'lucide-react';
import { cn } from '@/lib/utils';

interface ChatUIProps {
  onComplete?: (valuesCard: any) => void;
  className?: string;
  initialMessage?: string;
}

export function ChatUI({ onComplete, className, initialMessage }: ChatUIProps) {
  const [messages, setMessages] = useState(
    initialMessage ? [{ role: 'assistant', content: initialMessage }] : []
  );
  const [input, setInput] = useState('');
  const [isThinking, setIsThinking] = useState(false);
  const chatEndRef = useRef<HTMLDivElement | null>(null);

  // Scroll to bottom whenever messages are updated
  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleMessage = (text: string) => {
    // Check for values card format
    const cardMatch = text.match(/<values-card>(.*?)<\/values-card>/s);
    if (cardMatch) {
      try {
        const valuesCard = JSON.parse(cardMatch[1]);
        if (onComplete) {
          onComplete(valuesCard);
        }
        // Return just the confirmation message without the JSON
        return "I've captured the values from our conversation in a card. Would you like to make a promise about how you'll live this value in the coming year?";
      } catch (e) {
        console.error('Error parsing values card:', e);
      }
    }
    return text;
  };

  const handleSendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { role: 'user', content: input.trim() };
    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setIsThinking(true);

    try {
      const response = await fetch('/api/reflection/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ 
          messages: [...messages, userMessage]
        }),
      });

      if (!response.body) return;

      const reader = response.body.getReader();
      const decoder = new TextDecoder();
      let assistantMessage = '';

      while (true) {
        const { done, value } = await reader.read();
        if (done) break;

        const chunk = decoder.decode(value, { stream: true });
        assistantMessage += chunk;

        // Process the message for display
        const displayMessage = handleMessage(assistantMessage);

        setMessages(prev => {
          const updated = [...prev];
          const lastMessage = updated[updated.length - 1];
          if (lastMessage.role === 'assistant') {
            lastMessage.content = displayMessage;
          } else {
            updated.push({ role: 'assistant', content: displayMessage });
          }
          return updated;
        });
      }

    } catch (err) {
      console.error('Error:', err);
      setMessages(prev => [
        ...prev,
        { 
          role: 'assistant', 
          content: "I'm sorry, I encountered an error. Please try again." 
        }
      ]);
    } finally {
      setIsThinking(false);
    }
  };

  const handleKeyDown = (e: React.KeyboardEvent<HTMLTextAreaElement>) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  return (
    <div className={cn('flex flex-col space-y-4', className)}>
      <div className="flex-1 overflow-y-auto space-y-4 min-h-[300px] max-h-[500px] p-4 rounded-lg bg-white">
        {messages.map((message, index) => (
          <div
            key={index}
            className={cn(
              'flex w-full',
              message.role === 'user' ? 'justify-end' : 'justify-start'
            )}
          >
            <div
              className={cn(
                'rounded-lg px-4 py-2 max-w-[80%]',
                message.role === 'user'
                  ? 'bg-blue-500 text-white'
                  : 'bg-gray-100 text-gray-900'
              )}
            >
              {message.content}
            </div>
          </div>
        ))}
        <div ref={chatEndRef} />
      </div>

      <form
        onSubmit={(e) => {
          e.preventDefault();
          handleSendMessage();
        }}
        className="flex items-end gap-2"
      >
        <Textarea
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyDown}
          placeholder="Share your thoughts..."
          className="flex-1 min-h-[80px] p-2 rounded-lg border border-gray-200 focus:outline-none focus:ring-2 focus:ring-blue-500"
          disabled={isThinking}
        />
        <Button
          type="submit"
          className="flex items-center gap-2 bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-lg"
          disabled={isThinking || !input.trim()}
        >
          <Send className="w-4 h-4" />
          {isThinking ? 'Thinking...' : 'Send'}
        </Button>
      </form>
    </div>
  );
}",
    "contexts/ReflectionContext.tsx": "// contexts/ReflectionContext.tsx
'use client';

import { createContext, useContext, useState } from 'react';

interface ValuesCard {
  title: string;
  description: string;
  patterns: string[];
  suggested_practices: string[];
}

interface ReflectionState {
  story: string;
  photo_url?: string;
  values_card?: ValuesCard;
  share_key?: string;
}

const defaultState: ReflectionState = {
  story: '',
};

const ReflectionContext = createContext<{
  state: ReflectionState;
  updateState: (updates: Partial<ReflectionState>) => void;
} | null>(null);

export function ReflectionProvider({ children }: { children: React.ReactNode }) {
  const [state, setState] = useState<ReflectionState>(defaultState);

  const updateState = (updates: Partial<ReflectionState>) => {
    setState(prev => ({
      ...prev,
      ...updates
    }));
  };

  return (
    <ReflectionContext.Provider value={{ state, updateState }}>
      {children}
    </ReflectionContext.Provider>
  );
}

export const useReflection = () => {
  const context = useContext(ReflectionContext);
  if (!context) {
    throw new Error('useReflection must be used within a ReflectionProvider');
  }
  return context;
};",
