    "app/api/reflection/chat/route.ts": "// app/api/reflection/chat/route.ts
// app/api/reflection/chat/route.ts
import OpenAI from 'openai';
import { NextResponse } from 'next/server';

const openai = new OpenAI({
  apiKey: process.env.OPENAI_API_KEY!,
});

const systemPrompt = `You are a compassionate guide helping people discover their core values through meaningful moments. Your approach:

1. Listen deeply to their story and reflect back what seems meaningful
2. Ask gentle, probing questions to explore deeper themes
3. Notice patterns and metaphors they use
4. Help identify what they pay attention to in these meaningful moments

Approach:
- Use warmth and emotional mirroring
- Offer multiple interpretations to help them explore
- Build on metaphors they introduce
- Move gradually from story to structured insights

Format responses as a natural conversation, but work towards identifying:
- What they notice in meaningful moments
- What patterns of attention bring them alive
- What ways of being feel most authentic

End goal is to help them articulate a clear values card with:
- A title capturing the essence
- 3-4 specific things they pay attention to
- Always grounded in their actual experience

When they express clarity about a value, respond with:
<values-card>
{
  "title": "Core value as a simple phrase",
  "description": "2-3 sentences connecting their story to this value",
  "attention_patterns": ["3-4 specific things they naturally notice or care about"],
  "practices": ["2-3 ways to live this value more fully"]
}
</values-card>

Otherwise, continue the natural conversation helping them explore what matters.`;

export async function POST(req: Request) {
  try {
    const { messages } = await req.json();

    const completion = await openai.chat.completions.create({
      model: 'gpt-4-turbo',
      stream: true,
      messages: [
        { role: 'system', content: systemPrompt },
        ...messages,
      ],
    });

    // Create a readable stream
    const stream = new ReadableStream({
      async start(controller) {
        try {
          for await (const chunk of completion) {
            const content = chunk.choices[0]?.delta?.content || '';
            if (content) {
              controller.enqueue(new TextEncoder().encode(content));
            }
          }
        } catch (err) {
          console.error('Error streaming response:', err);
          controller.error(err);
        } finally {
          controller.close();
        }
      },
    });

    return new Response(stream, {
      headers: {
        'Content-Type': 'text/plain; charset=utf-8',
        'Cache-Control': 'no-cache',
      },
    });
  } catch (error) {
    console.error('Error in API route:', error);
    return NextResponse.json({ error: 'Failed to process the request' }, { status: 500 });
  }
}",
