  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [isImageModalOpen, setIsImageModalOpen] = useState(false);

  useEffect(() => {
    async function loadCard() {
      try {
        const { data, error } = await supabase
          .from('values_cards')
          .select('id, created_at, story, description, attention_patterns, practices, photo_url, share_key, is_public, value_promise')
          .eq('share_key', params.shareKey)
          .single();

        if (error) throw error;
        if (data) {
          setCard(data as ValuesCard);
        }
      } catch (err) {
        console.error('Error loading card:', err);
        setError('Unable to load the values card');
