              {/* Story Section */}
              <div className="space-y-3">
                <h3 className="font-semibold text-gray-900 text-xl">Story</h3>
                <p className="text-gray-700 text-lg leading-relaxed">{card.story}</p>
              </div>

              {/* Attention Patterns Section */}
              <div className="space-y-3">
                <h3 className="font-semibold text-gray-900 text-xl">What You Pay Attention To</h3>
                <ul className="space-y-2">
                  {card.attention_patterns.map((pattern, i) => (
                    <li key={i} className="text-gray-700 flex items-start">
                      <span className="text-blue-500 mr-2">â€¢</span>
                      {pattern}
                    </li>
                  ))}
                </ul>
              </div>

              {/* Practices Section */}
              <div className="space-y-3">
                <h3 className="font-semibold text-gray-900 text-xl">Ways to Live This Value</h3>
                <ul className="space-y-2">
                  {card.practices.map((practice, i) => (
                    <li key={i} className="text-gray-700 flex items-start">
                      <span className="text-blue-500 mr-2">â€¢</span>
                      {practice}
                    </li>
                  ))}
                </ul>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>

      {/* Image Modal */}
      {isImageModalOpen && card.photo_url && (
        <div 
          className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4"
          onClick={() => setIsImageModalOpen(false)}
        >
          <div className="max-w-4xl w-full h-full relative">
            <img
              src={card.photo_url}
              alt="Value-related photo"
              className="object-contain w-full h-full"
            />
            <button
              className="absolute top-4 right-4 bg-white rounded-full p-2 shadow-lg"
              onClick={(e) => {
                e.stopPropagation();
                setIsImageModalOpen(false);
              }}
            >
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>
        </div>
      )}
    </>
  );
}

