  values_seen: string;
  created_at: string;
}

interface ValuesCard {
  title: string;
  description: string;
  attention_patterns: string[];
  practices: string[];
  photo_url?: string;
  value_promise?: string;
  friend_perspectives?: FriendPerspective[];
}

export default function CardViewPage({ params }: { params: { shareKey: string } }) {
  const [card, setCard] = useState<ValuesCard | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [isImageModalOpen, setIsImageModalOpen] = useState(false);
  const router = useRouter();

  useEffect(() => {
    async function loadCard() {
      try {
        const { data, error } = await supabase
          .from('values_cards')
          .select(`
            *,
            friend_perspectives (
              id,
              memory,
              observation,
              impact,
              values_seen,
              created_at
            )
          `)
          .eq('share_key', params.shareKey)
          .single();

        if (error) throw error;
        if (data) {
          setCard(data as ValuesCard);
        }
      } catch (err) {
        console.error('Error loading card:', err);
        setError('Unable to load the values card');
      } finally {
        setLoading(false);
      }
    }

    loadCard();
  }, [params.shareKey]);

  const handleSharePerspective = () => {
    router.push(`/friend/${params.shareKey}`);
