// contexts/ReflectionContext.tsx
'use client';

import { createContext, useContext, useState } from 'react';
import { ValuesCard } from '@/lib/types';

interface ReflectionState {
  story: string;
  photo_url?: string;
  values_card?: ValuesCard;
  share_key?: string;
  session_id?: string;  // Add session_id to state
}

const defaultState: ReflectionState = {
  story: '',
};

const ReflectionContext = createContext<{
  state: ReflectionState;
  updateState: (updates: Partial<ReflectionState>) => void;
} | null>(null);

export function ReflectionProvider({ children }: { children: React.ReactNode }) {
  const [state, setState] = useState<ReflectionState>(() => {
    // Initialize session_id on first load
    return {
      ...defaultState,
      session_id: crypto.randomUUID()
    };
  });

  const updateState = (updates: Partial<ReflectionState>) => {
    setState(prev => ({
      ...prev,
      ...updates,
      // Preserve session_id unless explicitly changed
      session_id: updates.session_id || prev.session_id,
      values_card: updates.values_card || prev.values_card
    }));
  };

  return (
    <ReflectionContext.Provider value={{ state, updateState }}>
      {children}
    </ReflectionContext.Provider>
  );
}

export const useReflection = () => {
  const context = useContext(ReflectionContext);
  if (!context) {
    throw new Error('useReflection must be used within a ReflectionProvider');
  }
  return context;
};

// app/reflection/page.tsx
'use client';

import { useState } from 'react';
import { useReflection } from '@/contexts/ReflectionContext';
import { supabase } from '@/lib/supabase';
import { Card, CardHeader, CardContent } from '@/components/ui/card';
import { ChatUI } from '@/components/reflection/shared/ChatUI';
import ValuesCard from '@/components/reflection/ValuesCard';
import { ValuesCard as ValuesCardType } from '@/lib/types';

export default function ReflectionPage() {
  const { state, updateState } = useReflection();
  const [story, setStory] = useState('');
  const [photoFile, setPhotoFile] = useState<File | null>(null);
  const [showChat, setShowChat] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleStorySubmit = async () => {
    if (!story.trim() || isSubmitting) return;

    setIsSubmitting(true);
    try {
      let photo_url = '';
      if (photoFile) {
        const { data, error } = await supabase.storage
          .from('values-photos')
          .upload(`public/${Date.now()}-${photoFile.name}`, photoFile);

        if (!error && data) {
          const { data: urlData } = supabase.storage
            .from('values-photos')
            .getPublicUrl(data.path);
          photo_url = urlData.publicUrl;
        }
      }

      updateState({ story, photo_url });
      setShowChat(true);
    } catch (error) {
      console.error('Error uploading photo:', error);
      alert('Failed to upload photo. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleChatComplete = async (valuesCard: any) => {
    if (!state.session_id) {
      console.error('No session ID available');
      return;
    }

    try {
      const { title, description, attention_patterns, practices } = valuesCard;
      const updates = {
        title,
        description,
        attention_patterns,
        practices,
        story: state.story,
        photo_url: state.photo_url,
        is_public: false,
        session_id: state.session_id  // Include session_id in creation
      };

      // Try to fetch existing card for this session
      const { data: existingCard } = await supabase
        .from('values_cards')
        .select()
        .eq('session_id', state.session_id)
        .maybeSingle();

      let result;
      if (existingCard) {
        // Update existing card
        const { data, error } = await supabase
          .from('values_cards')
          .update(updates)
          .eq('session_id', state.session_id)
          .select()
          .single();

        if (error) throw error;
        result = data;
      } else {
        // Create new card
        const { data, error } = await supabase
          .from('values_cards')
          .insert([updates])
          .select()
          .single();

        if (error) throw error;
        result = data;
      }

      updateState({
        values_card: result as ValuesCardType
      });
    } catch (err) {
      console.error('Error saving card:', err);
      alert('An error occurred while saving your values card. Please try again.');
    }
  };

  const handleUpdateCard = async (updates: Partial<ValuesCardType>) => {
    if (!state.session_id) return false;

    try {
      const { data, error } = await supabase
        .from('values_cards')
        .update(updates)
        .eq('session_id', state.session_id)
        .select()
        .single();

      if (error) throw error;
      if (!data) throw new Error('No data returned from update');

      updateState({
        values_card: { ...state.values_card, ...data } as ValuesCardType
      });

      return true;
    } catch (err) {
      console.error('Error updating card:', err);
      return false;
    }
  };

  // Rest of the component remains the same...
  return (
    // Existing JSX...
  );
}
