    "app/page.tsx": "// app/page.tsx
'use client';

import { useEffect, useState } from 'react';
import { useRouter } from 'next/navigation';
import { Button } from '@/components/ui/button';
import { Card, CardHeader, CardContent } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { supabase } from '@/lib/supabase';

export default function WelcomePage() {
  const router = useRouter();
  const [user, setUser] = useState<any>(null);
  const [email, setEmail] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    // Check current session
    supabase.auth.getSession().then(({ data: { session } }) => {
      if (session?.user) {
        setUser(session.user);
      }
    });

    // Subscribe to auth state changes
    const { data: { subscription } } = supabase.auth.onAuthStateChange((_event, session) => {
      setUser(session?.user || null);
    });

    return () => subscription.unsubscribe();
  }, []);

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);
    setMessage('');

    try {
      const { error } = await supabase.auth.signInWithOtp({
        email,
        options: {
          emailRedirectTo: `${window.location.origin}/profile`,
        },
      });

      if (error) throw error;
      setMessage('Check your email for the login link!');
    } catch (error) {
      if (error instanceof Error) {
        setMessage(error.message);
      } else {
        setMessage('An error occurred. Please try again.');
      }
    } finally {
      setIsLoading(false);
    }
  };

  const startReflection = () => {
    router.push('/reflection');
  };

  const goToProfile = () => {
    if (!user) {
      setMessage('Please sign in to access your profile.');
      return;
    }
    router.push('/profile');
  };

  const createShareLink = async () => {
    if (!user) {
      setMessage('You need to sign in to create a share link.');
      return;
    }

    try {
      const shareKey = crypto.randomUUID();
      const { data, error } = await supabase
        .from('values_cards')
        .insert([
          {
            title: 'Value Reflections',
            description: 'I\'d like to hear your perspective on what you value about me.',
            user_id: user.id,
            share_key: shareKey,
            is_public: true,
          },
        ])
        .select()
        .single();

      if (error) throw error;

      const shareLink = `${window.location.origin}/friend/${shareKey}`;
      await navigator.clipboard.writeText(shareLink);
      setMessage('Share link copied to clipboard!');
    } catch (err) {
      console.error('Error creating share link:', err);
      setMessage('Failed to create share link. Please try again.');
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-12">
      <div className="max-w-4xl mx-auto px-4">
        <Card className="bg-white shadow-lg rounded-lg">
          <CardHeader className="p-6 border-b border-gray-200">
            <h1 className="text-3xl font-bold text-gray-800 text-center">
              Welcome to Values Reflection
            </h1>
          </CardHeader>
          <CardContent className="p-6">
            <div className="text-center space-y-8">
              <p className="text-gray-600 text-lg">
                Start reflecting on your values and insights, you can log in now to save your reflections, or reflect and create a link to save without a profile.
              </p>
              <div className="space-y-4">
                <Button
                  onClick={startReflection}
                  className="w-full bg-blue-500 hover:bg-blue-600 text-white py-3 rounded-lg shadow-md"
                >
                  Reflect Now
                </Button>
                {user ? (
                  <>
                    <div className="bg-blue-50 p-4 rounded-lg">
                      <p className="text-blue-800">
                        Welcome back, {user.email}!
                      </p>
                    </div>
                    <Button
                      onClick={goToProfile}
                      className="w-full bg-green-500 hover:bg-green-600 text-white py-3 rounded-lg shadow-md"
                    >
                      View Your Profile
                    </Button>
                    <Button
                      onClick={createShareLink}
                      className="w-full bg-purple-500 hover:bg-purple-600 text-white py-3 rounded-lg shadow-md"
                    >
                      Create Share Link
                    </Button>
                  </>
                ) : (
                  <form onSubmit={handleLogin} className="space-y-4">
                    <Input
                      type="email"
                      placeholder="Enter your email"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      required
                      className="w-full px-4 py-2 text-gray-900 bg-gray-100 border border-gray-300 rounded-lg"
                    />
                    <Button
                      type="submit"
                      className="w-full bg-blue-500 hover:bg-blue-600 text-white py-3 rounded-lg shadow-md"
                      disabled={isLoading}
                    >
                      {isLoading ? 'Sending...' : 'Send email to login/sign-sp here'}
                    </Button>
                  </form>
                )}
                {message && (
                  <p
                    className={`text-sm mt-2 ${
                      message.includes('Check your email') || message.includes('Share link copied')
                        ? 'text-green-600'
                        : 'text-red-600'
                    }`}
                  >
                    {message}
                  </p>
                )}
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}
",
    "app/reflection/page.tsx": "'use client';
import { VoiceInput } from '@/components/voice/VoiceInput';
import { useEffect, useState } from 'react';
import { useRouter } from 'next/navigation';
import { useReflection } from '@/contexts/ReflectionContext';
import { supabase } from '@/lib/supabase';
import { Card, CardHeader, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { ChatUI } from '@/components/reflection/shared/ChatUI';
import ValuesCard from '@/components/reflection/ValuesCard';
import { ValuesCard as ValuesCardType } from '@/lib/types';
import { REFLECTION_PROMPT } from '@/lib/prompts';
import { Home, User } from 'lucide-react';
import Link from 'next/link';

export default function ReflectionPage() {
  const router = useRouter();
  const { state, updateState, resetState } = useReflection();
  const [story, setStory] = useState('');
  const [photoFile, setPhotoFile] = useState<File | null>(null);
  const [showChat, setShowChat] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isSaving, setIsSaving] = useState(false);
  const [currentUser, setCurrentUser] = useState<any>(null);
  const [currentCardId, setCurrentCardId] = useState<string | null>(null);
  const [showValuesCard, setShowValuesCard] = useState(false);

  useEffect(() => {
    // Only reset state if we don't already have card data
    if (!state.card_id) {
      resetState();
    }

    supabase.auth.getSession().then(({ data: { session } }) => {
      if (session?.user) {
        setCurrentUser(session.user);
      }
    });

    const { data: { subscription } } = supabase.auth.onAuthStateChange((_event, session) => {
      setCurrentUser(session?.user || null);
    });

    return () => subscription.unsubscribe();
  }, []); // Remove resetState from dependencies

  const handleStorySubmit = async () => {
    if (!story.trim() || isSubmitting) return;

    setIsSubmitting(true);
    try {
      let photo_url = '';
      if (photoFile) {
        const { data, error } = await supabase.storage
          .from('values-photos')
          .upload(`public/${Date.now()}-${photoFile.name}`, photoFile);

        if (!error && data) {
          const { data: urlData } = supabase.storage
            .from('values-photos')
            .getPublicUrl(data.path);
          photo_url = urlData.publicUrl;
        }
      }

      const { data: cardData, error: cardError } = await supabase
        .from('values_cards')
        .insert([{
          story,
          photo_url,
          share_key: crypto.randomUUID(),
          user_id: currentUser?.id || null,
          title: 'Reflection in Progress',
          description: 'This reflection is being created...',
          attention_patterns: [],
          practices: [],
          is_public: false
        }])
        .select()
        .single();

      if (cardError) throw cardError;

      console.log('Created card with ID:', cardData.id);
      setCurrentCardId(cardData.id);

      updateState({
        story,
        photo_url,
        card_id: cardData.id,
        share_key: cardData.share_key,
      });

      setShowChat(true);
      setShowValuesCard(false); // Reset values card view
    } catch (error) {
      console.error('Error starting reflection:', error);
      alert('Failed to start reflection. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleChatComplete = async (valuesCard: any, rawText: string) => {
    const cardId = currentCardId;
    console.log('handleChatComplete called with cardId:', cardId);

    if (!cardId) {
      console.error('No card ID available for update');
      return;
    }

    if (isSaving) {
      console.log('Already saving, skipping update');
      return;
    }

    setIsSaving(true);

    try {
      console.log('Updating card:', cardId, 'with values:', valuesCard);
      const { title, description, attention_patterns, practices } = valuesCard;

      const updates = {
        title: title || 'Untitled Card',
        description: description || '',
        attention_patterns: attention_patterns || [],
        practices: practices || [],
      };

      const { data, error } = await supabase
        .from('values_cards')
        .update(updates)
        .eq('id', cardId)
        .select()
        .single();

      if (error) throw error;
      if (!data) throw new Error('No data returned from update');

      console.log('Card updated successfully:', data);

      // Create the full card data
      const fullCard = {
        ...data,
        id: cardId,
        story: state.story,
        photo_url: state.photo_url,
        share_key: data.share_key,
        value_promise: '',
      };

      // Update state and show values card
      updateState({ values_card: fullCard });
      setTimeout(() => {
        setShowChat(false);
        setShowValuesCard(true);
      }, 100);

    } catch (err) {
      console.error('Error updating card:', err);
      alert('An error occurred while saving your reflection. Please try again.');
    } finally {
      setIsSaving(false);
    }
  };

  const handleUpdateCard = async (updates: Partial<ValuesCardType>): Promise<boolean> => {
    if (!state.card_id) {
      console.error('No card ID available for update');
      return false;
    }

    try {
      console.log('Updating card with promise:', state.card_id, updates);
      const { data, error } = await supabase
        .from('values_cards')
        .update(updates)
        .eq('id', state.card_id)
        .select()
        .single();

      if (error) throw error;
      if (!data) throw new Error('No data returned from update');

      console.log('Card updated successfully:', data);

      const updatedCard = {
        ...state.values_card,
        ...data,
        story: state.story,
        photo_url: state.photo_url,
      };

      updateState({ values_card: updatedCard });
      return true;
    } catch (err) {
      console.error('Error updating card:', err);
      return false;
    }
  };

  return (
    <div className="min-h-screen bg-background py-12">
      <div className="max-w-4xl mx-auto px-4">
        <div className="mb-6 flex justify-between items-center">
          <Link href="/">
            <Button variant="outline" className="flex items-center gap-2">
              <Home className="w-4 h-4" />
              Home
            </Button>
          </Link>
          <Link href="/profile">
            <Button variant="outline" className="flex items-center gap-2">
              <User className="w-4 h-4" />
              Profile
            </Button>
          </Link>
        </div>

        {showValuesCard && state.values_card ? (
          <div className="rounded-lg bg-card shadow-lg">
            <header className="p-6 border-b border-gray-200">
              <h1 className="text-2xl font-bold text-text-dark text-center">
                Your Values Card
              </h1>
            </header>
            <main className="p-6">
              <ValuesCard
                {...state.values_card}
                photo_url={state.photo_url}
                onUpdate={handleUpdateCard}
              />
            </main>
          </div>
        ) : showChat ? (
          <div className="rounded-lg bg-card shadow-lg">
            <header className="p-6 border-b border-gray-200">
              <h1 className="text-2xl font-bold text-text-dark text-center">
                Explore Your Values
              </h1>
            </header>
            <main className="p-6">
              <Card>
                <CardHeader>
                  <h1 className="text-2xl font-bold text-gray-900">
                    Continue Your Story
                  </h1>
                </CardHeader>
                <CardContent>
                  <ChatUI
                    onComplete={handleChatComplete}
                    className="min-h-[400px]"
                    initialMessage={`Thank you for sharing this story. Let's explore what makes it meaningful and the values it highlights. What stands out most to you about this experience?`}
                    systemPrompt={REFLECTION_PROMPT}
                    cardId={currentCardId}
                  />
                </CardContent>
              </Card>
            </main>
          </div>
        ) : (
          <div className="rounded-lg bg-card shadow-lg">
            <header className="p-6 border-b border-gray-200">
              <h1 className="text-2xl font-bold text-text-dark text-center">
                Share a Meaningful Story From The Last Year
              </h1>
            </header>
            <main className="p-6 space-y-6">
            <div className="space-y-4">
  <div className="relative">
    <textarea
      value={story}
      onChange={(e) => setStory(e.target.value)}
      placeholder="Tell me about a meaningful experience, pick something that made you feel something :)"
      className="w-full p-4 rounded-lg border border-gray-300 bg-white text-black placeholder-gray-600 focus:ring-2 focus:ring-blue-500 focus:outline-none min-h-[200px]"
    />
    <div className="absolute top-2 right-2">
      <VoiceInput
        onTranscript={(transcript) => setStory(prev => prev + ' ' + transcript)}
        isDisabled={isSubmitting}
      />
    </div>
  </div>

  <div>
    <label className="block text-sm text-gray-700 mb-2">
      Add a photo (optional)
    </label>
    <input
      type="file"
      accept="image/*"
      onChange={(e) => setPhotoFile(e.target.files?.[0] || null)}
      className="text-sm text-gray-700 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
    />
  </div>

  <button
    onClick={handleStorySubmit}
    disabled={!story.trim() || isSubmitting}
    className={`w-full py-2 rounded-lg font-medium transition-colors ${
      story.trim() && !isSubmitting
        ? 'bg-blue-500 text-white hover:bg-blue-600'
        : 'bg-gray-300 text-gray-500 cursor-not-allowed'
    }`}
  >
    {isSubmitting ? 'Submitting...' : 'Continue'}
  </button>
</div>
            </main>
          </div>
        )}
      </div>
    </div>
  );
}",
    "app/profile/page.tsx": "// app/profile/page.tsx
'use client';

import { useEffect, useState } from 'react';
import { Card, CardHeader, CardContent } from '@/components/ui/card';
import { supabase } from '@/lib/supabase';
import Link from 'next/link';
import type { ValuesCard as ValuesCardType, FriendPerspective } from '@/lib/types';

export default function ProfilePage() {
  const [user, setUser] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [myValues, setMyValues] = useState<ValuesCardType[]>([]);
  const [friendPerspectives, setFriendPerspectives] = useState<FriendPerspective[]>([]);

  useEffect(() => {
    supabase.auth.getSession().then(({ data: { session } }) => {
      if (!session) {
        window.location.href = '/';
        return;
      }
      setUser(session.user);
      loadContent(session.user.id);
    });

    const { data: { subscription } } = supabase.auth.onAuthStateChange((_event, session) => {
      if (!session) {
        window.location.href = '/';
        return;
      }
      setUser(session.user);
      loadContent(session.user.id);
    });

    return () => subscription.unsubscribe();
  }, []);

  const loadContent = async (userId: string) => {
    try {
      // Fetch user's values cards
      const { data: valuesCards, error: valuesError } = await supabase
        .from('values_cards')
        .select('id, title, description, share_key, photo_url')
        .eq('user_id', userId)
        .not('title', 'is', null)
        .not('description', 'is', null)
        .order('created_at', { ascending: false });

      if (valuesError) throw valuesError;

      setMyValues(valuesCards || []);

      // Fetch friend perspectives related to the user's values cards
      const valuesCardIds = valuesCards?.map((card) => card.id) || [];

      if (valuesCardIds.length > 0) {
        const { data: perspectives, error: perspectivesError } = await supabase
          .from('friend_perspectives')
          .select('*')
          .in('card_id', valuesCardIds)
          .order('created_at', { ascending: false });

        if (perspectivesError) throw perspectivesError;

        setFriendPerspectives(perspectives || []);
      }
    } catch (error) {
      console.error('Error loading content:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-background py-12 flex items-center justify-center">
        <div className="animate-pulse text-gray-500">Loading...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background py-6">
      {/* Header */}
      <header className="bg-gray-100 py-4 shadow-md">
        <div className="max-w-6xl mx-auto px-4 flex justify-between items-center">
          <h1 className="text-xl font-bold text-gray-900">My Profile</h1>
          <div className="space-x-4">
            <Link href="/">
              <button className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition">
                Home
              </button>
            </Link>
            <Link href="/reflection">
              <button className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 transition">
                Reflect
              </button>
            </Link>
            <button
              onClick={async () => {
                await supabase.auth.signOut();
                window.location.href = '/';
              }}
              className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600 transition"
            >
              Logout
            </button>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <div className="max-w-6xl mx-auto px-4 py-8 space-y-12">
        {/* My Reflections */}
        <section>
          <h2 className="text-3xl font-bold text-gray-900 mb-6">My Values Reflections</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {myValues.map((card) => (
              <Link key={card.id} href={`/card/${card.share_key}`}>
                <Card className="hover:shadow-lg transition-shadow cursor-pointer h-full bg-white">
                  <CardHeader className="border-b border-gray-100 bg-gray-50">
                    <h3 className="text-xl font-semibold text-gray-900">{card.title}</h3>
                  </CardHeader>
                  <CardContent>
                    <p className="text-gray-600 line-clamp-3">{card.description}</p>
                    {card.photo_url && (
                      <div className="mt-4 aspect-video relative overflow-hidden rounded">
                        <img
                          src={card.photo_url}
                          alt="Reflection moment"
                          className="object-cover w-full h-full"
                        />
                      </div>
                    )}
                  </CardContent>
                </Card>
              </Link>
            ))}
          </div>
        </section>

        {/* Friend Perspectives */}
        <section>
          <h2 className="text-3xl font-bold text-gray-900 mb-6">
            What Your Friends Appreciate About You
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {friendPerspectives.map((perspective) => (
              <Link key={perspective.id} href={`/perspective/${perspective.id}`}>
                <Card className="hover:shadow-lg transition-shadow cursor-pointer h-full bg-white">
                  <CardHeader className="border-b border-gray-100 bg-gray-50">
                    <h3 className="text-xl font-semibold text-gray-900">{perspective.title}</h3>
                  </CardHeader>
                  <CardContent>
