'use client';

import { useState, useEffect, useCallback } from 'react';
import { Button } from '@/components/ui/button';
import { Mic, MicOff, Loader2 } from 'lucide-react';

interface VoiceInputProps {
  onTranscript: (transcript: string) => void;
  isDisabled?: boolean;
  className?: string;
}

export function VoiceInput({ onTranscript, isDisabled = false, className }: VoiceInputProps) {
  const [isListening, setIsListening] = useState(false);
  const [isSupported, setIsSupported] = useState(false);
  const [recognition, setRecognition] = useState<any>(null);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    // First check if the browser supports speech recognition
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition) {
      setError('Speech recognition not supported in this browser');
      return;
    }

    try {
      const recognitionInstance = new SpeechRecognition();

      // Set basic properties
      recognitionInstance.continuous = true;
      recognitionInstance.interimResults = true;
      recognitionInstance.lang = 'en-US'; // Start with en-US

      let currentTranscript = '';

      recognitionInstance.onstart = () => {
        setError(null);
        currentTranscript = '';
        console.log('Speech recognition started');
      };

      recognitionInstance.onresult = (event: any) => {
        let interimTranscript = '';
        let finalTranscript = '';

        for (let i = event.resultIndex; i < event.results.length; ++i) {
          const transcript = event.results[i][0].transcript;
          if (event.results[i].isFinal) {
            finalTranscript += transcript;
          } else {
            interimTranscript += transcript;
          }
        }

        if (finalTranscript) {
          currentTranscript += finalTranscript + ' ';
          onTranscript(currentTranscript.trim());
        }
      };

      recognitionInstance.onerror = (event: any) => {
        console.log('Speech recognition error:', event.error);

        if (event.error === 'language-not-supported') {
          // Try switching to a different language variant
          if (recognitionInstance.lang === 'en-US') {
            recognitionInstance.lang = 'en-GB';
          } else if (recognitionInstance.lang === 'en-GB') {
            recognitionInstance.lang = 'en';
          }

          // Try to restart if we changed the language
          if (isListening) {
            try {
              recognitionInstance.stop();
              setTimeout(() => {
                recognitionInstance.start();
              }, 100);
              return;
            } catch (e) {
              console.error('Error restarting recognition:', e);
            }
          }
        }

        setError(event.error);
        setIsListening(false);
      };

      recognitionInstance.onend = () => {
        console.log('Speech recognition ended');
        setIsListening(false);
      };

      setRecognition(recognitionInstance);
      setIsSupported(true);
    } catch (err) {
      console.error('Error initializing speech recognition:', err);
      setError('Failed to initialize speech recognition');
      setIsSupported(false);
    }

    return () => {
      if (recognition) {
        try {
          recognition.stop();
        } catch (err) {
          console.error('Error stopping recognition:', err);
        }
      }
    };
  }, [onTranscript]);

  const toggleListening = useCallback(async () => {
    if (!recognition || isDisabled) return;

    try {
      if (isListening) {
        recognition.stop();
        setIsListening(false);
      } else {
        setError(null);
        await recognition.start();
        setIsListening(true);
      }
    } catch (err) {
      console.error('Error toggling recognition:', err);
      setError('Failed to start recording');
      setIsListening(false);
    }
  }, [recognition, isListening, isDisabled]);

  if (!isSupported) {
    return (
      <div className="text-sm text-red-500">
        Speech recognition not supported in this browser
      </div>
    );
  }

  return (
    <div className={`flex items-center gap-2 ${className}`}>
      <Button
        onClick={toggleListening}
        disabled={isDisabled}
        variant="outline"
        className={`p-2 ${isListening ? 'bg-red-50 hover:bg-red-100' : ''} ${error ? 'border-red-500' : ''}`}
        title={isListening ? 'Stop recording' : 'Start recording'}
      >
        {isListening ? (
          <>
            <MicOff className="h-5 w-5 text-red-500" />
            <Loader2 className="h-4 w-4 ml-2 animate-spin" />
          </>
        ) : (
          <Mic className={`h-5 w-5 ${error ? 'text-red-500' : 'text-gray-500'}`} />
        )}
      </Button>
      {error && (
        <span className="text-sm text-red-500">
          {error === 'language-not-supported' ? 'Trying alternative language...' : error}
        </span>
      )}
      {isListening && !error && (
        <span className="text-sm text-green-500">
          Listening...
        </span>
      )}
    </div>
  );
}

