    "app/auth/callback/route.ts": "// app/auth/callback/route.ts
import { createRouteHandlerClient } from '@supabase/auth-helpers-nextjs';
import { cookies } from 'next/headers';
import { NextResponse } from 'next/server';

export const dynamic = 'force-dynamic';

export async function GET(request: Request) {
  try {
    const requestUrl = new URL(request.url);
    const cardId = requestUrl.searchParams.get('cardId');
    const returnUrl = requestUrl.searchParams.get('returnUrl');
    const code = requestUrl.searchParams.get('code');
    const access_token = requestUrl.searchParams.get('access_token');
    const refresh_token = requestUrl.searchParams.get('refresh_token');

    console.log('Auth callback received:', { 
      hasCode: !!code,
      hasAccessToken: !!access_token,
      cardId,
      returnUrl 
    });

    // Create supabase client
    const cookieStore = cookies();
    const supabase = createRouteHandlerClient({ cookies: () => cookieStore });

    let session;

    if (access_token) {
      // Handle hash-based auth (from email verification)
      const { data, error } = await supabase.auth.setSession({
        access_token,
        refresh_token: refresh_token || '',
      });

      if (error) throw error;
      session = data.session;
    } else if (code) {
      // Handle code-based auth (from magic link)
      const { data, error } = await supabase.auth.exchangeCodeForSession(code);
      if (error) throw error;
      session = data.session;
    }

    if (!session?.user) {
      // If no session, redirect to login page to handle hash params
      const loginUrl = new URL('/login', requestUrl.origin);
      if (cardId) loginUrl.searchParams.set('cardId', cardId);
      if (returnUrl) loginUrl.searchParams.set('returnUrl', returnUrl);
      return NextResponse.redirect(loginUrl);
    }

    // If we have a cardId and session, update the card
    if (cardId && session.user.id) {
      const { error: cardError } = await supabase
        .from('values_cards')
        .update({ 
          user_id: session.user.id,
          is_public: true 
        })
        .eq('id', cardId);

      if (cardError) {
        console.error('Error updating card:', cardError);
      }
    }

    // Redirect to returnUrl or profile
    const redirectTo = returnUrl 
      ? decodeURIComponent(returnUrl)
      : '/profile';

    return NextResponse.redirect(new URL(redirectTo, requestUrl.origin));

  } catch (error) {
    console.error('Auth callback error:', error);
    const errorUrl = new URL('/login', request.url);
    errorUrl.searchParams.set('error', 'auth_callback_failed');
    errorUrl.searchParams.set('error_description', error.message);
    return NextResponse.redirect(errorUrl);
  }
}",
    "app/login/page.tsx": "// app/login/page.tsx
'use client';

import { useEffect, useState } from 'react';
import { useRouter, useSearchParams } from 'next/navigation';
import { Button } from '@/components/ui/button';
import { Card, CardHeader, CardContent } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { supabase } from '@/lib/supabase';

export default function LoginPage() {
  const router = useRouter();
  const searchParams = useSearchParams();
  const [email, setEmail] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    const handleSessionAndParams = async () => {
      // Check for hash params (#) in URL
      if (window.location.hash) {
        const hashParams = new URLSearchParams(window.location.hash.substring(1));
        const access_token = hashParams.get('access_token');
        const refresh_token = hashParams.get('refresh_token');

        if (access_token) {
          console.log('Found access token in hash, setting up session...');

          // Set up session with tokens
          const { data, error } = await supabase.auth.setSession({
            access_token,
            refresh_token: refresh_token || '',
          });

          if (error) {
            console.error('Error setting session:', error);
            setMessage(error.message);
            return;
          }

          // If we have a session, handle redirect with card
          if (data.session) {
            const cardId = searchParams?.get('cardId');
            const returnUrl = searchParams?.get('returnUrl');

            const callbackUrl = new URL('/auth/callback', window.location.origin);
            callbackUrl.searchParams.set('access_token', access_token);
            if (refresh_token) callbackUrl.searchParams.set('refresh_token', refresh_token);
            if (cardId) callbackUrl.searchParams.set('cardId', cardId);
            if (returnUrl) callbackUrl.searchParams.set('returnUrl', returnUrl);

            window.location.href = callbackUrl.toString();
            return;
          }
        }
      }

      // Handle error params
      const error = searchParams?.get('error');
      const errorDescription = searchParams?.get('error_description');

      if (error) {
        console.log('Login Error:', { error, errorDescription });
        setMessage(decodeURIComponent(errorDescription || 'An error occurred during login'));
        return;
      }

      // Check for existing session
      const { data: { session } } = await supabase.auth.getSession();
      if (session?.user) {
        router.push('/profile');
      }
    };

    handleSessionAndParams();
  }, [router, searchParams]);

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);
    setMessage('');

    try {
      const cardId = searchParams?.get('cardId');
      const returnUrl = searchParams?.get('returnUrl');

      console.log('Starting login with:', { cardId, returnUrl });

      // Set up redirect URL with all necessary params
      const redirectTo = new URL('/auth/callback', window.location.origin);
      if (cardId) redirectTo.searchParams.set('cardId', cardId);
      if (returnUrl) redirectTo.searchParams.set('returnUrl', returnUrl);

      console.log('Redirect URL:', redirectTo.toString());

      const { error } = await supabase.auth.signInWithOtp({
        email,
        options: {
          emailRedirectTo: redirectTo.toString(),
        },
      });

      if (error) throw error;
      setMessage('Check your email for the login link!');
      console.log('Magic link sent successfully');
    } catch (error) {
      console.error('Login error:', error);
      setMessage(error instanceof Error ? error.message : 'An error occurred');
    } finally {
      setIsLoading(false);
    }
  };

  const startReflection = () => {
    router.push('/reflection');
  };

  // Rest of your component remains the same...
  return (
    <div className="min-h-screen bg-gray-50 py-12">
      <div className="max-w-4xl mx-auto px-4">
        <Card className="bg-white shadow-lg rounded-lg">
          <CardHeader className="p-6 border-b border-gray-200">
            <h1 className="text-3xl font-bold text-gray-900 text-center">
              Welcome to Values Reflection
            </h1>
          </CardHeader>
          <CardContent className="p-6">
            <div className="text-center space-y-8">
              <p className="text-gray-600 text-lg">
                Start reflecting on your values and insights. You can reflect right away or create an account to save your reflections.
              </p>

              <div className="space-y-4">
                <Button
                  onClick={startReflection}
                  className="w-full bg-blue-500 hover:bg-blue-600 text-white py-3 rounded-lg shadow-md text-lg"
                >
                  Start Reflecting Now
                </Button>
                <p className="text-gray-500 text-sm">No account required to start</p>
              </div>

              <div className="pt-8 border-t border-gray-200">
                <h2 className="text-xl font-semibold text-gray-900 mb-4">
                  Save your reflections with an account
                </h2>
                <form onSubmit={handleLogin} className="space-y-4">
                  <Input
                    type="email"
                    placeholder="Enter your email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                    className="w-full px-4 py-2 text-gray-900 bg-gray-100 border border-gray-300 rounded-lg"
                  />
                  <Button
                    type="submit"
                    className="w-full bg-gray-800 hover:bg-gray-900 text-white py-3 rounded-lg shadow-md"
                    disabled={isLoading}
                  >
                    {isLoading ? 'Sending...' : 'Send magic link'}
                  </Button>
                </form>
                {message && (
                  <p
                    className={`text-sm mt-4 p-3 rounded ${
                      message.includes('Check your email')
                        ? 'bg-green-50 text-green-800'
                        : 'bg-red-50 text-red-800'
                    }`}
                  >
                    {message}
                  </p>
                )}
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}",
    "app/reflection/page.tsx": "'use client';

import { useEffect, useState } from 'react';
import { useReflection } from '@/contexts/ReflectionContext';
import { supabase } from '@/lib/supabase';
import { Card } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { ChatUI } from '@/components/reflection/shared/ChatUI';
import ValuesCard from '@/components/reflection/ValuesCard';
import { REFLECTION_PROMPT } from '@/lib/prompts';
import { Header } from '@/components/layout/Header';
import { useRouter } from 'next/navigation';

export default function ReflectionPage() {
  const { state, updateState, resetState } = useReflection();
  const [story, setStory] = useState('');
  const [photoFile, setPhotoFile] = useState<File | null>(null);
  const [showChat, setShowChat] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isSaving, setIsSaving] = useState(false);
  const [currentUser, setCurrentUser] = useState<any>(null);
  const [currentCardId, setCurrentCardId] = useState<string | null>(null);
  const [showValuesCard, setShowValuesCard] = useState(false);
  const [showExamplePopup, setShowExamplePopup] = useState(false);
  const [showPhotoGuidance, setShowPhotoGuidance] = useState(false);
  const router = useRouter(); 
  useEffect(() => {
    // Reset state if no card data exists
    if (!state.card_id) resetState();

    supabase.auth.getSession().then(({ data: { session } }) => {
      if (session?.user) setCurrentUser(session.user);
    });

    const { data: { subscription } } = supabase.auth.onAuthStateChange((_event, session) => {
      setCurrentUser(session?.user || null);
    });

    return () => subscription.unsubscribe();
  }, []);

  useEffect(() => {
    // Check for pending story after authentication
    const pendingStory = localStorage.getItem('pendingStory');
    if (pendingStory && currentUser) {
      setStory(pendingStory);
      // Optionally show a notification that their story was recovered
      if (localStorage.getItem('hasPendingPhoto')) {
        alert('Your story has been recovered, but you\'ll need to reselect your photo.');
      }
      // Clear the pending data
      localStorage.removeItem('pendingStory');
      localStorage.removeItem('hasPendingPhoto');
    }
  }, [currentUser]);

  const handleStorySubmit = async () => {
    if (!story.trim() || isSubmitting) return;

    setIsSubmitting(true);
    try {
      // First check for authentication
      const { data: { session } } = await supabase.auth.getSession();

      if (!session?.user) {
        // Save story to local storage
        localStorage.setItem('pendingStory', story);
        if (photoFile) {
          localStorage.setItem('hasPendingPhoto', 'true');
        }

        // Use the router instance from component level
        const loginUrl = new URL('/login', window.location.origin);
        loginUrl.searchParams.set('cardId', currentCardId || '');
        loginUrl.searchParams.set('returnUrl', encodeURIComponent('/profile'));
        router.push(loginUrl.toString());
        return;
      }

      let photo_url = '';
      if (photoFile) {
        const { data, error } = await supabase.storage
          .from('values-photos')
          .upload(`public/${Date.now()}-${photoFile.name}`, photoFile);

        if (error) {
          throw new Error('Failed to upload photo: ' + error.message);
        }

        if (data) {
          const { data: urlData } = supabase.storage
            .from('values-photos')
            .getPublicUrl(data.path);
          photo_url = urlData.publicUrl;
        }
      }

      const { data: cardData, error: cardError } = await supabase
        .from('values_cards')
        .insert([{
          story,
          photo_url,
          share_key: crypto.randomUUID(),
          user_id: session.user.id,  // Now we know we have a valid user
          title: 'Reflection in Progress',
          description: 'This reflection is being created...',
          attention_patterns: [],
          practices: [],
          is_public: false
        }])
        .select()
        .single();

      if (cardError) {
        console.error('Database error:', cardError);
        throw new Error('Failed to save your reflection. Please try again.');
      }

      console.log('Created card with ID:', cardData.id);
      setCurrentCardId(cardData.id);

      updateState({
        story,
        photo_url,
        card_id: cardData.id,
        share_key: cardData.share_key,
      });

      setShowChat(true);
      setShowValuesCard(false);

      // Clear any pending story from local storage
      localStorage.removeItem('pendingStory');
      localStorage.removeItem('hasPendingPhoto');

    } catch (error) {
      console.error('Error starting reflection:', error);
      alert(error instanceof Error ? error.message : 'Failed to start reflection. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };
  const handleChatComplete = async (valuesCard: any, rawText: string) => {
    const cardId = currentCardId;
    if (!cardId) return;

    if (isSaving) return;

    setIsSaving(true);

    try {
      const { title, description, attention_patterns, practices } = valuesCard;

      const updates = {
        title: title || 'Untitled Card',
        description: description || '',
        attention_patterns: attention_patterns || [],
        practices: practices || [],
      };

      const { data, error } = await supabase
        .from('values_cards')
        .update(updates)
        .eq('id', cardId)
        .select()
        .single();

      if (error) throw error;

      const fullCard = {
        ...data,
        id: cardId,
        story: state.story,
        photo_url: state.photo_url,
        share_key: data.share_key,
        value_promise: '',
      };

      updateState({ values_card: fullCard });
      setTimeout(() => {
        setShowChat(false);
        setShowValuesCard(true);
      }, 100);
    } catch (err) {
      console.error('Error updating card:', err);
      alert('An error occurred while saving your reflection. Please try again.');
    } finally {
      setIsSaving(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />
      <main className="max-w-4xl mx-auto px-6 pt-12 pb-8 space-y-6">
        <h1 className="text-2xl font-bold text-gray-900 text-center">
          {showValuesCard
            ? 'Your Values Card'
            : showChat
            ? 'Explore Your Values'
            : 'Share a Meaningful Story'}
        </h1>

        {showPhotoGuidance && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
            <div className="bg-white p-8 rounded-xl max-w-md shadow-2xl">
              <h2 className="text-xl font-semibold mb-4">Choose a Photo That Speaks to Your Heart</h2>
              <p className="text-gray-600 mb-6">
                Look for an image that captures a moment when you felt:
              </p>
              <ul className="list-disc list-inside mt-2 text-gray-600 mb-6">
                <li>Most alive</li>
                <li>Deeply connected</li>
                <li>Proud of yourself</li>
                <li>Kind or compassionate</li>
                <li>Truly at peace</li>
              </ul>
              <p className="italic text-blue-600 mb-6">
                "The best memories don't just remind us of the past—they help us see who we are."
              </p>
              <div className="flex justify-end space-x-4">
                <button
                  onClick={() => setShowPhotoGuidance(false)}
                  className="px-4 py-2 bg-gray-200 rounded-md hover:bg-gray-300 transition"
                >
                  Close
                </button>
                <button
                  onClick={() => {
                    document.getElementById('photoUpload')?.click();
                    setShowPhotoGuidance(false);
                  }}
                  className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 transition"
                >
                  Choose My Photo
                </button>
              </div>
            </div>
          </div>
        )}

        {showExamplePopup && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
            <div className="bg-white p-6 rounded-lg shadow-lg max-w-sm">
              <h2 className="text-lg font-bold text-black">Example Story</h2>
