import { useEffect, useState } from 'react';
import { Card, CardHeader, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { supabase } from '@/lib/supabase';
import type { ValuesCard as ValuesCardType, FriendPerspective } from '@/lib/types';
import { use } from 'react';
import Link from 'next/link';
import { User } from '@supabase/supabase-js';
import Image from 'next/image';

type PageParams = {
  params: Promise<{ sharekey: string }>
};

export default function CardViewPage({ params }: PageParams) {
  const resolvedParams = use(params);
  const sharekey = resolvedParams.sharekey;

  const [card, setCard] = useState<ValuesCardType | null>(null);
  const [friendPerspectives, setFriendPerspectives] = useState<FriendPerspective[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [errorMessage, setErrorMessage] = useState<string | null>(null);
  const [user, setUser] = useState<User | null>(null);

  useEffect(() => {
    supabase.auth.getSession().then(({ data: { session } }) => {
      if (session?.user) {
        setUser(session.user);
      }
    });
  }, []);

  useEffect(() => {
    async function loadCardAndPerspectives() {
      if (!sharekey) {
        setErrorMessage('No share key provided');
        setIsLoading(false);
        return;
      }

      try {
        const { data: cardData, error: cardError } = await supabase
          .from('values_cards')
          .select('*')
          .eq('share_key', sharekey)
          .single();

        if (cardError) {
          throw cardError;
        }

        if (!cardData) {
          throw new Error('Card not found');
        }

        setCard(cardData as ValuesCardType);

        const { data: perspectivesData, error: perspectivesError } = await supabase
          .from('friend_perspectives')
          .select('*')
          .eq('card_id', cardData.id)
          .order('created_at', { ascending: false });

        if (perspectivesError) {
          console.error('Error loading perspectives:', perspectivesError);
        } else {
          setFriendPerspectives(perspectivesData as FriendPerspective[] || []);
        }
      } catch (err) {
        console.error('Error loading data:', err);
        setErrorMessage(err instanceof Error ? err.message : 'Unable to load the values card');
      } finally {
        setIsLoading(false);
      }
    }

    loadCardAndPerspectives();
  }, [sharekey]);

  if (isLoading) {
    return (
      <div className="min-h-screen bg-background py-12 flex items-center justify-center">
        <div className="animate-pulse text-gray-500">Loading...</div>
      </div>
    );
  }

  if (errorMessage || !card) {
    return (
      <div className="min-h-screen bg-background py-12">
        <div className="max-w-4xl mx-auto px-4">
          <Card className="w-full bg-white shadow-lg">
            <CardContent className="p-8 text-center">
              <h2 className="text-xl text-red-600 font-semibold">
                {errorMessage || 'Card not found'}
              </h2>
              <p className="text-gray-600 mt-2">
                This link might be invalid or have expired.
              </p>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background py-12">
      <div className="max-w-4xl mx-auto px-4">
        <div className="mb-6 flex justify-between items-center">
          {user ? (
            <div className="flex items-center gap-4">
              <span className="text-sm text-gray-600">
                Logged in as {user.email}
              </span>
              <Link href="/profile">
                <Button variant="outline" size="sm">
                  View Profile
                </Button>
              </Link>
            </div>
          ) : (
            <Link href="/">
              <Button variant="outline" size="sm">
                Go home
              </Button>
            </Link>
          )}
        </div>

        <Card className="w-full bg-white shadow-lg mb-8">
          <CardHeader className="border-b border-gray-100 bg-gray-50">
            <h1 className="text-3xl font-bold text-gray-900 text-center">{card.title}</h1>
          </CardHeader>
          <CardContent className="space-y-6 p-8">
            {card.photo_url && (
              <div
                onClick={() => {
                  window.open(card.photo_url, '_blank');
                }}
                className="w-full aspect-video relative overflow-hidden rounded-lg shadow-inner cursor-pointer"
              >
                <Image
                  src={card.photo_url}
                  alt="Meaningful moment"
                  fill
                  className="object-cover hover:opacity-90 transition-opacity"
                />
              </div>
            )}
            {card.description && (
              <div>
                <h3 className="text-xl font-bold text-gray-900">Description</h3>
                <p className="text-gray-700 text-lg leading-relaxed">{card.description}</p>
              </div>
            )}

            {card.attention_patterns?.length > 0 && (
              <div>
                <h3 className="text-xl font-bold text-gray-900">What You Pay Attention To</h3>
                <ul className="space-y-2">
                  {card.attention_patterns.map((pattern, index) => (
                    <li key={index} className="text-gray-700">
                      <span className="text-blue-500 mr-2">•</span>
                      {pattern}
                    </li>
                  ))}
                </ul>
              </div>
            )}

            {card.practices?.length > 0 && (
              <div>
                <h3 className="text-xl font-bold text-gray-900">Ways to Live This Value</h3>
                <ul className="space-y-2">
                  {card.practices.map((practice, index) => (
                    <li key={index} className="text-gray-700">
                      <span className="text-blue-500 mr-2">•</span>
                      {practice}
                    </li>
                  ))}
                </ul>
