'use client';

import { useEffect, useState } from 'react';
import { Card, CardHeader, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { supabase } from '@/lib/supabase';
import Link from 'next/link';
import type { ValuesCard as ValuesCardType, FriendPerspective } from '@/lib/types';
import { Info, Share2, Home, LogOut, PlusCircle } from 'lucide-react';

interface UnifiedPerspective extends FriendPerspective {
  source: 'direct' | 'card';
  sourceTitle: string;
  values_cards?: {
    id: string;
    title: string;
    description: string;
    user_id: string;
  };
}

export default function ProfilePage() {
  const [user, setUser] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [myValues, setMyValues] = useState<ValuesCardType[]>([]);
  const [friendPerspectives, setFriendPerspectives] = useState<UnifiedPerspective[]>([]);
  const [isGeneratingLink, setIsGeneratingLink] = useState(false);
  const [shareLink, setShareLink] = useState<string | null>(null);

  useEffect(() => {
    supabase.auth.getSession().then(({ data: { session } }) => {
      if (!session) {
        window.location.href = '/';
        return;
      }
      setUser(session.user);
      loadContent(session.user.id);
    });

    const { data: { subscription } } = supabase.auth.onAuthStateChange((_event, session) => {
      if (!session) {
        window.location.href = '/';
        return;
      }
      setUser(session.user);
      loadContent(session.user.id);
    });

    return () => subscription.unsubscribe();
  }, []);

  const loadContent = async (userId: string) => {
    try {
      const { data: valuesCards, error: valuesError } = await supabase
        .from('values_cards')
        .select('id, title, description, share_key, photo_url')
        .eq('user_id', userId)
        .order('created_at', { ascending: false });

      if (valuesError) throw valuesError;

      const validCards = valuesCards?.filter(card => card.title && card.description) || [];
      setMyValues(validCards);

      const cardIds = validCards.map(card => card.id);
      const { data: directPerspectives, error: directError } = await supabase
        .from('friend_perspectives')
        .select('*, values_cards!friend_perspectives_card_id_fkey(id, title, description, user_id)')
        .eq('user_id', userId)
        .order('created_at', { ascending: false });

      if (directError) throw directError;

      let cardPerspectives = [];
      if (cardIds.length > 0) {
        const { data: cardBasedPerspectives, error: cardError } = await supabase
          .from('friend_perspectives')
          .select('*, values_cards!friend_perspectives_card_id_fkey(id, title, description, user_id)')
          .in('card_id', cardIds)
          .order('created_at', { ascending: false });

        if (cardError) throw cardError;
        cardPerspectives = cardBasedPerspectives || [];
      }

      const allPerspectives = [...(directPerspectives || []), ...cardPerspectives];
      const processedPerspectives = allPerspectives.map(perspective => ({
        ...perspective,
        source: perspective.user_id === userId ? 'direct' : 'card',
        sourceTitle: perspective.user_id === userId
          ? 'Direct Share'
          : (perspective.values_cards?.title || 'Shared via Values Card'),
      }));

      setFriendPerspectives(processedPerspectives);
    } catch (error) {
      console.error('Error loading content:', error);
    } finally {
      setLoading(false);
    }
  };

  const createShareLink = async () => {
    setIsGeneratingLink(true);
    try {
      const shareKey = crypto.randomUUID();
      const { data, error } = await supabase
        .from('values_cards')
        .insert([
          {
            title: 'Value Reflections',
            description: 'I\'d like to hear your perspective on what you value about me.',
            user_id: user.id,
            share_key: shareKey,
            is_public: true,
          },
        ])
        .select()
        .single();

      if (error) throw error;

      const newShareLink = `${window.location.origin}/friend/${shareKey}`;
      setShareLink(newShareLink);
      await navigator.clipboard.writeText(newShareLink);
    } catch (err) {
      console.error('Error creating share link:', err);
    } finally {
      setIsGeneratingLink(false);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-background">
        <div className="animate-pulse text-gray-500 flex items-center justify-center h-screen">
          Loading...
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <header className="fixed top-0 left-0 right-0 bg-gradient-to-r from-primary-dark via-primary to-primary-light shadow-lg z-50">
        <div className="max-w-6xl mx-auto px-4 h-16 flex items-center justify-between text-white">
          <div className="flex items-center gap-4">
            <h1 className="text-xl font-bold tracking-wide">My Values Journey</h1>
          </div>
          <div className="flex items-center gap-4">
            <Link href="/about">
              <Button
                variant="ghost"
                size="sm"
                className="flex items-center gap-2 hover:text-gray-200"
              >
                <Info className="w-4 h-4" />
                About
              </Button>
            </Link>
            <Link href="/reflection">
              <Button
                variant="ghost"
                size="sm"
                className="flex items-center gap-2 hover:text-gray-200"
              >
                <PlusCircle className="w-4 h-4" />
                New Reflection
              </Button>
            </Link>
            <Link href="/">
              <Button
                variant="ghost"
                size="sm"
                className="flex items-center gap-2 hover:text-gray-200"
              >
                <Home className="w-4 h-4" />
                Home
              </Button>
            </Link>
            <Button
              onClick={async () => {
                await supabase.auth.signOut();
                window.location.href = '/';
              }}
              variant="ghost"
              size="sm"
              className="flex items-center gap-2 text-red-300 hover:text-red-500"
            >
              <LogOut className="w-4 h-4" />
              Logout
            </Button>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-6xl mx-auto px-4 pt-24 pb-12">
        {/* Quick Actions */}
        <section className="mb-12">
          <Card className="bg-gradient-to-r from-blue-50 to-indigo-50 shadow">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <h2 className="text-xl font-semibold text-gray-900 mb-2">
                    Gather More Insights
                  </h2>
                  <p className="text-gray-600 max-w-2xl">
                    Share a link with friends to collect their perspectives on your values and impact.
                  </p>
                </div>
                <Button
                  onClick={createShareLink}
                  disabled={isGeneratingLink}
                  className="bg-blue-600 hover:bg-blue-700 text-white flex items-center gap-2"
                >
                  <Share2 className="w-4 h-4" />
                  {isGeneratingLink ? 'Generating...' : 'Create Share Link'}
                </Button>
              </div>
            </CardContent>
          </Card>
        </section>

        {/* My Values Reflections */}
        <section className="mb-12">
          <h2 className="text-2xl font-bold text-gray-900 mb-6">My Values Reflections</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {myValues.map((card) => (
              <Link key={card.id} href={`/card/${card.share_key}`}>
                <Card className="bg-white hover:shadow-lg transition-shadow">
                  <CardHeader className="border-b bg-white">
                    <h3 className="text-xl font-semibold text-gray-900">{card.title}</h3>
                  </CardHeader>
                  <CardContent>
                    <p className="text-gray-600 line-clamp-3">{card.description}</p>
                  </CardContent>
                </Card>
              </Link>
            ))}
          </div>
        </section>

        {/* Friend Perspectives */}
        <section>
          <h2 className="text-2xl font-bold text-gray-900 mb-6">Friend Perspectives</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {friendPerspectives.map((perspective) => (
              <Link key={perspective.id} href={`/perspective/${perspective.id}`}>
                <Card className="bg-white hover:shadow-lg transition-shadow">
                  <CardHeader className="border-b bg-white">
                    <h3 className="text-xl font-semibold text-gray-900">
                      {perspective.title}
                    </h3>
                    <p className="text-sm text-gray-500">{perspective.sourceTitle}</p>
                  </CardHeader>
                  <CardContent>
                    <p className="text-gray-600 line-clamp-3">{perspective.description}</p>
                  </CardContent>
                </Card>
              </Link>
            ))}
          </div>
        </section>
      </main>
    </div>
  );
}
