'use client';

import { useState, useEffect } from 'react';
import { Card, CardHeader, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Textarea } from '@/components/ui/textarea';
import type { ValuesCard as ValuesCardType } from '@/lib/types';
import { supabase } from '@/lib/supabase';
import Link from 'next/link';

interface ValuesCardProps extends Partial<ValuesCardType> {
  onShare?: (shareKey: string) => void;
  onUpdate?: (updates: Partial<ValuesCardType>) => Promise<boolean>;
}

export default function ValuesCard({
  id,
  title,
  description,
  attention_patterns = [],
  practices = [],
  photo_url,
  share_key: initialShareKey,
  value_promise: initialValuePromise,
  onShare,
  onUpdate,
}: ValuesCardProps) {
  const [isSharing, setIsSharing] = useState(false);
  const [shareKey, setShareKey] = useState<string | null>(initialShareKey || null);
  const [valuePromise, setValuePromise] = useState(initialValuePromise || '');
  const [isEditingPromise, setIsEditingPromise] = useState(!initialValuePromise);
  const [isSavingPromise, setIsSavingPromise] = useState(false);
  const [isImageModalOpen, setIsImageModalOpen] = useState(false);
  const [currentUser, setCurrentUser] = useState<any>(null);

  useEffect(() => {
    // Check authentication state
    supabase.auth.getSession().then(({ data: { session } }) => {
      setCurrentUser(session?.user || null);
    });

    const { data: { subscription } } = supabase.auth.onAuthStateChange((_event, session) => {
      setCurrentUser(session?.user || null);
    });

    return () => subscription.unsubscribe();
  }, []);

  const handleShare = async () => {
    if (!id || !onUpdate || !onShare) return;

    setIsSharing(true);
    try {
      const newShareKey = crypto.randomUUID();
      const success = await onUpdate({ 
        share_key: newShareKey,
        is_public: true 
      });

      if (success) {
        setShareKey(newShareKey);
        onShare(newShareKey);
      }
    } catch (err) {
      console.error('Error sharing card:', err);
      alert('Sorry, there was an error sharing your card. Please try again.');
    } finally {
      setIsSharing(false);
    }
  };

  const saveValuePromise = async () => {
    if (!id || !onUpdate) return;

    setIsSavingPromise(true);
    try {
      const success = await onUpdate({ value_promise: valuePromise });
      if (success) {
        setIsEditingPromise(false);
      }
    } catch (err) {
      console.error('Error saving promise:', err);
      alert('Sorry, there was an error saving your promise. Please try again.');
    } finally {
      setIsSavingPromise(false);
    }
  };

  return (
    <Card className="w-full bg-white shadow-lg">
      <CardHeader className="border-b border-gray-100 bg-gray-50">
        <h2 className="text-3xl font-bold text-gray-900 text-center">{title}</h2>
      </CardHeader>
      <CardContent className="space-y-6 p-8">
        {/* Image Section */}
        {photo_url && (
          <div 
            onClick={() => setIsImageModalOpen(true)}
            className="w-full aspect-video relative overflow-hidden rounded-lg shadow-inner cursor-pointer"
          >
            <img
              src={photo_url}
              alt="Meaningful moment"
              className="object-cover w-full h-full hover:opacity-90 transition-opacity"
            />
          </div>
        )}

        <p className="text-gray-700 text-lg leading-relaxed">{description}</p>

        {/* Attention Patterns Section */}
        <div className="space-y-3">
          <h3 className="font-semibold text-gray-900 text-xl">What you pay attention to:</h3>
          <ul className="space-y-2">
            {attention_patterns.map((pattern, i) => (
              <li key={i} className="text-gray-700 flex items-start">
                <span className="text-blue-500 mr-2">•</span>
                {pattern}
              </li>
            ))}
          </ul>
        </div>

        {/* Practices Section */}
        <div className="space-y-3">
          <h3 className="font-semibold text-gray-900 text-xl">Ways to live this value:</h3>
          <ul className="space-y-2">
            {practices.map((practice, i) => (
              <li key={i} className="text-gray-700 flex items-start">
                <span className="text-blue-500 mr-2">•</span>
                {practice}
              </li>
            ))}
          </ul>
        </div>

        {/* Value Promise Section */}
        <div className="space-y-3 border-t pt-6">
          <div className="flex justify-between items-center">
            <h3 className="font-semibold text-gray-900 text-xl">Your Value Promise</h3>
            {!isEditingPromise && (
              <Button
                onClick={() => setIsEditingPromise(true)}
                variant="outline"
                size="sm"
              >
                Edit Promise
              </Button>
            )}
          </div>

          {isEditingPromise ? (
            <div className="space-y-3">
              <Textarea
                value={valuePromise}
                onChange={(e) => setValuePromise(e.target.value)}
                placeholder="How will you bring this value to life in the coming year?"
                className="min-h-[100px]"
              />
              <div className="flex gap-2">
                <Button
                  onClick={saveValuePromise}
                  disabled={isSavingPromise}
                  className="flex-1 bg-blue-600 hover:bg-blue-700 text-white"
                >
                  {isSavingPromise ? 'Saving...' : 'Save Promise'}
                </Button>
                <Button
                  onClick={() => {
                    setIsEditingPromise(false);
                    setValuePromise(initialValuePromise || '');
                  }}
                  variant="outline"
                  className="flex-1"
                  disabled={isSavingPromise}
                >
                  Cancel
                </Button>
              </div>
            </div>
          ) : (
            <div className="bg-gray-50 p-4 rounded">
              <p className="text-gray-700">
                {valuePromise || "Click 'Edit Promise' to add your commitment to living this value."}
              </p>
            </div>
          )}
        </div>

        {/* Share Section */}
        <div className="space-y-6 border-t pt-6">
          <div className="flex items-center justify-between">
            <div>
              <h3 className="font-semibold text-gray-900 text-xl mb-2">Share Your Values</h3>
              <p className="text-gray-600">
                {currentUser 
                  ? "Create a link to share your values card and gather perspectives from friends"
                  : "Share your values and collect perspectives from friends"}
              </p>
            </div>
            {!shareKey && (
              <Button
                onClick={handleShare}
                disabled={isSharing}
                className="bg-blue-600 hover:bg-blue-700 text-white"
              >
                {isSharing ? 'Creating Link...' : 'Create Share Link'}
              </Button>
            )}
          </div>

          {shareKey && (
            <div className="space-y-6">
              {/* View Link */}
              <div className="bg-gray-50 p-6 rounded-lg">
                <h4 className="font-semibold text-gray-800 mb-2">
                  View Your Values Card
                </h4>
                <p className="text-gray-600 text-sm mb-4">
                  Share this link to let others see your values reflection
                </p>
                <div className="flex items-center gap-2">
                  <a
                    href={`${window.location.origin}/card/${shareKey}`}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-gray-900 underline break-all"
                  >
                    {`${window.location.origin}/card/${shareKey}`}
                  </a>
                  <Button
                    onClick={() =>
                      navigator.clipboard.writeText(
                        `${window.location.origin}/card/${shareKey}`
                      ).then(() => alert('Link copied!'))
                    }
                    variant="outline"
                    size="sm"
                  >
                    Copy
                  </Button>
                </div>
              </div>

              {/* Friend Perspective Section */}
              <div className="bg-blue-50 p-6 rounded-lg border border-blue-100">
                <h4 className="font-semibold text-blue-900 mb-2">
                  Gather Friend Perspectives
                </h4>
                <p className="text-blue-800 text-sm mb-4">
                  Share this special link with friends to gather their unique perspectives on how your values show up in their lives.
                  {!currentUser && (
                    <span className="block mt-2 font-medium">
                      Create an account to see all perspectives people share about your values!
                    </span>
                  )}
                </p>
                <div className="flex items-center gap-2">
                  <a
                    href={`${window.location.origin}/friend/${shareKey}`}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-blue-900 underline break-all"
                  >
                    {`${window.location.origin}/friend/${shareKey}`}
                  </a>
                  <Button
                    onClick={() =>
                      navigator.clipboard.writeText(
                        `${window.location.origin}/friend/${shareKey}`
                      ).then(() => alert('Link copied!'))
                    }
                    variant="outline"
                    size="sm"
                    className="bg-white hover:bg-blue-50"
                  >
                    Copy
                  </Button>
                </div>
                {!currentUser && id && (
            <div className="mt-4 p-4 bg-white rounded-lg border border-blue-200">
              <p className="text-blue-800 font-medium mb-2">
                Want to save this reflection and see what others share about you?
              </p>
              <Link 
                href={`/login?cardId=${id}&returnUrl=${encodeURIComponent(
