import { cn } from '@/lib/utils';

interface ChatUIProps {
  onComplete?: (valuesCard: any, rawText: string) => void;
  className?: string;
  initialMessage?: string;
  systemPrompt?: string;
  cardId?: string;
}

export function ChatUI({ onComplete, className, initialMessage, systemPrompt, cardId }: ChatUIProps) {
  const [messages, setMessages] = useState(
    initialMessage ? [{ role: 'assistant', content: initialMessage }] : []
  );
  const [input, setInput] = useState('');
  const [isThinking, setIsThinking] = useState(false);
  const [isComplete, setIsComplete] = useState(false);
  const [exchangeCount, setExchangeCount] = useState(0);
  const [showRequestButton, setShowRequestButton] = useState(false);
  const [isInitialLoad, setIsInitialLoad] = useState(true);

  const processedCardRef = useRef<string | null>(null);
  const chatEndRef = useRef<HTMLDivElement | null>(null);
  const chatContainerRef = useRef<HTMLDivElement | null>(null);
  const inputRef = useRef<HTMLTextAreaElement | null>(null);

  // Focus the input textarea on initial load
  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.focus();
    }
  }, []);

  // Prevent scrolling on initial load
  useEffect(() => {
    if (chatEndRef.current && !isInitialLoad) {
      chatEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
    setIsInitialLoad(false);
  }, [messages]);

  // Display "Request Values Card" button after a certain number of messages
  useEffect(() => {
    if (messages.length >= 6 && !showRequestButton && !isComplete) {
      setShowRequestButton(true);
    }
  }, [messages.length, isComplete]);

  const handleMessage = async (text: string): Promise<string> => {
    if (isComplete) {
      console.log('Already completed, skipping processing');
      return text;
    }

    const cardMatch = text.match(/<values-card>(.*?)<\/values-card>/s);
    if (!cardMatch) {
      console.log('No <values-card> tag found in text');
      return text;
    }

    const extractedCard = cardMatch[1];

    if (processedCardRef.current === extractedCard) {
      console.log('Duplicate values-card content detected, skipping');
      return text.replace(/<values-card>.*?<\/values-card>/s, '');
    }

    try {
      const valuesCard = JSON.parse(extractedCard);
      console.log('Extracted values card:', valuesCard);

      processedCardRef.current = extractedCard;
      setIsComplete(true);

      if (onComplete) {
        console.log('Invoking onComplete with values card');
        await onComplete(valuesCard, text);
        return "I've captured your reflection and am preparing your values card...";
      }
    } catch (error) {
      console.error('Error parsing or processing values card:', error);
      return 'Error processing your reflection. Please try again.';
    }

    return text;
  };

  const handleSendMessage = async (customMessage?: string) => {
    const messageToSend = customMessage || input.trim();
    if (!messageToSend || isThinking || isComplete) return;

    const userMessage = { role: 'user', content: messageToSend };
    setMessages((prev) => [...prev, userMessage]);
    setInput('');
    setIsThinking(true);
    setExchangeCount((prev) => prev + 1);

    try {
      const response = await fetch('/api/reflection/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          messages: [...messages, userMessage],
          systemPrompt,
        }),
      });

      if (!response.ok) {
        throw new Error(`API responded with status ${response.status}`);
      }

      if (!response.body) {
        throw new Error('No response body received');
      }

      const reader = response.body.getReader();
      const decoder = new TextDecoder();
      let currentMessage = '';

      while (true) {
        const { done, value } = await reader.read();
        if (done) break;

        const chunk = decoder.decode(value, { stream: true });
        currentMessage += chunk;

        if (!isComplete) {
          const displayMessage = await handleMessage(currentMessage);
          setMessages((prev) => {
            const updated = [...prev];
            const lastMessage = updated[updated.length - 1];
            if (lastMessage?.role === 'assistant') {
              lastMessage.content = displayMessage;
            } else {
              updated.push({ role: 'assistant', content: displayMessage });
            }
            return updated;
          });
        }
      }
    } catch (err) {
      console.error('Error in chat communication:', err);
      if (!isComplete) {
        setMessages((prev) => [
          ...prev,
          {
            role: 'assistant',
            content: "I'm sorry, I encountered an error. Please try again.",
          },
        ]);
      }
    } finally {
      setIsThinking(false);
    }
  };

  const handleRequestValuesCard = () => {
    handleSendMessage('request values card');
  };

  const handleKeyDown = (e: React.KeyboardEvent<HTMLTextAreaElement>) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  return (
    <div className={cn('flex flex-col h-full relative', className)}>
      {/* Chat Messages */}
      <div
        ref={chatContainerRef}
        className={cn(
          'flex-1 overflow-y-auto space-y-4 px-4 py-2 bg-gray-50 rounded-lg',
          'mobile:h-full desktop:h-[400px] desktop:max-h-[60vh]'
        )}
      >
        <div className="space-y-4 pt-2">
          {messages.map((message, index) => (
            <div
              key={index}
              className={cn(
                'flex w-full',
                message.role === 'user' ? 'justify-end' : 'justify-start'
              )}
            >
              <div
                className={cn(
                  'rounded-lg px-4 py-2 max-w-[80%]',
                  message.role === 'user'
                    ? 'bg-blue-500 text-white'
                    : 'bg-gray-100 text-gray-900'
                )}
              >
                {message.content}
              </div>
            </div>
          ))}
          <div ref={chatEndRef} className="h-1" />
        </div>
      </div>

      {showRequestButton && !isComplete && (
        <div className="px-4 py-2 bg-white border-t">
          <Button
            onClick={handleRequestValuesCard}
            className="w-full bg-green-500 hover:bg-green-600 text-white"
          >
            Request Values Card
          </Button>
        </div>
      )}

      {/* Input Box */}
      <div className="border-t bg-white">
        <div className="px-4 py-2 flex items-center gap-2">
          <div className="relative flex-1">
            <Textarea
              ref={inputRef}
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={handleKeyDown}
              placeholder={isComplete ? 'Reflection complete...' : 'Share your thoughts...'}
              className="w-full min-h-[50px] max-h-[100px] resize-none rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
              disabled={isThinking || isComplete}
            />
            <div className="absolute bottom-2 right-2">
              <VoiceInput
                onTranscript={(transcript) => setInput((prev) => prev + ' ' + transcript)}
                isDisabled={isThinking || isComplete}
              />
            </div>
          </div>
          <Button
            type="submit"
            className="bg-blue-500 hover:bg-blue-600 text-white rounded-lg px-4 py-2"
            disabled={isThinking || !input.trim() || isComplete}
            onClick={handleSendMessage}
          >
            <Send className="w-4 h-4" />
          </Button>
        </div>
      </div>
    </div>
  );
}
