import { useEffect, useState } from 'react';
import { Card, CardHeader, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { supabase } from '@/lib/supabase';
import type { ValuesCard as ValuesCardType, FriendPerspective } from '@/lib/types';
import { use } from 'react';
import Link from 'next/link';
import { User } from '@supabase/supabase-js';
import Image from 'next/image';

type PageParams = {
  params: Promise<{ sharekey: string }>
};

export default function CardViewPage({ params }: PageParams) {
  const resolvedParams = use(params);
  const sharekey = resolvedParams.sharekey;

  const [card, setCard] = useState<ValuesCardType | null>(null);
  const [friendPerspectives, setFriendPerspectives] = useState<FriendPerspective[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [errorMessage, setErrorMessage] = useState<string | null>(null);
  const [user, setUser] = useState<User | null>(null);

  useEffect(() => {
    supabase.auth.getSession().then(({ data: { session } }) => {
      if (session?.user) {
        setUser(session.user);
      }
    });
  }, []);

  useEffect(() => {
    async function loadCardAndPerspectives() {
      if (!sharekey) {
        setErrorMessage('No share key provided');
        setIsLoading(false);
        return;
      }

      try {
        const { data: cardData, error: cardError } = await supabase
          .from('values_cards')
          .select('*')
          .eq('share_key', sharekey)
          .single();

        if (cardError) {
          throw cardError;
        }

        if (!cardData) {
          throw new Error('Card not found');
        }

        setCard(cardData as ValuesCardType);

        const { data: perspectivesData, error: perspectivesError } = await supabase
          .from('friend_perspectives')
          .select('*')
          .eq('card_id', cardData.id)
          .order('created_at', { ascending: false });

        if (perspectivesError) {
          console.error('Error loading perspectives:', perspectivesError);
        } else {
          setFriendPerspectives(perspectivesData as FriendPerspective[] || []);
        }
      } catch (err) {
        console.error('Error loading data:', err);
