    "app/perspective/[id]/page.tsx": "'use client';

import { useEffect, useState } from 'react';
import { Card, CardHeader, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { supabase } from '@/lib/supabase';
import type { FriendPerspective, ValuesCard } from '@/lib/types';
import Link from 'next/link';
import { ChevronLeft } from 'lucide-react';

type PageParams = {
  params: Promise<{ id: string }>;
};

export default function PerspectiveViewPage({ params }: PageParams) {
  const [perspectiveId, setPerspectiveId] = useState<string | null>(null);
  const [perspective, setPerspective] = useState<FriendPerspective | null>(null);
  const [originalCard, setOriginalCard] = useState<ValuesCard | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    async function resolveParams() {
      const resolvedParams = await params;
      setPerspectiveId(resolvedParams.id);
    }

    resolveParams();
  }, [params]);

  useEffect(() => {
    if (!perspectiveId) return;

    async function loadPerspective() {
      try {
        const { data: perspectiveData, error: perspectiveError } = await supabase
          .from('friend_perspectives')
          .select('*')
          .eq('id', perspectiveId)
          .single();

        if (perspectiveError) throw perspectiveError;
        if (!perspectiveData) throw new Error('Perspective not found');

        setPerspective(perspectiveData as FriendPerspective);

        const { data: cardData, error: cardError } = await supabase
          .from('values_cards')
          .select('*')
          .eq('id', perspectiveData.card_id)
          .single();

        if (cardError) throw cardError;
        setOriginalCard(cardData as ValuesCard);
      } catch (err) {
        console.error('Error loading perspective:', err);
        setError(err instanceof Error ? err.message : 'Unable to load this perspective');
      } finally {
        setLoading(false);
      }
    }

    loadPerspective();
  }, [perspectiveId]);

  if (loading) {
    return (
      <div className="min-h-screen bg-background py-12">
        <div className="max-w-4xl mx-auto px-4">
          <div className="animate-pulse space-y-4">
            <div className="h-12 bg-gray-200 rounded w-3/4 mx-auto"></div>
            <div className="h-64 bg-gray-200 rounded"></div>
          </div>
        </div>
      </div>
    );
  }

  if (error || !perspective) {
    return (
      <div className="min-h-screen bg-background py-12">
        <div className="max-w-4xl mx-auto px-4">
          <Card className="w-full bg-white shadow-lg">
            <CardContent className="p-8 text-center">
              <h2 className="text-xl text-red-600 font-semibold">
                {error || 'Perspective not found'}
              </h2>
              <p className="text-gray-600 mt-2">
                This perspective might have been removed or the link might be incorrect.
              </p>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background py-12">
      <div className="max-w-4xl mx-auto px-4">
        <Card className="w-full bg-white shadow-lg">
          <CardHeader className="border-b border-gray-100 bg-gray-50">
            <div className="flex items-center mb-4">
              {originalCard?.share_key && (
                <Link
                  href={`/card/${originalCard.share_key}`}
                  className="text-sm text-blue-600 hover:text-blue-800 flex items-center gap-1"
                >
                  <ChevronLeft className="w-4 h-4" />
                  Back to Values Card
                </Link>
              )}
            </div>
            <h1 className="text-3xl font-bold text-gray-900">{perspective.title}</h1>
          </CardHeader>
          <CardContent className="space-y-6 p-8">
            <div className="bg-blue-50 p-4 rounded-lg">
              <p className="text-blue-900 text-lg">
                A reflection on how they express their values through their actions and way of being.
              </p>
            </div>

            {perspective.story && (
              <div className="space-y-3">
                <h3 className="font-semibold text-gray-900 text-xl">A Meaningful Moment</h3>
                <div className="bg-gray-50 p-4 rounded-lg">
                  <p className="text-gray-700 text-lg leading-relaxed">{perspective.story}</p>
                </div>
              </div>
            )}

            <div className="space-y-3">
              <h3 className="font-semibold text-gray-900 text-xl">Reflection</h3>
              <p className="text-gray-700 text-lg leading-relaxed">{perspective.description}</p>
            </div>

            {perspective.attention_patterns && perspective.attention_patterns.length > 0 && (
              <div className="space-y-3">
                <h3 className="font-semibold text-gray-900 text-xl">What Others Notice</h3>
                <ul className="space-y-2">
                  {perspective.attention_patterns.map((pattern, i) => (
                    <li key={i} className="text-gray-700 flex items-start">
                      <span className="text-blue-500 mr-2">•</span>
                      {pattern}
                    </li>
                  ))}
                </ul>
              </div>
            )}

            {perspective.practices && perspective.practices.length > 0 && (
              <div className="space-y-3">
                <h3 className="font-semibold text-gray-900 text-xl">Their Impact on Others</h3>
                <ul className="space-y-2">
                  {perspective.practices.map((practice, i) => (
                    <li key={i} className="text-gray-700 flex items-start">
                      <span className="text-blue-500 mr-2">•</span>
                      {practice}
                    </li>
                  ))}
                </ul>
              </div>
            )}

            <div className="border-t pt-6 mt-8">
              {originalCard?.share_key && (
                <Link href={`/card/${originalCard.share_key}`}>
                  <Button className="w-full bg-blue-500 hover:bg-blue-600 text-white">
                    Return to Values Card
                  </Button>
                </Link>
              )}
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}
