    "components/voice/VoiceInput.tsx": "// components/voice/VoiceInput.tsx
'use client';

import { useState, useRef, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Mic, X, Loader2 } from 'lucide-react';

interface VoiceInputProps {
  onTranscript: (transcript: string) => void;
  isDisabled?: boolean;
  className?: string;
}

export function VoiceInput({ onTranscript, isDisabled = false, className }: VoiceInputProps) {
  const [isRecording, setIsRecording] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const mediaRecorderRef = useRef<MediaRecorder | null>(null);
  const chunksRef = useRef<Blob[]>([]);
  const streamRef = useRef<MediaStream | null>(null);
  const [recordingTime, setRecordingTime] = useState(0);
  const timerRef = useRef<NodeJS.Timer | null>(null);

  const cleanup = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
    }
    if (mediaRecorderRef.current) {
      if (mediaRecorderRef.current.state !== 'inactive') {
        mediaRecorderRef.current.stop();
      }
      mediaRecorderRef.current = null;
    }
    if (timerRef.current) {
      clearInterval(timerRef.current);
      timerRef.current = null;
    }
    chunksRef.current = [];
    setRecordingTime(0);
  };

  useEffect(() => {
    return cleanup;
  }, []);

  const getMimeType = () => {
    const types = [
      'audio/webm',
      'audio/webm;codecs=opus',
      'audio/ogg;codecs=opus',
      'audio/mp4'
    ];

    for (const type of types) {
      if (MediaRecorder.isTypeSupported(type)) {
        return type;
      }
    }
    return 'audio/webm'; // Fallback
  };

  const startRecording = async () => {
    try {
      cleanup();
      setError(null);

      const stream = await navigator.mediaDevices.getUserMedia({ 
        audio: {
          echoCancellation: true,
          noiseSuppression: true,
          autoGainControl: true
        } 
      });

      streamRef.current = stream;
      const mimeType = getMimeType();
      console.log('Using MIME type:', mimeType);

      const mediaRecorder = new MediaRecorder(stream, {
        mimeType
      });

      mediaRecorderRef.current = mediaRecorder;
      chunksRef.current = [];

      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          chunksRef.current.push(event.data);
        }
      };

      mediaRecorder.onstop = async () => {
        if (chunksRef.current.length === 0) {
          setError('No audio recorded');
          cleanup();
          return;
        }

        const audioBlob = new Blob(chunksRef.current, { 
          type: mimeType 
        });

        await transcribeAudio(audioBlob);
        cleanup();
      };

      // Collect data in smaller chunks for better handling
      mediaRecorder.start(500);
      setIsRecording(true);

      timerRef.current = setInterval(() => {
        setRecordingTime(prev => prev + 1);
      }, 1000);

    } catch (err) {
      console.error('Error starting recording:', err);
      setError('Could not access microphone');
      cleanup();
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state !== 'inactive') {
      mediaRecorderRef.current.stop();
      setIsRecording(false);
    }
  };

  const transcribeAudio = async (audioBlob: Blob) => {
    if (audioBlob.size === 0) {
      setError('No audio recorded');
      return;
    }

    setIsProcessing(true);
    setError(null);

    try {
      const formData = new FormData();
      formData.append('file', audioBlob);
      formData.append('mimeType', audioBlob.type); // Send MIME type to server

      const response = await fetch('/api/transcribe', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Transcription failed');
      }

      if (!data.text) {
        throw new Error('No transcription received');
      }

      onTranscript(data.text.trim());
    } catch (err) {
      console.error('Transcription error:', err);
      setError(err instanceof Error ? err.message : 'Failed to transcribe audio');
    } finally {
      setIsProcessing(false);
    }
  };

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div className={`relative inline-flex ${className}`}>
      <Button
        onClick={() => isRecording ? stopRecording() : startRecording()}
        disabled={isDisabled || isProcessing}
        className={`
          transition-all duration-300 h-12
          ${isRecording 
            ? 'bg-blue-500 hover:bg-blue-600 text-white px-6 min-w-[200px]' 
            : 'bg-red-500 hover:bg-red-600 text-white w-12'}
          rounded-full shadow-md flex items-center justify-center gap-2
        `}
        title={isRecording ? 'Click to stop recording' : 'Click to start recording'}
      >
        {isProcessing ? (
          <>
            <Loader2 className="h-5 w-5 animate-spin" />
            <span className="text-white">Transcribing...</span>
          </>
        ) : isRecording ? (
          <>
            <span className="font-mono text-white">{formatTime(recordingTime)}</span>
            <X className="h-5 w-5 text-white" />
            <span className="text-white">Click to finish</span>
          </>
        ) : (
          <Mic className="h-6 w-6 text-white" />
        )}
      </Button>

      {error && (
        <div className="absolute left-0 -bottom-8 whitespace-nowrap">
          <span className="text-sm text-red-500 bg-white px-3 py-1 rounded-full shadow-sm border border-red-100">
            {error}
          </span>
        </div>
      )}
    </div>
  );
}"
