import React from 'react';
import { Card, CardContent, CardHeader } from "@/components/ui/card";

const DecisionTree = () => {
  const steps = [
    {
      title: "Validation Phase",
      options: [
        "Interview potential users about their learning journeys",
        "Test SCRIBER framework with small user group",
        "Prototype vector space navigation UI"
      ],
      questions: [
        "Who are our early adopters?",
        "What is the minimum feature set?",
        "How do we measure success?"
      ]
    },
    {
      title: "Technical Prototype",
      options: [
        "Build browser extension MVP",
        "Develop knowledge graph visualization",
        "Implement basic pattern recognition"
      ],
      questions: [
        "Which LLM API to use?",
        "How to handle user data privacy?",
        "What metrics track learning progress?"
      ]
    },
    {
      title: "Business Model",
      options: [
        "Individual subscription model",
        "Enterprise licensing",
        "Educational institution partnerships"
      ],
      questions: [
        "What's the pricing strategy?",
        "How to demonstrate ROI?",
        "What are the scaling costs?"
      ]
    }
  ];

  return (
    <div className="w-full space-y-4 p-4">
      {steps.map((step, i) => (
        <Card key={i} className="w-full">
