import datetime
import json
import os
from typing import Dict, List
import anthropic
import time

class AdaptiveCheckinSystem:
    def __init__(self):
        self.client = anthropic.Client(os.getenv('ANTHROPIC_API_KEY'))
        self.data_file = 'checkin_data.json'
        self.load_data()

    def load_data(self):
        if os.path.exists(self.data_file):
            with open(self.data_file, 'r') as f:
                self.data = json.load(f)
        else:
            self.data = {
                'checkins': [],
                'active_intentions': [],
                'suggested_next_checkin': None,
                'patterns': {}
            }

    def save_data(self):
        with open(self.data_file, 'w') as f:
            json.dump(self.data, f, indent=2)

    def get_claude_response(self, message: str) -> str:
        response = self.client.messages.create(
            model="claude-3-sonnet-20240229",
            max_tokens=1000,
            messages=[{
                "role": "user",
                "content": message
            }]
        )
        return response.content[0].text

    def checkin(self):
        """Main check-in function that adapts based on previous patterns"""
        print("\n=== Adaptive Check-in ===")

        # Get current state
        print("\nQuick state check:")
        energy = input("Energy (1-5): ")
        current_focus = input("What's on your mind right now? ")

        # Check active intentions
        if self.data['active_intentions']:
            print("\nActive intentions:")
            for intention in self.data['active_intentions']:
                print(f"- {intention['what']}")
                progress = input(f"Progress update on {intention['what']}? (Enter to skip): ")
                if progress:
                    intention['updates'].append({
                        'timestamp': datetime.datetime.now().isoformat(),
                        'update': progress
                    })

        # New intentions
        new_intention = input("\nAny new intention to track? (Enter to skip): ")
        if new_intention:
            self.data['active_intentions'].append({
                'what': new_intention,
                'created': datetime.datetime.now().isoformat(),
                'updates': []
            })

        # Capture check-in
        checkin_data = {
            'timestamp': datetime.datetime.now().isoformat(),
            'energy': energy,
            'focus': current_focus,
            'brave_action': input("Did you do something brave? "),
            'next_brave': input("Next brave thing needed? "),
            'beautiful_moment': input("Any beautiful moment to capture? ")
        }
        self.data['checkins'].append(checkin_data)

        # Get Claude's analysis
        analysis_prompt = f"""
        Based on this check-in and previous patterns, analyze:
        1. When should the next check-in be?
        2. What patterns do you notice?
        3. What needs attention?

        Current check-in: {checkin_data}
        Active intentions: {self.data['active_intentions']}
        Recent checkins: {self.data['checkins'][-3:] if len(self.data['checkins']) > 3 else self.data['checkins']}
        """

        analysis = self.get_claude_response(analysis_prompt)
        print("\nAnalysis:", analysis)

        # Save everything
        self.save_data()

    def view_patterns(self):
        """View historical patterns and insights"""
        analysis_prompt = f"""
        Analyze these check-ins for patterns:
        {self.data['checkins']}

        Focus on:
        1. Energy patterns
        2. Completion patterns
        3. Connection points
        4. Growth edges
        """

        analysis = self.get_claude_response(analysis_prompt)
        print("\nPattern Analysis:", analysis)

    def run(self):
        """Main interaction loop"""
        while True:
            print("\n1. Check-in")
            print("2. View patterns")
            print("3. Exit")
            choice = input("Choose an option: ")

            if choice == '1':
                self.checkin()
            elif choice == '2':
                self.view_patterns()
            elif choice == '3':
                break

if __name__ == "__main__":
    system = AdaptiveCheckinSystem()
    system.run()

