#!/usr/bin/env python3

import json
import os
import datetime
from pathlib import Path
import anthropic

class ChatCheckin:
    def __init__(self):
        self.config = self.load_json('config.json')
        self.state = self.load_json('state.json')
        self.alive_list = self.load_json('alive_list.json')
        self.checkins = self.load_json('checkins.json')
        self.client = anthropic.Client(os.getenv('ANTHROPIC_API_KEY'))
        self.current_session = {
            'timestamp': datetime.datetime.now().isoformat(),
            'raw_chat': [],
            'patterns_noticed': [],
            'state_updates': {}
        }

    def load_json(self, filename):
        path = Path(filename)
        return json.load(open(path)) if path.exists() else {}

    def save_json(self, data, filename):
        with open(filename, 'w') as f:
            json.dump(data, f, indent=2)

    def chat_checkin(self):
        """Natural chat interface with invisible structure"""
        print("\nShare what's on your mind (type 'done' when finished):")

        while True:
            line = input("> ")
            if line.lower() == 'done':
                break
            self.current_session['raw_chat'].append(line)

            # Real-time pattern detection
            self.detect_patterns_in_line(line)

        # Process complete session
        self.process_session()

        # Optional review
        if self.should_show_interpretation():
            self.review_interpretation()

        # Save updates
        self.save_all_updates()

    def detect_patterns_in_line(self, line):
        """Real-time pattern detection during chat"""
        prompt = self.create_pattern_detection_prompt(line)
        response = self.get_claude_response(prompt)

        patterns = self.parse_pattern_response(response)
        if patterns:
            self.current_session['patterns_noticed'].extend(patterns)

    def create_pattern_detection_prompt(self, line):
        """Create prompt for real-time pattern detection"""
        return f"""Given this line from the user's check-in:
"{line}"

And these active patterns and states:
{json.dumps(self.get_relevant_state(), indent=2)}

Notice any:
1. Pattern manifestations
2. State changes
3. Protection mechanisms
4. Growth opportunities

Consider response guidance:
{json.dumps(self.config['response_guidance'], indent=2)}

Return only relevant observations in JSON format."""

    def process_session(self):
        """Process complete chat session"""
        full_analysis_prompt = self.create_full_analysis_prompt()
        analysis = self.get_claude_response(full_analysis_prompt)

        structured_data = self.parse_analysis(analysis)
        self.update_state(structured_data)
        self.update_alive_list(structured_data)

        # Generate reflection questions if energy permits
        if self.should_offer_reflection(structured_data):
            self.offer_reflection(structured_data)

    def create_full_analysis_prompt(self):
        """Create comprehensive analysis prompt"""
        return f"""Based on this check-in conversation:
{self.format_chat_for_prompt()}

And current state/patterns:
{json.dumps(self.get_relevant_state(), indent=2)}

Following these response principles:
Prioritize:
{json.dumps(self.config['response_guidance']['prioritize'], indent=2)}

Avoid:
{json.dumps(self.config['response_guidance']['avoid'], indent=2)}

Provide:
1. State assessment
2. Pattern updates
3. Integration movements
4. Alive list changes
5. Growth edges

Return structured JSON."""

    def update_state(self, analysis):
        """Update state based on session analysis"""
        # Update pattern manifestations
        for pattern_name, pattern_data in analysis.get('patterns', {}).items():
            if pattern_name in self.state['current_patterns']:
                self.state['current_patterns'][pattern_name].update({
                    'current_manifestation': pattern_data.get('manifestation'),
                    'recent_evidence': self.update_evidence_list(
                        self.state['current_patterns'][pattern_name]['recent_evidence'],
                        pattern_data.get('evidence', [])
                    )
                })

        # Update integration movements
        if 'integration_movements' in analysis:
            self.state['integration_movements']['current_focus'].update({
                'progress': analysis['integration_movements'].get('progress', 0),
                'recent_evidence': self.update_evidence_list(
                    self.state['integration_movements']['current_focus']['recent_evidence'],
                    analysis['integration_movements'].get('evidence', [])
                )
            })

        # Update readiness indicators
        self.state['readiness_indicators']['current_state'].update(
            analysis.get('readiness', {})
        )

    def update_alive_list(self, analysis):
        """Update alive list based on session analysis"""
        # Update existing items
        for item_type in ['connections', 'projects', 'growth_experiments']:
            for item in analysis.get('alive_items', {}).get(item_type, []):
                self.update_or_add_alive_item(item_type, item)

        # Update avoidance patterns
        if 'avoidance_patterns' in analysis:
            for pattern, data in analysis['avoidance_patterns'].items():
                if pattern in self.alive_list['avoidance_patterns']:
                    self.alive_list['avoidance_patterns'][pattern].update(data)
                else:
                    self.alive_list['avoidance_patterns'][pattern] = data

    def should_offer_reflection(self, analysis):
        """Determine if reflection questions would be helpful"""
        energy_level = analysis.get('readiness', {}).get('energy_level', 3)
        protection_active = len(analysis.get('patterns', {}).get('protection_mechanisms', [])) > 0
        return energy_level >= 3 and not protection_active

    def offer_reflection(self, analysis):
        """Offer reflection questions based on analysis"""
        questions = self.generate_reflection_questions(analysis)
        print("\nWould you like to reflect on any of these? (y/n)")
        if input().lower().startswith('y'):
            self.capture_reflections(questions)

    def generate_reflection_questions(self, analysis):
        """Generate contextual reflection questions"""
        prompt = f"""Based on this analysis:
{json.dumps(analysis, indent=2)}

And these response guidelines:
{json.dumps(self.config['response_guidance'], indent=2)}

Generate 2-3 reflection questions that:
1. Honor current energy state
2. Support integration movements
3. Acknowledge growth edges
4. Feel naturally relevant

Return questions in JSON format."""

        response = self.get_claude_response(prompt)
        return json.loads(response)

    def save_all_updates(self):
        """Save all updated files"""
        self.save_json(self.state, 'state.json')
        self.save_json(self.alive_list, 'alive_list.json')
        self.save_json(self.checkins, 'checkins.json')

    def get_claude_response(self, prompt):
        """Get response from Claude API"""
        response = self.client.messages.create(
