def generate_reflection_questions(self, analysis):
    """Generate deeply contextual reflection questions"""

    # Get current state context
    state_context = {
        'energy_center': analysis.get('readiness', {}).get('energy_center', 'unknown'),
        'protection_active': any(
            p in analysis.get('patterns', {}).get('active_patterns', [])
            for p in self.config['system_prompt']['core_understanding']['hidden_patterns'].keys()
        ),
        'growth_edge_present': any(
            edge in analysis.get('growth_edges', [])
            for edge in self.config['system_prompt']['core_understanding']['intervention_principles']['growth_edges']
        )
    }

    # Determine reflection approach
    if state_context['energy_center'] == 'heart' and not state_context['protection_active']:
        reflection_mode = 'when_grounded'
    elif state_context['protection_active']:
        reflection_mode = 'when_protected'
    elif state_context['growth_edge_present']:
        reflection_mode = 'when_growth_edge'
    else:
        reflection_mode = 'when_grounded'

    guidance = self.config['system_prompt']['reflection_guidance'][reflection_mode]

    prompt = f"""Based on this check-in analysis:
{json.dumps(analysis, indent=2)}

