    def __init__(self):
        self.config = self.load_json('config.json')
        self.data = {
            'checkins': self.load_json('checkins.json'),
            'patterns': self.load_json('patterns.json'),
            'context': self.load_json('context.json')
        }
        self.client = anthropic.Client(os.getenv('ANTHROPIC_API_KEY'))
        self.current_checkin = {}

    def load_json(self, filename):
        """Load JSON file or return empty structure if doesn't exist"""
        path = Path(filename)
        if path.exists():
            with open(path) as f:
                return json.load(f)
        return {}

    def save_json(self, data, filename):
        """Save data to JSON file"""
        with open(filename, 'w') as f:
            json.dump(data, f, indent=2)

    def chat_checkin(self):
        """Main check-in flow"""
        print("\nShare what's on your mind (type 'done' when finished):")

        # Collect natural chat input
        chat_lines = []
        while True:
            line = input("> ")
            if line.lower() == 'done':
                break
            chat_lines.append(line)

        chat_text = "\n".join(chat_lines)

        # Extract structured data from chat
        structure = self.analyze_chat(chat_text)

        # Show interpretation (optional)
        if self.confirm("Would you like to see how I interpreted that?"):
            self.show_interpretation(structure)
            if not self.confirm("Does this interpretation feel accurate?"):
                structure = self.adjust_interpretation(structure)

        # Process patterns and update files
        self.process_checkin(structure)

    def analyze_chat(self, chat_text):
        """Use Claude to extract structured data from natural chat"""
        prompt = f"""Based on this check-in conversation, extract structured data according to our schema.
        Look for:
        - Energy state and physical indicators
        - Growth edges and progress
        - People on your mind
        - Things wanting to happen
        - Active patterns manifesting

        Current context and patterns: {json.dumps(self.get_relevant_context(), indent=2)}

        Chat text:
        {chat_text}

        Return only the JSON structure."""

        response = self.client.messages.create(
            model=self.config['model_name'],
            max_tokens=1000,
            messages=[{
                "role": "user",
                "content": prompt
            }]
        )

        try:
            return json.loads(response.content[0].text)
        except:
            return self.get_default_structure()

    def get_relevant_context(self):
        """Get current active patterns and context"""
        return {
            'active_patterns': [p for p in self.data['patterns'].get('active_patterns', [])
                              if p.get('active', True)],
            'growth_edges': self.data['patterns'].get('growth_edges', []),
            'alive_list': self.data['patterns'].get('alive_list', [])
        }

    def show_interpretation(self, structure):
