def find_protection_patterns(self, structure):
    """Find active protection patterns in the check-in"""
    patterns = []
    known_patterns = {
        'visibility_paradox': ['automation', 'system', 'tool', 'passive'],
        'action_reflection_loop': ['analyze', 'understand', 'perfect', 'plan'],
        'value_giving_cycle': ['help', 'create', 'teach', 'give'],
        'financial_safety_dance': ['money', 'invest', 'finance', 'cost']
    }

    try:
        # Extract text for analysis
        text = ' '.join([
            str(structure.get('raw_chat', '')),
            str(structure.get('notes', '')),
            str(structure.get('ai_analysis', ''))
        ]).lower()

        # Look for pattern indicators
        for pattern, indicators in known_patterns.items():
            if any(indicator in text for indicator in indicators):
                context = self.extract_pattern_context(text, pattern)
                patterns.append({
                    'pattern': pattern,
                    'context': context,
                    'confidence': self.calculate_pattern_confidence(text, indicators)
                })

        return patterns
    except Exception as e:
        print(f"Error in pattern detection: {e}")
        return []

def find_energy_patterns(self, structure):
    """Find energy patterns in the check-in"""
    try:
        energy_data = {
            'level': structure.get('energy_state', {}).get('level'),
            'description': structure.get('energy_state', {}).get('description'),
            'physical_signs': structure.get('physical_signs', []),
            'context': structure.get('context', '')
        }

        patterns = []

        # Check for energy shifts
        if self.previous_energy_level is not None:
            if energy_data['level'] > self.previous_energy_level:
                patterns.append({
                    'type': 'energy_increase',
                    'context': energy_data['description'],
                    'physical_signs': energy_data['physical_signs']
                })
            elif energy_data['level'] < self.previous_energy_level:
                patterns.append({
                    'type': 'energy_decrease',
                    'context': energy_data['description'],
                    'physical_signs': energy_data['physical_signs']
                })

        # Look for readiness indicators
        for indicator in self.config['system_prompt']['readiness_indicators']['ready_for_action']:
            if any(indicator.lower() in str(sign).lower() for sign in energy_data['physical_signs']):
                patterns.append({
                    'type': 'readiness',
                    'indicator': indicator,
                    'context': energy_data['description']
                })

        return patterns
    except Exception as e:
        print(f"Error in energy pattern detection: {e}")
        return []

def extract_movement_context(self, movement, ai_analysis):
    """Extract context for movement progress"""
    try:
        # Look for mentions of the movement's from/to states
        movement_text = f"from {movement['from']} to {movement['to']}"
        sentences = re.split(r'[.!?]+', ai_analysis)
        relevant_sentences = [
            s for s in sentences 
            if movement['from'].lower() in s.lower() 
            or movement['to'].lower() in s.lower()
        ]

        if relevant_sentences:
            return ' '.join(relevant_sentences).strip()
        return "Context not found"
    except Exception as e:
        print(f"Error extracting movement context: {e}")
        return "Error extracting context"

def calculate_movement_progress(self, movement):
    """Calculate progress score for a movement"""
    try:
        recent_manifestations = movement.get('recent_manifestations', [])
        if not recent_manifestations:
            return movement.get('progress', 0)

        # Weight recent progress more heavily
        weighted_sum = 0
        weights = [0.5, 0.3, 0.2]  # Most recent counts more
        for i, manifestation in enumerate(recent_manifestations[-3:]):
            progress_indicators = [
                word in manifestation.get('insight', '').lower()
                for word in ['success', 'progress', 'improvement', 'breakthrough']
            ]
            weighted_sum += weights[i] * (sum(progress_indicators) / len(progress_indicators))

        # Combine with existing progress, ensuring we don't decrease too much
        current_progress = movement.get('progress', 0)
        new_progress = min(1.0, max(current_progress, current_progress * 0.8 + weighted_sum * 0.2))
        return round(new_progress, 2)
    except Exception as e:
        print(f"Error calculating movement progress: {e}")
        return movement.get('progress', 0)

def extract_protection_mechanisms(self, ai_analysis):
    """Extract active protection mechanisms"""
    try:
        known_mechanisms = {
            'analysis_paralysis': ['overthinking', 'analysis', 'perfect', 'understand'],
            'avoidance': ['avoid', 'delay', 'later', 'should'],
            'system_building': ['automate', 'system', 'tool', 'framework'],
            'withdrawal': ['withdraw', 'alone', 'isolate', 'distance']
        }

        mechanisms = []
        for name, indicators in known_mechanisms.items():
            if any(indicator in ai_analysis.lower() for indicator in indicators):
                # Find relevant context
                sentences = re.split(r'[.!?]+', ai_analysis)
                context = [s for s in sentences if any(i in s.lower() for i in indicators)]

                mechanisms.append({
                    'name': name,
                    'manifestation': context[0] if context else 'Context not found',
                    'confidence': sum(i in ai_analysis.lower() for i in indicators) / len(indicators)
                })

        return mechanisms
    except Exception as e:
        print(f"Error extracting protection mechanisms: {e}")
        return []

def extract_breakthrough_opportunities(self, ai_analysis):
    """Extract breakthrough opportunities"""
    try:
        # Look for forward-looking statements
        opportunities = []
        sentences = re.split(r'[.!?]+', ai_analysis)

        opportunity_indicators = [
            'could', 'might', 'opportunity', 'potential', 'next step',
            'consider', 'try', 'possible', 'suggestion'
        ]

        for sentence in sentences:
            if any(indicator in sentence.lower() for indicator in opportunity_indicators):
                related_patterns = []
                for pattern in self.config['system_prompt']['core_understanding']['hidden_patterns']:
                    if pattern.lower() in sentence.lower():
                        related_patterns.append(pattern)

                opportunities.append({
                    'opportunity': sentence.strip(),
                    'related_patterns': related_patterns,
                    'confidence': len(related_patterns) / len(self.config['system_prompt']['core_understanding']['hidden_patterns'])
                })

        return sorted(opportunities, key=lambda x: x['confidence'], reverse=True)
    except Exception as e:
        print(f"Error extracting breakthrough opportunities: {e}")
        return []

def shows_edge_progress(self, edge, ai_analysis):
    """Check if check-in shows progress on a growth edge"""
    try:
        # Look for progress indicators specific to this edge
        edge_terms = edge['edge'].lower().split()
        progress_terms = ['progress', 'improvement', 'better', 'growth', 'step']

        # Count co-occurrence of edge terms and progress terms
        sentences = re.split(r'[.!?]+', ai_analysis)
        for sentence in sentences:
            if any(term in sentence.lower() for term in edge_terms) and \
               any(term in sentence.lower() for term in progress_terms):
                return True
        return False
    except Exception as e:
        print(f"Error checking edge progress: {e}")
        return False

def extract_edge_progress(self, edge, ai_analysis):
    """Extract progress details for a growth edge"""
    try:
        edge_name = edge['edge'].lower()
        sentences = re.split(r'[.!?]+', ai_analysis)

        # Find relevant sentences
        relevant = [s for s in sentences if edge_name in s.lower() and \
                   any(term in s.lower() for term in ['progress', 'improvement', 'step'])]

        if relevant:
            return relevant[0].strip()
        return ""
    except Exception as e:
        print(f"Error extracting edge progress: {e}")
        return ""

def shows_energy_pattern(self, ai_analysis):
    """Check if analysis shows an energy pattern"""
    try:
        energy_terms = ['energy', 'tired', 'energized', 'flow', 'resistance', 'grounded']
        return any(term in ai_analysis.lower() for term in energy_terms)
    except Exception as e:
        print(f"Error checking energy pattern: {e}")
        return False

def extract_energy_pattern(self, ai_analysis):
    """Extract energy pattern details"""
    try:
        sentences = re.split(r'[.!?]+', ai_analysis)
        energy_sentences = [s for s in sentences if 'energy' in s.lower()]

        if energy_sentences:
            return energy_sentences[0].strip()
        return ""
    except Exception as e:
        print(f"Error extracting energy pattern: {e}")
        return ""

