#!/usr/bin/env python3

import json
import os
import datetime
from pathlib import Path
import anthropic

class ChatCheckin:
    def __init__(self):
        # Load config first since we need API key
        self.config = self.load_json('config.json') or {
            "claude_api_key": "",
            "model_name": "claude-3-5-sonnet-20241022"
        }

        # Initialize data structures with empty defaults if files don't exist
        self.data = {
            'checkins': self.load_json('checkins.json') or {
                "checkins": [],
                "pattern_evolution": {},
                "current_state": {
                    "last_updated": None,
                    "energy_trends": [],
                    "active_protection_mechanisms": [],
                    "breakthrough_opportunities": []
                }
            },
            'patterns': self.load_json('patterns.json') or {},
            'context': self.load_json('context.json') or {}
        }

        # Initialize Anthropic client with key from config
        try:
            self.client = anthropic.Client(api_key=self.config.get('claude_api_key'))
        except Exception as e:
            print(f"Error initializing Anthropic client: {e}")
            print("Please ensure your API key is set in config.json")
            exit(1)

        self.current_session = {
            'timestamp': datetime.datetime.now().isoformat(),
            'raw_chat': [],
            'patterns_noticed': [],
            'state_updates': {}
        }

        self.previous_energy_level = None


    def ensure_config_exists(self):
        """Create initial config file if it doesn't exist"""
        if not Path('config.json').exists():
            initial_config = {
                "claude_api_key": "your-key-here",  # Replace with actual key
                "model_name": "claude-3-5-sonnet-20241022",
                "system_prompt": {
                    # ... [rest of your system prompt configuration]
                }
            }
            self.save_json(initial_config, 'config.json')
            print("Created initial config.json - please add your API key")
            exit(1)

    def load_json(self, filename):
        """Load JSON file or return empty structure if it doesn't exist."""
        path = Path(filename)
        if path.exists():
            try:
                with open(path) as f:
                    data = json.load(f)
                    # Validate structure
                    if filename == 'patterns.json' and not isinstance(data.get('active_patterns', []), list):
                        print(f"Invalid structure in {filename}. Resetting to default.")
                        return {"active_patterns": [], "growth_edges": [], "alive_list": []}
                    return data
            except json.JSONDecodeError:
                print(f"Error reading {filename}. Resetting to default state.")
                self.reset_data_files()
        return {}

    def save_json(self, data, filename):
        """Save data to JSON file"""
        with open(filename, 'w') as f:
            json.dump(data, f, indent=2)

    def confirm(self, question):
        """Helper to get yes/no confirmation"""
        return input(f"\n{question} (y/n) ").lower().startswith('y')

    def chat_checkin(self):
        """Main check-in flow"""
        print("\nShare what's on your mind (type 'done' when finished):")

        # Collect natural chat input
        chat_lines = []
        while True:
            line = input("> ")
            if line.lower() == 'done':
                break
            elif line.lower() == "review":
                priorities = self.review_checkins()
                print("\n=== Prioritized Suggestions ===")
                for idx, priority in enumerate(priorities, start=1):
                    print(f"{idx}. {priority}")

            chat_lines.append(line)

        chat_text = "\n".join(chat_lines)

        # Extract structured data from chat
        structure = self.analyze_chat(chat_text)

        # Show interpretation (optional)
        if self.confirm("Would you like to see how I interpreted that?"):
            self.show_interpretation(structure)
            if not self.confirm("Does this interpretation feel accurate?"):
                structure = self.adjust_interpretation(structure)

        # Process patterns and update files
        self.process_checkin(structure)

    def analyze_chat(self, chat_text):
        """Use Claude to extract structured data from natural chat"""
        prompt = f"""Based on this check-in conversation, extract structured data according to our schema.
        Look for:
        - Energy state and physical indicators
        - Growth edges and progress
        - People on your mind
        - Things wanting to happen
        - Active patterns manifesting

        Current context and patterns: {json.dumps(self.get_relevant_context(), indent=2)}

        Chat text:
        {chat_text}

        Return only the JSON structure."""

        response = self.client.messages.create(
            model=self.config['model_name'],
            max_tokens=1000,
            messages=[{
                "role": "user",
                "content": prompt
            }]
        )

        try:
            return json.loads(response.content[0].text)
        except:
            return self.get_default_structure()

    def get_relevant_context(self):
        """Get current active patterns and context"""
        active_patterns = self.data['patterns'].get('active_patterns', [])
        print(f"Active patterns: {active_patterns}")  # Debugging line
        return {
            'active_patterns': [
                p for p in active_patterns 
                if isinstance(p, dict) and p.get('active', True)
            ],
            'growth_edges': self.data['patterns'].get('growth_edges', []),
            'alive_list': self.data['patterns'].get('alive_list', [])
        }

    def find_growth_patterns(self, structure):
        """Find growth patterns in the check-in"""
        patterns = []
        try:
            if 'growth_edges' in structure:
                for edge in structure['growth_edges']:
                    patterns.append({
                        'edge': edge,
                        'context': structure.get('context', 'No context provided')
                    })
        except Exception as e:
            print(f"Error in growth pattern detection: {e}")
        return patterns
    def review_checkins(self):
        """Review recent check-ins and suggest priorities using GPT"""
        # Get the last 5 check-ins
        recent_checkins = self.data['checkins']['checkins'][-5:]  # Adjust number as needed
        context = self.get_relevant_context()

        # Create a summary of recent check-ins
        checkin_summaries = [
            {
                "timestamp": checkin.get('timestamp', 'Unknown'),
                "energy": checkin.get('energy', 'Unknown'),
                "brave_action": checkin.get('brave_action', 'None'),
                "notes": checkin.get('notes', '')
            }
            for checkin in recent_checkins
        ]

        # Compose the GPT prompt
        prompt = f"""You are an assistant helping prioritize tasks based on recent check-ins. 
        Here are the last 5 check-ins:
        {json.dumps(checkin_summaries, indent=2)}

        Current context:
        {json.dumps(context, indent=2)}

        Include blockers, unseen elements, and active patterns to suggest 3-5 key priorities.
        Each suggestion should include:
        - Why it's important
        - Specific, actionable next steps
        - How it aligns with the patterns or context
        """

        try:
            # Send the prompt to GPT
            response = self.client.messages.create(
                model=self.config['model_name'],
                max_tokens=1000,
                messages=[{
                    "role": "user",
                    "content": prompt
                }]
            )

            # Parse GPT response
            priorities = response.content[0].text.strip().split("\n")
            return priorities
        except Exception as e:
            print(f"Error in GPT review: {e}")
            return ["Could not generate priorities due to an error."]

        # General patterns
        for pattern in patterns.get('current_patterns', {}).values():
            if pattern.get('active') and pattern.get('current_need'):
                priorities.append(f"Address pattern: {pattern['current_need']}")

        return priorities

    def show_interpretation(self, structure):
        """Display AI's interpretation of the chat"""
        print("\n=== My Understanding ===")
        energy = structure.get('energy_state', {})
        print(f"Energy: {energy.get('level', 'Unknown')} - {energy.get('description', 'No description')}")

        if structure.get('people_on_mind'):
            print("\nPeople on your mind:")
            for person in structure['people_on_mind']:
                if isinstance(person, dict):
                    print(f"- {person.get('name', 'Unknown')}: {person.get('context', 'No context provided')}")
                else:  # Handle case where person is a string
                    print(f"- {person}")

        if structure.get('growth_edges'):
            print("\nGrowth edges active:")
            for edge in structure['growth_edges']:
                print(f"- {edge}")

        if structure.get('alive_list'):
            print("\nThings wanting attention:")
            for item in structure['alive_list']:
                if isinstance(item, dict):
                    print(f"- {item.get('what', 'Unknown')}: {item.get('status', 'No status provided')}")
                else:  # Handle case where item is a string
                    print(f"- {item}")

    def adjust_interpretation(self, structure):
        """Allow user to adjust AI's interpretation"""
        print("\nLet's adjust this understanding. What needs to change?")
        # Add interactive adjustment logic here
        return structure

    def process_checkin(self, structure):
        """Process the interpreted structure and update all relevant files"""
        try:
            timestamp = datetime.datetime.now().isoformat()
            checkin = {
                'timestamp': timestamp,
                'raw_chat': structure.get('raw_chat', ''),
                'structured_data': structure,
                'patterns_noticed': self.detect_patterns(structure)
            }
            self.data['checkins']['checkins'].append(checkin)

            # Update patterns
            self.update_patterns(structure)

            # Save everything
            self.save_all()
        except Exception as e:
            print(f"Error in process_checkin: {e}")

    def detect_patterns(self, structure):
        """Detect active patterns in current check-in"""
        return {
            'protection_patterns': self.find_protection_patterns(structure),
            'growth_patterns': self.find_growth_patterns(structure),
            'energy_patterns': self.find_energy_patterns(structure),
            'connection_patterns': self.find_connection_patterns(structure)
        }

    def update_patterns_from_checkin(self, checkin_data, ai_analysis):
        """Update pattern evolution based on new check-in"""

        # Extract pattern manifestations from AI analysis
        patterns = self.extract_pattern_manifestations(ai_analysis)

        # Update each relevant pattern
        for pattern_name, manifestation in patterns.items():
            if pattern_name in self.checkins['pattern_evolution']['hidden_patterns']:
                pattern = self.checkins['pattern_evolution']['hidden_patterns'][pattern_name]

                # Add new manifestation
                pattern['current']['manifestations'].append({
                    'date': checkin_data['timestamp'],
                    'context': manifestation['context'],
                    'insight': manifestation['insight']
                })

                # Keep only recent manifestations (last 5)
                pattern['current']['manifestations'] = \
                    pattern['current']['manifestations'][-5:]

                # Update progress indicators if present
                if 'progress' in manifestation:
                    pattern['current']['progress_indicators'].append({
                        'date': checkin_data['timestamp'],
                        'progress': manifestation['progress']
                    })

        # Update integration movements
        self.update_integration_movements(checkin_data, ai_analysis)

        # Update growth edges
        self.update_growth_edges(checkin_data, ai_analysis)

        # Update current state
        self.update_current_state(checkin_data, ai_analysis)

    def extract_pattern_manifestations(self, ai_analysis):
        """Extract pattern manifestations from AI analysis"""
        prompt = f"""Given this analysis:
    {ai_analysis}

    And these known patterns:
    {json.dumps(self.checkins['pattern_evolution']['hidden_patterns'], indent=2)}

    Extract any pattern manifestations, noting:
    1. Which patterns were active
    2. How they manifested
    3. Any progress or insights
    4. New variations or evolutions

    Return as structured JSON."""

        response = self.get_claude_response(prompt)
        return json.loads(response)

    def update_integration_movements(self, checkin_data, ai_analysis):
        """Update integration movements based on check-in"""
        movements = self.checkins['pattern_evolution']['integration_movements']

        # Look for progress on active movements
        for movement in movements['active']:
            # Check if this check-in shows progress
            if self.shows_movement_progress(movement, ai_analysis):
                movement['recent_manifestations'].append({
                    'date': checkin_data['timestamp'],
                    'context': self.extract_movement_context(movement, ai_analysis),
                    'insight': self.extract_movement_insight(movement, ai_analysis)
                })

                # Update progress score
                movement['progress'] = self.calculate_movement_progress(movement)

                # Check if movement is complete
                if movement['progress'] >= 0.8:  # Threshold for completion
                    movement['completed_date'] = checkin_data['timestamp']
                    movements['completed'].append(movement)
                    movements['active'].remove(movement)

    def update_growth_edges(self, checkin_data, ai_analysis):
        """Update growth edges based on check-in"""
        edges = self.checkins['pattern_evolution']['growth_edges']

        # Update existing edges
        for edge in edges['active']:
            if self.shows_edge_progress(edge, ai_analysis):
                edge['progress_markers'].append({
                    'date': checkin_data['timestamp'],
                    'marker': self.extract_edge_progress(edge, ai_analysis)
                })

        # Look for new edges
        new_edges = self.extract_new_edges(ai_analysis)
        for edge in new_edges:
            edges['active'].append({
                'edge': edge['name'],
                'discovered': checkin_data['timestamp'],
                'context': edge['context'],
                'progress_markers': [],
                'next_steps': edge['suggested_steps']
            })

    def update_current_state(self, checkin_data, ai_analysis):
        """Update current state based on check-in"""
        state = self.checkins['current_state']
        state['last_updated'] = checkin_data['timestamp']

        # Update energy trends
        if self.shows_energy_pattern(ai_analysis):
            state['energy_trends'].append({
                'date': checkin_data['timestamp'],
                'pattern': self.extract_energy_pattern(ai_analysis),
                'evidence': [self.extract_energy_evidence(ai_analysis)]
            })

        # Update protection mechanisms
        active_mechanisms = self.extract_protection_mechanisms(ai_analysis)
        for mech in active_mechanisms:
            if any(m['mechanism'] == mech['name'] for m in state['active_protection_mechanisms']):
                # Update existing mechanism
                for m in state['active_protection_mechanisms']:
                    if m['mechanism'] == mech['name']:
                        m['recent_manifestations'].append(mech['manifestation'])
            else:
                # Add new mechanism
                state['active_protection_mechanisms'].append({
                    'mechanism': mech['name'],
                    'first_noticed': checkin_data['timestamp'],
                    'recent_manifestations': [mech['manifestation']]
                })

        # Update breakthrough opportunities
        state['breakthrough_opportunities'] = \
            self.extract_breakthrough_opportunities(ai_analysis)
    def save_all(self):
        """Save all data files"""
        self.save_json(self.data['checkins'], 'checkins.json')
        self.save_json(self.data['patterns'], 'patterns.json')
        self.save_json(self.data['context'], 'context.json')

        def get_default_structure(self):
            """Return default structure when analysis fails"""
            return {
                "energy_state": {
                    "level": None,
                    "description": None
                },
                "people_on_mind": [],
                "growth_edges": [],
                "alive_list": []
            }

    def find_protection_patterns(self, structure):
        """Find active protection patterns in the check-in"""
        patterns = []
        known_patterns = {
            'visibility_paradox': ['automation', 'system', 'tool', 'passive'],
            'action_reflection_loop': ['analyze', 'understand', 'perfect', 'plan'],
            'value_giving_cycle': ['help', 'create', 'teach', 'give'],
            'financial_safety_dance': ['money', 'invest', 'finance', 'cost']
        }

        try:
            # Extract text for analysis
            text = ' '.join([
                str(structure.get('raw_chat', '')),
                str(structure.get('notes', '')),
                str(structure.get('ai_analysis', ''))
            ]).lower()

            # Look for pattern indicators
            for pattern, indicators in known_patterns.items():
                if any(indicator in text for indicator in indicators):
                    context = self.extract_pattern_context(text, pattern)
                    patterns.append({
                        'pattern': pattern,
                        'context': context,
                        'confidence': self.calculate_pattern_confidence(text, indicators)
                    })

            return patterns
        except Exception as e:
            print(f"Error in pattern detection: {e}")
            return []

    def find_energy_patterns(self, structure):
        """Find energy patterns in the check-in"""
        try:
            energy_data = {
                'level': structure.get('energy_state', {}).get('level'),
                'description': structure.get('energy_state', {}).get('description'),
                'physical_signs': structure.get('physical_signs', []),
                'context': structure.get('context', '')
            }

            patterns = []

            # Compare with the previous energy level if available
            if self.previous_energy_level is not None and energy_data['level'] is not None:
                if energy_data['level'] > self.previous_energy_level:
                    patterns.append({
                        'type': 'energy_increase',
                        'context': energy_data['description'],
                        'physical_signs': energy_data['physical_signs']
                    })
                elif energy_data['level'] < self.previous_energy_level:
                    patterns.append({
                        'type': 'energy_decrease',
                        'context': energy_data['description'],
                        'physical_signs': energy_data['physical_signs']
                    })

            # Update the previous energy level
            self.previous_energy_level = energy_data['level']

            return patterns
        except Exception as e:
            print(f"Error in energy pattern detection: {e}")
            return []

    def extract_movement_context(self, movement, ai_analysis):
        """Extract context for movement progress"""
        try:
            # Look for mentions of the movement's from/to states
            movement_text = f"from {movement['from']} to {movement['to']}"
            sentences = re.split(r'[.!?]+', ai_analysis)
            relevant_sentences = [
                s for s in sentences 
                if movement['from'].lower() in s.lower() 
                or movement['to'].lower() in s.lower()
            ]

            if relevant_sentences:
                return ' '.join(relevant_sentences).strip()
            return "Context not found"
        except Exception as e:
            print(f"Error extracting movement context: {e}")
            return "Error extracting context"

    def calculate_movement_progress(self, movement):
        """Calculate progress score for a movement"""
        try:
            recent_manifestations = movement.get('recent_manifestations', [])
            if not recent_manifestations:
                return movement.get('progress', 0)

            # Weight recent progress more heavily
            weighted_sum = 0
            weights = [0.5, 0.3, 0.2]  # Most recent counts more
            for i, manifestation in enumerate(recent_manifestations[-3:]):
                progress_indicators = [
                    word in manifestation.get('insight', '').lower()
                    for word in ['success', 'progress', 'improvement', 'breakthrough']
                ]
                weighted_sum += weights[i] * (sum(progress_indicators) / len(progress_indicators))

            # Combine with existing progress, ensuring we don't decrease too much
            current_progress = movement.get('progress', 0)
            new_progress = min(1.0, max(current_progress, current_progress * 0.8 + weighted_sum * 0.2))
            return round(new_progress, 2)
        except Exception as e:
            print(f"Error calculating movement progress: {e}")
            return movement.get('progress', 0)

    def extract_protection_mechanisms(self, ai_analysis):
        """Extract active protection mechanisms"""
        try:
            known_mechanisms = {
                'analysis_paralysis': ['overthinking', 'analysis', 'perfect', 'understand'],
                'avoidance': ['avoid', 'delay', 'later', 'should'],
                'system_building': ['automate', 'system', 'tool', 'framework'],
                'withdrawal': ['withdraw', 'alone', 'isolate', 'distance']
            }

            mechanisms = []
            for name, indicators in known_mechanisms.items():
                if any(indicator in ai_analysis.lower() for indicator in indicators):
                    # Find relevant context
                    sentences = re.split(r'[.!?]+', ai_analysis)
                    context = [s for s in sentences if any(i in s.lower() for i in indicators)]

                    mechanisms.append({
                        'name': name,
                        'manifestation': context[0] if context else 'Context not found',
                        'confidence': sum(i in ai_analysis.lower() for i in indicators) / len(indicators)
                    })

            return mechanisms
        except Exception as e:
            print(f"Error extracting protection mechanisms: {e}")
            return []

    def extract_breakthrough_opportunities(self, ai_analysis):
        """Extract breakthrough opportunities"""
        try:
            # Look for forward-looking statements
            opportunities = []
            sentences = re.split(r'[.!?]+', ai_analysis)

            opportunity_indicators = [
                'could', 'might', 'opportunity', 'potential', 'next step',
                'consider', 'try', 'possible', 'suggestion'
            ]

            for sentence in sentences:
                if any(indicator in sentence.lower() for indicator in opportunity_indicators):
                    related_patterns = []
                    for pattern in self.config['system_prompt']['core_understanding']['hidden_patterns']:
                        if pattern.lower() in sentence.lower():
                            related_patterns.append(pattern)

                    opportunities.append({
                        'opportunity': sentence.strip(),
                        'related_patterns': related_patterns,
                        'confidence': len(related_patterns) / len(self.config['system_prompt']['core_understanding']['hidden_patterns'])
                    })

            return sorted(opportunities, key=lambda x: x['confidence'], reverse=True)
        except Exception as e:
            print(f"Error extracting breakthrough opportunities: {e}")
            return []

    def shows_edge_progress(self, edge, ai_analysis):
        """Check if check-in shows progress on a growth edge"""
        try:
            # Look for progress indicators specific to this edge
            edge_terms = edge['edge'].lower().split()
            progress_terms = ['progress', 'improvement', 'better', 'growth', 'step']

            # Count co-occurrence of edge terms and progress terms
            sentences = re.split(r'[.!?]+', ai_analysis)
            for sentence in sentences:
                if any(term in sentence.lower() for term in edge_terms) and \
                any(term in sentence.lower() for term in progress_terms):
                    return True
            return False
        except Exception as e:
            print(f"Error checking edge progress: {e}")
            return False

    def extract_edge_progress(self, edge, ai_analysis):
        """Extract progress details for a growth edge"""
        try:
            edge_name = edge['edge'].lower()
            sentences = re.split(r'[.!?]+', ai_analysis)

            # Find relevant sentences
            relevant = [s for s in sentences if edge_name in s.lower() and \
                    any(term in s.lower() for term in ['progress', 'improvement', 'step'])]

            if relevant:
                return relevant[0].strip()
            return ""
        except Exception as e:
            print(f"Error extracting edge progress: {e}")
            return ""

    def shows_energy_pattern(self, ai_analysis):
        """Check if analysis shows an energy pattern"""
        try:
            energy_terms = ['energy', 'tired', 'energized', 'flow', 'resistance', 'grounded']
            return any(term in ai_analysis.lower() for term in energy_terms)
        except Exception as e:
            print(f"Error checking energy pattern: {e}")
            return False

    def extract_energy_pattern(self, ai_analysis):
        """Extract energy pattern details"""
        try:
            sentences = re.split(r'[.!?]+', ai_analysis)
            energy_sentences = [s for s in sentences if 'energy' in s.lower()]

            if energy_sentences:
                return energy_sentences[0].strip()
            return ""
        except Exception as e:
            print(f"Error extracting energy pattern: {e}")
            return ""
    def find_connection_patterns(self, structure):
        """Find connection patterns in the check-in"""
        connection_patterns = []
        try:
            if 'people_on_mind' in structure:
                for person in structure['people_on_mind']:
                    if isinstance(person, dict):
                        connection_patterns.append({
                            'name': person.get('name', 'Unknown'),
                            'context': person.get('context', 'No context provided')
                        })
                    elif isinstance(person, str):
                        connection_patterns.append({
                            'name': person,
                            'context': 'No context provided'
                        })
        except Exception as e:
            print(f"Error in connection pattern detection: {e}")
        return connection_patterns
    def update_patterns(self, structure):
        """Update patterns based on structured data"""
        try:
            # Example logic to update patterns
            for pattern in self.detect_patterns(structure):
                self.data['patterns'].setdefault('active_patterns', []).append(pattern)
        except Exception as e:
            print(f"Error updating patterns: {e}")

    def extract_pattern_context(self, text, pattern):
        """Extract context for a detected pattern"""
        # Simplistic context extraction
