        """Parse analysis or default to adaptive scheduling based on 'energy'"""
        # Attempt to read user energy
        last_checkin = self.data['checkins'][-1]
        energy_str = last_checkin.get("energy", "")
        next_hours = self.choose_interval(energy_str)

