        self.data_file = '/Users/bilalghalib/Projects/Github/checkin/checkin-coach/presence_data.json'
        self.config_file = '/Users/bilalghalib/Projects/Github/checkin/checkin-coach/presence_config.json'

        # Load configuration and data
        self.load_files()

        # Setup Anthropic client
        self.setup_anthropic_client()

        # Rich console for terminal output
        self.console = Console()

        # Setup scheduling
        self.setup_scheduling()

    def setup_anthropic_client(self):
        """Initialize Anthropic client"""
        try:
            api_key = self.config.get("anthropic_api_key")
            if not api_key:
                rprint("[yellow]Warning: No Anthropic API key found[/yellow]")
                self.client = None
                return

            self.client = anthropic.Client(api_key=api_key)
            rprint("[green]Anthropic client initialized successfully[/green]")
        except Exception as e:
            rprint(f"[red]Error setting up Anthropic client: {e}[/red]")
            self.client = None

    def load_files(self):
        """Load configuration and data files"""
        try:
            # Load configuration
            if self.config_file.exists():
                with open(self.config_file, 'r') as f:
                    # Remove any potential comments
                    config_content = ''.join(line for line in f if not line.strip().startswith('//'))
                    self.config = json.loads(config_content)
            else:
                raise FileNotFoundError("Configuration file not found")

            # Load data file
            if self.data_file.exists():
                with open(self.data_file, 'r') as f:
                    self.data = json.load(f)
            else:
                self.data = {'checkins': [], 'patterns': []}

        except json.JSONDecodeError as e:
            rprint(f"[red]JSON Decode Error: {e}[/red]")
            # Fallback to minimal config if loading fails
            self.config = {}
            self.data = {'checkins': [], 'patterns': []}
        except Exception as e:
            rprint(f"[red]Error loading files: {e}[/red]")
            # Fallback to minimal config if loading fails
            self.config = {}
            self.data = {'checkins': [], 'patterns': []}

    def save_data(self):
        """Save check-in data"""
        try:
            with open(self.data_file, 'w') as f:
                json.dump(self.data, f, indent=2)
        except Exception as e:
            rprint(f"[red]Error saving data: {e}[/red]")

    def setup_scheduling(self):
        """Set up periodic reminders"""
        def run_schedule():
            while True:
                schedule.run_pending()
                time.sleep(60)

        try:
            # Schedule reminders based on config
            intervals = self.config.get('adaptive_scheduling', {})
            high_energy = intervals.get('high_energy', 2)

            schedule.every(high_energy).hours.do(self.show_reminder)

            # Start scheduling thread
            threading.Thread(target=run_schedule, daemon=True).start()
        except Exception as e:
            rprint(f"[red]Error setting up scheduling: {e}[/red]")

    @rumps.clicked("Quick Check-in")
    def quick_checkin(self, _):
        """Perform a check-in with multiple fields"""
        # Prepare check-in data
        checkin = {'timestamp': datetime.datetime.now().isoformat()}

        # Get fields to capture from config
        fields = self.config.get("fields_to_capture", [])

        # Capture each field
        for field in fields:
            resp = rumps.Window(
                title=f"Enter {field}",
                message=f"Provide your {field}",
                ok="Save",
                cancel="Skip",
                dimensions=(400, 60)
            ).run()

            if resp.clicked:
                checkin[field] = resp.text.strip()
            else:
                checkin[field] = ""

        # Save check-in
        self.data['checkins'].append(checkin)
        self.save_data()

        # Perform AI analysis if client is available
        if self.client:
            try:
                analysis = self.analyze_checkin(checkin)
                if analysis:
                    # Truncate analysis for notification
                    notification_text = analysis[:200] + "..." if len(analysis) > 200 else analysis
                    rumps.notification("Reflection", "", notification_text)

                    # Store pattern
                    self.data['patterns'].append({
                        "timestamp": checkin['timestamp'],
                        "analysis": analysis
                    })
                    self.save_data()
            except Exception as e:
                rprint(f"[red]Analysis error: {e}[/red]")

    def analyze_checkin(self, checkin):
        """Use Claude to analyze check-in"""
        if not self.client:
            return "AI analysis unavailable"

        try:
            # Get system prompt from config
            system_prompt = self.config.get('system_prompt', '')

            # Construct full prompt
            full_prompt = (
                f"{system_prompt}\n\n"
                f"User Check-In Data:\n{json.dumps(checkin, indent=2)}\n\n"
                "Please provide a thoughtful reflection that:"
                "1. Identifies key patterns"
                "2. Suggests a small, meaningful next step"
                "3. Offers gentle, supportive insight"
            )

            # Call Claude
            resp = self.client.completions.create(
                model="claude-instant-1",
                max_tokens_to_sample=400,
                temperature=0.7,
                prompt=anthropic.HUMAN_PROMPT + full_prompt + anthropic.AI_PROMPT
            )
            return resp.completion
        except Exception as e:
            return f"Analysis error: {e}"

    @rumps.clicked("View Patterns")
    def view_patterns(self, _):
        """Show recent patterns in terminal"""
        try:
            self.console.clear()
            self.console.print("=== Recent Patterns ===", style="bold magenta")

            # Show last 3 patterns
            for pat in reversed(self.data['patterns'][-3:]):
                self.console.print(
                    f"[green]{pat['timestamp']}[/green]: {pat['analysis'][:200]}", 
                    highlight=True
                )

            rumps.alert("Check terminal for pattern view")
        except Exception as e:
            rprint(f"[red]Error viewing patterns: {e}[/red]")

    @rumps.clicked("Edit Config")
    def edit_config(self, _):
        """Allow editing of configuration"""
        new_api_key = rumps.Window(
            title="Anthropic API Key",
            message="Enter your Anthropic API Key:",
            ok="Save",
            cancel="Cancel",
            dimensions=(400, 60)
        ).run()

        if new_api_key.clicked and new_api_key.text.strip():
            try:
                # Update API key in config
                self.config['anthropic_api_key'] = new_api_key.text.strip()

                # Save updated config
                with open(self.config_file, 'w') as f:
                    json.dump(self.config, f, indent=2)

                # Reinitialize Anthropic client
                self.setup_anthropic_client()

                rumps.notification("Config Updated", "", "API Key has been saved")
            except Exception as e:
                rprint(f"[red]Error saving config: {e}[/red]")

    @rumps.clicked("Quit")
    def quit(self, _):
        """Quit the application"""
        rumps.quit_application()

    def show_reminder(self):
        """Push reminder notification"""
        rumps.notification("PresenceTracker", "", "Time for a gentle check-in? Click menu to log it.")

def main():
    app = PresenceTracker()
    app.run()

if __name__ == "__main__":
    main()

#!/usr/bin/env python3

import json
import os
import datetime
from pathlib import Path
import rumps
import anthropic
import schedule
from rich.console import Console
from rich import print as rprint


class PresenceTracker(rumps.App):
    def __init__(self):
        super(PresenceTracker, self).__init__("ğŸŒ±", menu=["Quick Check-in", "View Patterns", "Quit"])

        # File paths
        self.data_file = Path.home() / '.presence_data.json'
        self.config_file = Path.home() / '.presence_config.json'

        # Load or initialize data/config
        self.data = self.load_file(self.data_file, default={'checkins': [], 'patterns': []})
        self.config = self.load_file(self.config_file, default={
            "anthropic_api_key": "",
            "system_prompt": "You are an AI assistant to help the user reflect.",
            "fields_to_capture": ["energy", "brave_action", "notes"],
            "adaptive_scheduling": {
                "high_energy": 2, "medium_energy": 4, "low_energy": 12, "fallback": 24
            }
        })

        # Debug: Print the loaded configuration to verify the API key
        print("Loaded Config:", self.config)

        # Anthropic client
        api_key = self.config.get("anthropic_api_key", "").strip()
        if not api_key:
            print("[Error] No API key found in config. Please provide a valid key.")
        else:
            self.client = anthropic.Client(api_key=api_key)

        # Console for pattern display
        self.console = Console()

        # Rumps timer for schedule run_pending
        self.timer = rumps.Timer(self.run_scheduled_jobs, 60)  # Check schedule every minute
        self.timer.start()

    def load_file(self, file_path, default):
        """Load a JSON file or initialize it with a default value."""
        if file_path.exists():
            try:
                with open(file_path, 'r') as f:
                    return json.load(f)
            except json.JSONDecodeError:
                print(f"[Error] Failed to parse {file_path}. Initializing with default data.")
        # If the file doesn't exist or is invalid, use the default
        with open(file_path, 'w') as f:
            json.dump(default, f, indent=2)
        return default

    def save_data(self):
        with open(self.data_file, 'w') as f:
            json.dump(self.data, f, indent=2)

    @rumps.clicked("Quick Check-in")
    def quick_checkin(self, _):
        """Open a dialog for quick check-in"""
        # Grab relevant fields from config
        fields = self.config.get("fields_to_capture", [])

        # Start building check-in data
        checkin = {}
        checkin['timestamp'] = datetime.datetime.now().isoformat()

        # For each field, open a small window
        for f in fields:
            default_msg = f"Enter '{f}'"
            resp = rumps.Window(
                title=f,
                message=default_msg,
                ok="Save",
                cancel="Skip",
                dimensions=(400, 60)
            ).run()
            if resp.clicked:
                checkin[f] = resp.text.strip()
            else:
                checkin[f] = ""

        # Append check-in
        self.data['checkins'].append(checkin)
        self.save_data()

        rumps.notification("Check-in Saved", "", "Your check-in has been recorded.")

    def run_scheduled_jobs(self, sender):
        """Called every minute to run scheduled reminders"""
        schedule.run_pending()

    def analyze_checkin(self, checkin):
        """Use Anthropic Claude to analyze the check-in."""
        prompt = (
            f"{self.config.get('system_prompt')}\n\n"
            f"Check-in Data:\n{json.dumps(checkin, indent=2)}\n"
            "Provide analysis and suggest next steps."
        )

        if not self.config.get("anthropic_api_key"):
            return "No API key provided. Skipping analysis."

        try:
            response = self.client.completions.create(
                model="claude-3-5-sonnet-20241022",
                max_tokens_to_sample=500,
                temperature=0.7,
                prompt=anthropic.HUMAN_PROMPT + prompt + anthropic.AI_PROMPT
            )
            analysis = response.completion
            self.data['patterns'].append({"timestamp": checkin['timestamp'], "analysis": analysis})
            self.save_data()
            return analysis
        except Exception as e:
            return f"Analysis error: {e}"

    def schedule_next_checkin(self, analysis):
        """Determine next check-in timing based on analysis or energy level."""
        energy = self.data['checkins'][-1].get("energy", "")
        next_hours = self.choose_interval(energy)

        rprint(f"[blue]Next check-in scheduled in ~{next_hours} hour(s).[/blue]")
        schedule.every(int(next_hours)).hours.do(self.show_reminder)

    def choose_interval(self, energy):
        """Choose interval based on energy level."""
        adaptive = self.config.get("adaptive_scheduling", {})
        try:
            energy_value = int(energy)
            if energy_value >= 4:
                return adaptive.get("high_energy", 2)
            elif energy_value >= 2:
                return adaptive.get("medium_energy", 4)
            else:
                return adaptive.get("low_energy", 12)
        except ValueError:
            return adaptive.get("fallback", 24)

    @rumps.clicked("View Patterns")
    def view_patterns(self, _):
        """Display recent patterns in the terminal."""
        self.console.clear()
        self.console.print("=== Recent Patterns ===", style="bold magenta")
        for pattern in self.data['patterns'][-5:]:
            self.console.print(f"[green]{pattern['timestamp']}[/green]: {pattern['analysis'][:200]}...")

    @rumps.clicked("Quit")
    def quit_app(self, _):
        """Quit the application."""
        rumps.quit_application()

    def run_scheduled_jobs(self, _):
        """Run scheduled reminders."""
        schedule.run_pending()

    def show_reminder(self):
        """Show a check-in reminder notification."""
        rumps.notification("PresenceTracker", "", "Time for a gentle check-in. Click to log it!")


def main():
    app = PresenceTracker()
    app.run()


if __name__ == "__main__":
    main()
