const correlationCalc = (x, y) => {
    const xMean = x.reduce((a, b) => a + b, 0) / x.length;
    const yMean = y.reduce((a, b) => a + b, 0) / y.length;
    const numerator = x.reduce((a, i, idx) => a + (i - xMean) * (y[idx] - yMean), 0);
    const denominator = Math.sqrt(
        x.reduce((a, i) => a + Math.pow(i - xMean, 2), 0) *
        y.reduce((a, i) => a + Math.pow(i - yMean, 2), 0)
    );
    return numerator / denominator;
};

