// Calculate means for different groups
const calcMeans = (arr1, arr2) => {
    const withExp = arr1.filter((_, i) => arr2[i] === 1);
    const withoutExp = arr1.filter((_, i) => arr2[i] === 0);
    return {
        withExp: withExp.reduce((a, b) => a + b, 0) / withExp.length,
        withoutExp: withoutExp.reduce((a, b) => a + b, 0) / withoutExp.length
    };
};

// Calculate correlations
const correlation = (x, y) => {
    const mean = arr => arr.reduce((a, b) => a + b, 0) / arr.length;
    const xMean = mean(x);
    const yMean = mean(y);

    const numerator = x.reduce((acc, val, i) => acc + (val - xMean) * (y[i] - yMean), 0);
    const denomX = Math.sqrt(x.reduce((acc, val) => acc + Math.pow(val - xMean, 2), 0));
    const denomY = Math.sqrt(y.reduce((acc, val) => acc + Math.pow(val - yMean, 2), 0));

    return numerator / (denomX * denomY);
};

// Calculate statistics for program group
const inProgMeans = {
    ptgi: calcMeans(data.in_program.ptgi, data.in_program.prior_experience),
    resilience: calcMeans(data.in_program.resilience, data.in_program.prior_experience),
    deliberate: calcMeans(data.in_program.deliberate_rumination, data.in_program.prior_experience),
    ent_intent: calcMeans(data.in_program.ent_intent, data.in_program.prior_experience)
};

