// Group definitions
const inProgram = [2, 7, 9, 11, 13, 14, 16, 18, 19, 20, 21];
const notProgram = [1, 3, 4, 5, 6, 8, 10, 12, 15, 17];

// Helper function to calculate means and standard deviations
const calculateStats = arr => {
    const mean = arr.reduce((a, b) => a + b, 0) / arr.length;
    const variance = arr.reduce((a, b) => a + Math.pow(b - mean, 2), 0) / arr.length;
    return {
        mean: mean,
        std: Math.sqrt(variance),
        n: arr.length
    };
};

// Analyze various metrics
const metrics = {
    "PTGI_scores": {
        "in_program": [91.0, 52.0, 47.0, 93.0, 81.0, 79.0, 87.0, 54.0, 75.0, 83.0, 71.0],
        "not_program": [76.0, 45.0, 83.0, 71.0, 81.0, 88.0, 61.0, 72.0, 78.0, 85.0]
    },
    "BRCS_scores": {
        "in_program": [18, 10, 15, 20, 15, 15, 20, 19, 20, 17, 13],
        "not_program": [12, 14, 18, 15, 14, 20, 15, 16, 16, 18]
    },
    "Entrepreneurial_Intent": {
        "in_program": [36, 32, 36, 32, 36, 16, 34, 34, 34, 32, 30],
        "not_program": [26, 32, 30, 31, 31, 31, 32, 24, 9, 36]
    }
};

// Calculate statistics for each metric
Object.entries(metrics).forEach(([metric, groups]) => {
    console.log(`\n${metric} Analysis:`);
    const inProgramStats = calculateStats(groups.in_program);
    const notProgramStats = calculateStats(groups.not_program);

    console.log(`In Program (n=${inProgramStats.n}):
    Mean: ${inProgramStats.mean.toFixed(2)}
    Std Dev: ${inProgramStats.std.toFixed(2)}`);

    console.log(`Not Program (n=${notProgramStats.n}):
    Mean: ${notProgramStats.mean.toFixed(2)}
    Std Dev: ${notProgramStats.std.toFixed(2)}`);

    // Calculate effect size (Cohen's d)
    const pooledStd = Math.sqrt(
        ((inProgramStats.n - 1) * Math.pow(inProgramStats.std, 2) + 
         (notProgramStats.n - 1) * Math.pow(notProgramStats.std, 2)) /
        (inProgramStats.n + notProgramStats.n - 2)
    );

    const cohensD = Math.abs(inProgramStats.mean - notProgramStats.mean) / pooledStd;

    console.log(`Effect Size (Cohen's d): ${cohensD.toFixed(3)}`);
});

