        "resilience": [12, 14, 18, 15, 14, 20, 15, 16, 16, 18],
        "prior_entrepreneurship": [0, 1, 0, 0, 0, 1, 0, 1, 0, 0]
    }
};

// Calculate summary statistics
const calcStats = arr => {
    const mean = arr.reduce((a, b) => a + b, 0) / arr.length;
    const variance = arr.reduce((a, b) => a + Math.pow(b - mean, 2), 0) / arr.length;
    return {
        mean,
        std: Math.sqrt(variance),
        median: [...arr].sort((a, b) => a - b)[Math.floor(arr.length / 2)],
        min: Math.min(...arr),
        max: Math.max(...arr)
    };
};

// Analyze entrepreneurial intent
const inProgramStats = calcStats(data.in_program.ent_intent);
const notProgramStats = calcStats(data.not_program.ent_intent);

console.log("\nEntrepreneurial Intent Statistics:");
