      ptgi: [76, 71, 88, 72, 78, 85],
      resilience: [12, 15, 20, 16, 16, 18]
    }
  }
};

// Calculate means and correlations
const calcGroupStats = (group) => {
  const mean = arr => arr.reduce((a, b) => a + b, 0) / arr.length;
  return {
    delib_rum_mean: mean(group.delib_rum),
    ptgi_mean: mean(group.ptgi),
    resilience_mean: mean(group.resilience),
    correlation_dr_ptg: correlation(group.delib_rum, group.ptgi)
  };
};

const correlation = (x, y) => {
  const mean = arr => arr.reduce((a, b) => a + b, 0) / arr.length;
  const xMean = mean(x);
  const yMean = mean(y);

  const numerator = x.reduce((acc, val, i) => acc + (val - xMean) * (y[i] - yMean), 0);
  const denomX = Math.sqrt(x.reduce((acc, val) => acc + Math.pow(val - xMean, 2), 0));
  const denomY = Math.sqrt(y.reduce((acc, val) => acc + Math.pow(val - yMean, 2), 0));

  return numerator / (denomX * denomY);
};

// Calculate stats for each group
const stats = {
  program: {
    prior_exp: calcGroupStats(data.program.prior_exp),
    no_prior_exp: calcGroupStats(data.program.no_prior_exp)
  },
  no_program: {
    prior_exp: calcGroupStats(data.no_program.prior_exp),
    no_prior_exp: calcGroupStats(data.no_program.no_prior_exp)
  }
};

console.log("Group Statistics:");
console.log(JSON.stringify(stats, null, 2));

