        "delib_rum": [36,33,4,31,34,37,46,21,30,26,36,23,36,23,34,9,40,36,32,36,32],
        "impact": [36,24,21,61,19,40,30,48,72,78,53,72,54,78,78,54,20,18,34,14,34],
        "ptgi": [76,91,45,83,71,81,88,61,72,78,93,72,81,79,78,87,54,75,83,71,75],
        "ent_intent": [26,36,32,30,31,31,31,32,24,9,32,24,36,16,9,34,34,34,32,30,32],
        "resilience": [12,18,14,18,15,14,20,15,16,16,20,16,15,15,16,20,19,20,17,13,17]
    }
};

// Helper functions
const mean = arr => arr.reduce((a, b) => a + b, 0) / arr.length;
const correlation = (x, y) => {
    const meanX = mean(x);
    const meanY = mean(y);
    const num = x.reduce((acc, xi, i) => acc + (xi - meanX) * (y[i] - meanY), 0);
    const denX = Math.sqrt(x.reduce((acc, xi) => acc + Math.pow(xi - meanX, 2), 0));
    const denY = Math.sqrt(y.reduce((acc, yi) => acc + Math.pow(yi - meanY, 2), 0));
    return num / (denX * denY);
};

// Group data by prior experience
const withPrior = {
    delib_rum: [],
    impact: [],
    ptgi: [],
    ent_intent: [],
    resilience: []
};
const withoutPrior = {
    delib_rum: [],
    impact: [],
    ptgi: [],
    ent_intent: [],
    resilience: []
};

data.all_participants.prior_exp.forEach((exp, i) => {
    const group = exp === 1 ? withPrior : withoutPrior;
    group.delib_rum.push(data.all_participants.delib_rum[i]);
    group.impact.push(data.all_participants.impact[i]);
    group.ptgi.push(data.all_participants.ptgi[i]);
    group.ent_intent.push(data.all_participants.ent_intent[i]);
    group.resilience.push(data.all_participants.resilience[i]);
});

// Calculate key metrics
const metrics = {
