import React from 'react';
import { Card, CardHeader, CardTitle, CardContent } from '@/components/ui/card';

const ProcessingPatterns = () => {
  const data = {
    processingMechanisms: [
      {
        type: "Spiritual Integration",
        description: "Using religious frameworks to process",
        quote: "aqqalal (use your brain) watawakal (rely on God)",
        strength: 8
      },
      {
        type: "Relational Processing",
        description: "Processing through relationships",
        quote: "you bond with people more",
        strength: 21
      },
      {
        type: "Practical Action",
        description: "Converting thoughts to action",
        quote: "Finding alternative methods to survive",
        strength: 16
      }
    ]
  };

  return (
    <Card className="w-full max-w-4xl">
      <CardHeader>
        <CardTitle>Processing Mechanisms Analysis</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="space-y-6">
          {data.processingMechanisms.map((mechanism, index) => (
            <div key={index} className="p-4 border rounded-lg bg-gray-50">
              <h3 className="text-lg font-semibold mb-2">{mechanism.type}</h3>
              <div className="flex items-center mb-2">
                <div className="w-full bg-gray-200 rounded-full h-2">
                  <div 
                    className="bg-blue-600 h-2 rounded-full" 
                    style={{width: `${(mechanism.strength/21)*100}%`}}
                  />
                </div>
                <span className="ml-2 text-sm">{mechanism.strength}</span>
              </div>
              <p className="text-sm text-gray-600 mb-2">{mechanism.description}</p>
              <p className="text-sm italic">"{mechanism.quote}"</p>
            </div>
          ))}
        </div>
      </CardContent>
    </Card>
  );
};

export default ProcessingPatterns;

