import React from 'react';
import { Card, CardHeader, CardTitle, CardContent } from '@/components/ui/card';

const TraumaGrowthPatterns = () => {
  const patterns = [
    {
      category: "Trauma Impact",
      intensity: 53, // IES-R Score
      manifestations: [
        "Direct blast experience",
        "PTSD symptoms",
        "High hyperarousal"
      ]
    },
    {
      category: "Processing Mechanisms",
      intensity: 20, // BRCS Score
      manifestations: [
        "Psychological awareness",
        "Emotional normalization",
        "Productive anger channeling"
      ]
    },
    {
      category: "Growth Actions",
      intensity: 32, // EI Score
      manifestations: [
        "Product creation",
        "Job creation",
        "Youth mentoring"
      ]
    }
  ];

  return (
    <Card className="w-full max-w-4xl">
      <CardHeader>
        <CardTitle>Trauma-Growth Pattern Analysis: Case 002</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="space-y-6">
          {patterns.map((pattern, index) => (
            <div key={index} className="p-4 border rounded-lg bg-gray-50">
              <div className="flex justify-between items-center mb-2">
                <h3 className="text-lg font-semibold">{pattern.category}</h3>
                <span className="text-sm bg-blue-100 px-2 py-1 rounded">
                  Score: {pattern.intensity}
                </span>
              </div>
              <div className="space-y-2">
                {pattern.manifestations.map((item, i) => (
                  <div key={i} className="flex items-center">
                    <div className="w-2 h-2 bg-blue-500 rounded-full mr-2" />
                    <p className="text-sm">{item}</p>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      </CardContent>
    </Card>
  );
};

export default TraumaGrowthPatterns;

