// Helper function to count emotional mentions
const countEmotionalMentions = (text, emotions) => {
  const counts = {};
  emotions.forEach(emotion => {
    const matches = text.match(new RegExp(`\\[\\[Code: Feeling-${emotion}`, 'gi'));
    counts[emotion.toLowerCase()] = matches ? matches.length : 0;
  });
  return counts;
};

// Helper function to count processing mechanisms
const countProcessingMechanisms = (text) => {
  const deliberate = (text.match(/\[\[Code: (?:Value-|Response-|Silver-Linings)/gi) || []).length;
  const avoidance = (text.match(/\[\[Code: Response-Avoidance/gi) || []).length;
  const meaning = (text.match(/\[\[Code: (?:Spirituality|Value-|Silver-Linings)/gi) || []).length;

  return {
    deliberate_rumination_examples: deliberate,
    avoidance_mentions: avoidance,
    meaning_making_attempts: meaning
  };
};

// Extract and analyze interview text
const text = document.querySelector('document_content').textContent;

// Calculate emotional mentions
const emotionCounts = countEmotionalMentions(text, ['Anger', 'Fear', 'Hope', 'Despair', 'Gratitude']);
console.log('Emotional Mentions:', emotionCounts);

// Calculate processing mechanisms
const processingCounts = countProcessingMechanisms(text);
console.log('Processing Mechanisms:', processingCounts);

// Extract formal scores
const scores = {
  ies_r: {
    total: parseInt(text.match(/IES-R_Score:: (\d+)/)[1]),
    intrusion: parseInt(text.match(/IES-R_Intrusion:: (\d+)/)[1]),
    avoidance: parseInt(text.match(/IES-R_Avoidance:: (\d+)/)[1]),
    hyperarousal: parseInt(text.match(/IES-R_Hyper_Arousal:: (\d+)/)[1])
  },
  ptgi: {
    total: parseInt(text.match(/PTGI_Score:: (\d+)/)[1]),
    appreciation: parseInt(text.match(/PTGI_Appreciation of life:: (\d+)/)[1]),
    spirituality: parseInt(text.match(/PTGI_Spiritual Enhancement:: (\d+)/)[1]),
    relationships: parseInt(text.match(/PTGI_Relating to others:: (\d+)/)[1]),
    possibilities: parseInt(text.match(/PTGI_New Possibilities:: (\d+)/)[1]),
    strength: parseInt(text.match(/PTGI_Personal Strength:: (\d+)/)[1])
  },
  deliberate_rumination: parseInt(text.match(/Deliberate_Rumination_Score:: (\d+)/)[1]),
  intrusive_rumination: parseInt(text.match(/Intrusive_Rumination_Score:: (\d+)/)[1])
};

console.log('Formal Scores:', scores);
