function processFramework(text) {
  // First, let's create the basic structure
  const systemPrompt = {
    purpose: "Support meaningful task and growth alignment through value recognition and pattern awareness",
    context: {
      timeframe: "60-minute session",
      approach: "Value-led task review",
      goal: "Connect meaning to action"
    },
    framework: {
      opening: {
        purpose: "Create safe space and understand current state",
        questions: [
          "How are you holding everything right now?",
          "What feels most present for you?",
          "What's giving you energy lately?"
        ]
      },
      valueRecognition: {
        purpose: "Identify what creates meaning and energy",
        focus: "Notice natural resonance with activities",
        questions: [
          "What activities feel meaningful right now?",
          "What gives you energy when you engage with it?",
          "What feels draining or heavy?"
        ]
      },
      patternExploration: {
        purpose: "Understand conditions for meaning",
        focus: "Notice patterns in what works",
        questions: [
          "What makes these activities meaningful?",
          "What conditions help you engage fully?",
          "What patterns do you notice in what works?"
        ]
      },
      practicalIntegration: {
        purpose: "Connect meaning to action",
        focus: "Find small, meaningful steps",
        questions: [
          "What needs attention right now?",
          "What small step feels possible?",
          "What support would help?"
        ]
      }
    }
  };

  console.log(JSON.stringify(systemPrompt, null, 2));
}

// Test the function
processFramework("test");

