import yaml
import anthropic
import sys
from pathlib import Path
from typing import Dict, List, Any
from datetime import datetime

class CAPSystem:
    def __init__(self):
        self.config_path = Path('./configs')
        self.config_path.mkdir(exist_ok=True)
        self.client = anthropic.Anthropic()
        self.conversation_history = []
        self.configs = self.load_configs()

    def load_configs(self) -> Dict[str, Any]:
        """Load all configuration files."""
        config_files = [
            'llm_values.yml',
            'person_context.yml',
            'story_repository.yml',
            'pattern_tracker.yml'
        ]

        configs = {}
        for file in config_files:
            try:
                with open(self.config_path / file, 'r') as f:
                    configs[file.stem] = yaml.safe_load(f)
            except FileNotFoundError:
                print(f"Creating new {file}")
                configs[file.stem] = self.create_initial_config(file.stem)
                self.save_config(file.stem, configs[file.stem])

        return configs

    def create_initial_config(self, config_type: str) -> Dict[str, Any]:
        """Create initial configuration based on type."""
        if config_type == 'llm_values':
            return create_llm_values()
        elif config_type == 'person_context':
            return create_person_context()
        elif config_type == 'story_repository':
            return create_story_repository()
        elif config_type == 'pattern_tracker':
            return create_pattern_tracker()
        return {}

    def save_config(self, config_name: str, content: Dict[str, Any]):
        """Save configuration to YAML file."""
        with open(self.config_path / f"{config_name}.yml", 'w') as f:
            yaml.dump(content, f, default_flow_style=False, sort_keys=False)

    def create_system_prompt(self) -> str:
        """Create system prompt from current configurations."""
        return f"""You are an advanced facilitation assistant designed to support the user in exploring tensions, resolving patterns, and moving toward meaningful action through a CAP-centered framework. Your responses should align with these configurations:

LLM Values: {yaml.dump(self.configs['llm_values'], default_flow_style=False)}

Personal Context: {yaml.dump(self.configs['person_context'], default_flow_style=False)}

Active Patterns: {yaml.dump(self.configs['pattern_tracker'], default_flow_style=False)}

Remember to:
1. Start with state/energy check
2. Notice tensions and patterns
3. Collect relevant stories
4. Support meaningful action
5. Update pattern tracking
6. End with three options to continue or close

Your responses should maintain the balance between structure and emergence, always watching for aliveness indicators."""

    def chat(self):
        """Run interactive chat session with Claude."""
        print("\nWelcome to your CAP-centered reflection space.")
        print("Type 'exit' to end the session, 'save' to save current state.")

        while True:
            user_input = input("\nYou: ").strip()

            if user_input.lower() == 'exit':
                break
            elif user_input.lower() == 'save':
                self.save_all_configs()
                continue

            messages = [
                {"role": "system", "content": self.create_system_prompt()},
                *self.conversation_history,
                {"role": "user", "content": user_input}
            ]

            try:
                response = self.client.messages.create(
                    model="claude-3-5-sonnet-20241022",
                    max_tokens=1024,
                    messages=messages
                )

                print("\nClaude:", response.content[0].text)

                # Update conversation history
                self.conversation_history.extend([
                    {"role": "user", "content": user_input},
                    {"role": "assistant", "content": response.content[0].text}
                ])

            except Exception as e:
                print(f"Error: {e}")

    def save_all_configs(self):
        """Save all current configurations."""
        for config_name, content in self.configs.items():
            self.save_config(config_name, content)
        print("All configurations saved.")

def main():
    system = CAPSystem()
    system.chat()

if __name__ == "__main__":
    main()

