class PatternUpdater:
    """Handles updates to pattern tracking during conversation."""
    def __init__(self, configs: Dict[str, Any]):
        self.configs = configs

    def update_pattern(self, pattern_name: str, 
                      context: str = None, 
                      resolution: str = None,
                      emotional_markers: List[str] = None):
        """Update pattern tracking with new instance."""
        patterns = self.configs['pattern_tracker']['active_patterns']

        if pattern_name in patterns:
            patterns[pattern_name]['frequency'] += 1

            if context and context not in patterns[pattern_name]['contexts']:
                patterns[pattern_name]['contexts'].append(context)

            if emotional_markers:
                current_markers = patterns[pattern_name]['emotional_markers']
                patterns[pattern_name]['emotional_markers'] = list(
                    set(current_markers + emotional_markers)
                )

            if resolution:
                if 'resolutions' not in patterns[pattern_name]:
                    patterns[pattern_name]['resolutions'] = []
                patterns[pattern_name]['resolutions'].append({
                    'timestamp': datetime.now().isoformat(),
                    'description': resolution
                })
        else:
            # Create new pattern
            patterns[pattern_name] = {
                'frequency': 1,
                'contexts': [context] if context else [],
                'emotional_markers': emotional_markers if emotional_markers else [],
                'resolutions': [{
                    'timestamp': datetime.now().isoformat(),
                    'description': resolution
                }] if resolution else []
            }

class StoryUpdater:
    """Handles updates to story repository during conversation."""
    def __init__(self, configs: Dict[str, Any]):
        self.configs = configs

    def add_story(self, 
                 story_type: str,
                 content: str,
                 emotional_markers: List[str],
                 patterns_revealed: List[str] = None,
                 caps_active: List[str] = None,
                 resolution: str = None,
                 wisdom_generated: str = None):
        """Add new story to repository."""
        stories = self.configs['story_repository']['stories']

        if story_type not in stories:
            stories[story_type] = {}

        story_id = f"{story_type}_{len(stories[story_type]) + 1}"

        stories[story_type][story_id] = {
            'content': content,
            'emotional_markers': emotional_markers,
            'patterns_revealed': patterns_revealed if patterns_revealed else [],
            'caps_active': caps_active if caps_active else [],
            'resolution': resolution,
            'wisdom_generated': wisdom_generated,
            'timestamp': datetime.now().isoformat()
        }

        # Update patterns if revealed
        if patterns_revealed and self.configs.get('pattern_tracker'):
            pattern_updater = PatternUpdater(self.configs)
            for pattern in patterns_revealed:
                pattern_updater.update_pattern(
                    pattern_name=pattern,
                    context=story_type,
                    emotional_markers=emotional_markers,
                    resolution=resolution
                )

